var n={name:"\u4ECE V8 \u4E2D\u770B JS \u6027\u80FD\u4F18\u5316",content:`<style>.markdown-body{word-break:break-word;line-height:1.75;font-weight:400;font-size:15px;overflow-x:hidden;color:#333}.markdown-body h1,.markdown-body h2,.markdown-body h3,.markdown-body h4,.markdown-body h5,.markdown-body h6{line-height:1.5;margin-top:35px;margin-bottom:10px;padding-bottom:5px}.markdown-body h1{font-size:30px;margin-bottom:5px}.markdown-body h2{padding-bottom:12px;font-size:24px;border-bottom:1px solid #ececec}.markdown-body h3{font-size:18px;padding-bottom:0}.markdown-body h4{font-size:16px}.markdown-body h5{font-size:15px}.markdown-body h6{margin-top:5px}.markdown-body p{line-height:inherit;margin-top:22px;margin-bottom:22px}.markdown-body img{max-width:100%}.markdown-body hr{border:none;border-top:1px solid #ddd;margin-top:32px;margin-bottom:32px}.markdown-body code{word-break:break-word;border-radius:2px;overflow-x:auto;background-color:#fff5f5;color:#ff502c;font-size:.87em;padding:.065em .4em}.markdown-body code,.markdown-body pre{font-family:Menlo,Monaco,Consolas,Courier New,monospace}.markdown-body pre{overflow:auto;position:relative;line-height:1.75}.markdown-body pre>code{font-size:12px;padding:15px 12px;margin:0;word-break:normal;display:block;overflow-x:auto;color:#333;background:#f8f8f8}.markdown-body a{text-decoration:none;color:#0269c8;border-bottom:1px solid #d1e9ff}.markdown-body a:active,.markdown-body a:hover{color:#275b8c}.markdown-body table{display:inline-block!important;font-size:12px;width:auto;max-width:100%;overflow:auto;border:1px solid #f6f6f6}.markdown-body thead{background:#f6f6f6;color:#000;text-align:left}.markdown-body tr:nth-child(2n){background-color:#fcfcfc}.markdown-body td,.markdown-body th{padding:12px 7px;line-height:24px}.markdown-body td{min-width:120px}.markdown-body blockquote{color:#666;padding:1px 23px;margin:22px 0;border-left:4px solid #cbcbcb;background-color:#f8f8f8}.markdown-body blockquote:after{display:block;content:""}.markdown-body blockquote>p{margin:10px 0}.markdown-body ol,.markdown-body ul{padding-left:28px}.markdown-body ol li,.markdown-body ul li{margin-bottom:0;list-style:inherit}.markdown-body ol li .task-list-item,.markdown-body ul li .task-list-item{list-style:none}.markdown-body ol li .task-list-item ol,.markdown-body ol li .task-list-item ul,.markdown-body ul li .task-list-item ol,.markdown-body ul li .task-list-item ul{margin-top:0}.markdown-body ol ol,.markdown-body ol ul,.markdown-body ul ol,.markdown-body ul ul{margin-top:3px}.markdown-body ol li{padding-left:6px}.markdown-body .contains-task-list{padding-left:0}.markdown-body .task-list-item{list-style:none}@media (max-width:720px){.markdown-body h1{font-size:24px}.markdown-body h2{font-size:20px}.markdown-body h3{font-size:18px}}</style><h1 data-id="heading-0">\u4ECE V8 \u4E2D\u770B JS \u6027\u80FD\u4F18\u5316</h1>
<pre><code class="hljs language-!" lang="!">\u6CE8\u610F\uFF1A\u8BE5\u77E5\u8BC6\u70B9\u5C5E\u4E8E\u6027\u80FD\u4F18\u5316\u9886\u57DF\u3002
</code></pre>
<p>\u6027\u80FD\u95EE\u9898\u8D8A\u6765\u8D8A\u6210\u4E3A\u524D\u7AEF\u706B\u70ED\u7684\u8BDD\u9898\uFF0C\u56E0\u4E3A\u968F\u7740\u9879\u76EE\u7684\u9010\u6B65\u53D8\u5927\uFF0C\u6027\u80FD\u95EE\u9898\u4E5F\u9010\u6B65\u4F53\u73B0\u51FA\u6765\u3002\u4E3A\u4E86\u63D0\u9AD8\u7528\u6237\u7684\u4F53\u9A8C\uFF0C\u51CF\u5C11\u52A0\u8F7D\u65F6\u95F4\uFF0C\u5DE5\u7A0B\u5E08\u4EEC\u60F3\u5C3D\u4E00\u5207\u529E\u6CD5\u53BB\u4F18\u5316\u7EC6\u8282\u3002</p>
<p>\u6398\u91D1\u4E4B\u524D\u5DF2\u7ECF\u51FA\u8FC7\u4E00\u672C\u5173\u4E8E\u6027\u80FD\u7684\u5C0F\u518C\uFF0C\u6211\u5728\u5199\u6D89\u53CA\u6027\u80FD\u4F18\u5316\u7684\u5185\u5BB9\u4E4B\u524D\u5C31\u7279\u5730\u53BB\u8D2D\u4E70\u4E86\u8FD9\u672C\u5C0F\u518C\u9605\u8BFB\uFF0C\u76EE\u7684\u662F\u4E3A\u4E86\u5199\u51FA\u70B9\u4E0D\u4E00\u6837\u7684\u4E1C\u897F\u3002\u5F53\u7136\u6027\u80FD\u4F18\u5316\u5F52\u7ED3\u8D77\u6765\u8FD8\u662F\u90A3\u51E0\u4E2A\u70B9\uFF0C\u6211\u53EA\u80FD\u5C3D\u53EF\u80FD\u5730\u5199\u51FA\u90A3\u672C\u5C0F\u518C\u6CA1\u6709\u63D0\u53CA\u7684\u5185\u5BB9\uFF0C\u90E8\u5206\u5185\u5BB9\u8FD8\u662F\u4F1A\u6709\u91CD\u53E0\u7684\u3002\u5F53\u7136\u5B83\u901A\u8FC7\u4E86\u5341\u4E94\u4E2A\u7AE0\u8282\u53BB\u4ECB\u7ECD\u6027\u80FD\uFF0C\u80AF\u5B9A\u4F1A\u8BB2\u7684\u6BD4\u6211\u7EC6\uFF0C\u6709\u5174\u8DA3\u7684\u53EF\u4EE5\u540C\u65F6\u8D2D\u4E70\u8FD8\u6709\u672C \u300C<a href="https://juejin.im/book/6844733750048210957?referrer=574f8d8d2e958a005fd4edac" target="_blank" title="https://juejin.im/book/6844733750048210957?referrer=574f8d8d2e958a005fd4edac">\u524D\u7AEF\u6027\u80FD\u4F18\u5316\u539F\u7406\u4E0E\u5B9E\u8DF5</a> \u300D\u5C0F\u518C\uFF0C\u5F62\u6210\u4E00\u4E2A\u4E92\u8865\u3002</p>
<p>\u5728\u8FD9\u51E0\u4E2A\u7AE0\u8282\u4E2D\u4E0D\u4F1A\u63D0\u53CA\u6D4F\u89C8\u5668\u3001Webpack\u3001\u7F51\u7EDC\u534F\u8BAE\u8FD9\u51E0\u5757\u5982\u4F55\u4F18\u5316\u7684\u5185\u5BB9\uFF0C\u56E0\u4E3A\u5BF9\u5E94\u7684\u6A21\u5757\u4E2D\u5DF2\u7ECF\u8BB2\u5230\u4E86\u8FD9\u90E8\u5206\u7684\u5185\u5BB9\uFF0C\u5982\u679C\u4F60\u60F3\u5B66\u4E60\u8FD9\u51E0\u5757\u8BE5\u5982\u4F55\u6027\u80FD\u4F18\u5316\u7684\u8BDD\uFF0C\u53EF\u4EE5\u53BB\u5BF9\u5E94\u7684\u7AE0\u8282\u9605\u8BFB\u3002</p>
<p>\u5728\u8FD9\u4E00\u7AE0\u8282\u4E2D\u6211\u4EEC\u5C06\u6765\u5B66\u4E60\u5982\u4F55\u8BA9 V8 \u4F18\u5316\u6211\u4EEC\u7684\u4EE3\u7801\uFF0C\u4E0B\u4E00\u7AE0\u8282\u5C06\u4F1A\u5B66\u4E60\u6027\u80FD\u4F18\u5316\u5269\u4F59\u7684\u7410\u788E\u70B9\uFF0C\u56E0\u4E3A\u6027\u80FD\u4F18\u5316\u8FD9\u4E2A\u9886\u57DF\u6240\u6D89\u53CA\u7684\u5185\u5BB9\u90FD\u5F88\u788E\u7247\u5316\u3002</p>
<p>\u5728\u5B66\u4E60\u5982\u4F55\u6027\u80FD\u4F18\u5316\u4E4B\u524D\uFF0C\u6211\u4EEC\u5148\u6765\u4E86\u89E3\u4E0B\u5982\u4F55\u6D4B\u8BD5\u6027\u80FD\u95EE\u9898\uFF0C\u6BD5\u7ADF\u662F\u5148\u6709\u95EE\u9898\u624D\u4F1A\u53BB\u60F3\u7740\u8BE5\u5982\u4F55\u6539\u8FDB\u3002</p>
<h2 data-id="heading-1">\u6D4B\u8BD5\u6027\u80FD\u5DE5\u5177</h2>
<p>Chrome \u5DF2\u7ECF\u63D0\u4F9B\u4E86\u4E00\u4E2A\u5927\u800C\u5168\u7684\u6027\u80FD\u6D4B\u8BD5\u5DE5\u5177 <strong>Audits</strong></p>
<p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/d2b87a0d66b2474cabdb94b0b7e4effc~tplv-k3u1fbpfcp-watermark.awebp" alt="" title="Audits \u6240\u5904\u4F4D\u7F6E" loading="lazy" class="medium-zoom-image"></p>
<p>\u70B9\u6211\u4EEC\u70B9\u51FB Audits \u540E\uFF0C\u53EF\u4EE5\u770B\u5230\u5982\u4E0B\u7684\u754C\u9762</p>
<p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/7651dca0f8d349b08fd82fd24293c05c~tplv-k3u1fbpfcp-watermark.awebp" alt="" title="Audits \u754C\u9762" loading="lazy" class="medium-zoom-image"></p>
<p>\u5728\u8FD9\u4E2A\u754C\u9762\u4E2D\uFF0C\u6211\u4EEC\u53EF\u4EE5\u9009\u62E9\u60F3\u6D4B\u8BD5\u7684\u529F\u80FD\u7136\u540E\u70B9\u51FB <strong>Run audits</strong> \uFF0C\u5DE5\u5177\u5C31\u4F1A\u81EA\u52A8\u8FD0\u884C\u5E2E\u52A9\u6211\u4EEC\u6D4B\u8BD5\u95EE\u9898\u5E76\u4E14\u7ED9\u51FA\u4E00\u4E2A\u5B8C\u6574\u7684\u62A5\u544A</p>
<p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/b86317c2ea444819ab0c8da083487e6a~tplv-k3u1fbpfcp-watermark.awebp" alt="" title="Audits \u5DE5\u5177\u7ED9\u51FA\u7684\u62A5\u544A" loading="lazy" class="medium-zoom-image"></p>
<p>\u4E0A\u56FE\u662F\u7ED9\u6398\u91D1\u9996\u9875\u6D4B\u8BD5\u6027\u80FD\u540E\u7ED9\u51FA\u7684\u4E00\u4E2A\u62A5\u544A\uFF0C\u53EF\u4EE5\u770B\u5230\u62A5\u544A\u4E2D\u5206\u522B\u4E3A<strong>\u6027\u80FD\u3001\u4F53\u9A8C\u3001SEO</strong> \u90FD\u7ED9\u51FA\u4E86\u6253\u5206\uFF0C\u5E76\u4E14\u6BCF\u4E00\u4E2A\u6307\u6807\u90FD\u6709\u8BE6\u7EC6\u7684<strong>\u8BC4\u4F30</strong></p>
<p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/47d27ca5ae0a4ecca918418954eef1ee~tplv-k3u1fbpfcp-watermark.awebp" alt="" title="\u6307\u6807\u4E2D\u7684\u8BE6\u7EC6\u8BC4\u4F30" loading="lazy" class="medium-zoom-image"></p>
<p>\u8BC4\u4F30\u7ED3\u675F\u540E\uFF0C\u5DE5\u5177\u8FD8\u63D0\u4F9B\u4E86\u4E00\u4E9B<strong>\u5EFA\u8BAE</strong>\u4FBF\u4E8E\u6211\u4EEC\u63D0\u9AD8\u8FD9\u4E2A\u6307\u6807\u7684\u5206\u6570</p>
<p><img src="https://p6-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/d98db62d24f2449584f90894ab47a8a5~tplv-k3u1fbpfcp-watermark.awebp" alt="" loading="lazy" class="medium-zoom-image"></p>
<p>\u6211\u4EEC\u53EA\u9700\u8981\u4E00\u6761\u6761\u6839\u636E\u5EFA\u8BAE\u53BB\u4F18\u5316\u6027\u80FD\u5373\u53EF\u3002</p>
<p>\u9664\u4E86 <strong>Audits</strong> \u5DE5\u5177\u4E4B\u5916\uFF0C\u8FD8\u6709\u4E00\u4E2A <strong>Performance</strong> \u5DE5\u5177\u4E5F\u53EF\u4EE5\u4F9B\u6211\u4EEC\u4F7F\u7528\u3002</p>
<p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/94b5452830ce43dab2844016b755c22a~tplv-k3u1fbpfcp-watermark.awebp" alt="" title="Performance \u5DE5\u5177\u7ED9\u51FA\u7684\u62A5\u544A" loading="lazy" class="medium-zoom-image"></p>
<p>\u5728\u8FD9\u5F20\u56FE\u4E2D\uFF0C\u6211\u4EEC\u53EF\u4EE5\u8BE6\u7EC6\u7684\u770B\u5230\u6BCF\u4E2A<strong>\u65F6\u95F4\u6BB5</strong>\u4E2D\u6D4F\u89C8\u5668\u5728\u5904\u7406\u4EC0\u4E48\u4E8B\u60C5\uFF0C\u54EA\u4E2A\u8FC7\u7A0B\u6700\u6D88\u8017\u65F6\u95F4\uFF0C\u4FBF\u4E8E\u6211\u4EEC\u66F4\u52A0\u8BE6\u7EC6\u7684\u4E86\u89E3\u6027\u80FD<strong>\u74F6\u9888</strong>\u3002</p>
<h2 data-id="heading-2">JS \u6027\u80FD\u4F18\u5316</h2>
<p>JS \u662F\u7F16\u8BD1\u578B\u8FD8\u662F\u89E3\u91CA\u578B\u8BED\u8A00\u5176\u5B9E\u5E76\u4E0D\u56FA\u5B9A\u3002\u9996\u5148 JS \u9700\u8981\u6709\u5F15\u64CE\u624D\u80FD\u8FD0\u884C\u8D77\u6765\uFF0C\u65E0\u8BBA\u662F\u6D4F\u89C8\u5668\u8FD8\u662F\u5728 Node \u4E2D\uFF0C\u8FD9\u662F\u89E3\u91CA\u578B\u8BED\u8A00\u7684\u7279\u6027\u3002\u4F46\u662F\u5728 V8 \u5F15\u64CE\u4E0B\uFF0C\u53C8\u5F15\u5165\u4E86 <code>TurboFan</code> \u7F16\u8BD1\u5668\uFF0C\u4ED6\u4F1A\u5728\u7279\u5B9A\u7684\u60C5\u51B5\u4E0B\u8FDB\u884C\u4F18\u5316\uFF0C\u5C06\u4EE3\u7801\u7F16\u8BD1\u6210\u6267\u884C\u6548\u7387\u66F4\u9AD8\u7684 <strong>Machine Code</strong>\uFF0C\u5F53\u7136\u8FD9\u4E2A\u7F16\u8BD1\u5668\u5E76\u4E0D\u662F JS \u5FC5\u987B\u9700\u8981\u7684\uFF0C\u53EA\u662F\u4E3A\u4E86\u63D0\u9AD8\u4EE3\u7801\u6267\u884C\u6027\u80FD\uFF0C\u6240\u4EE5\u603B\u7684\u6765\u8BF4 JS \u66F4\u504F\u5411\u4E8E\u89E3\u91CA\u578B\u8BED\u8A00\u3002</p>
<p>\u90A3\u4E48\u8FD9\u4E00\u5C0F\u8282\u7684\u5185\u5BB9\u4E3B\u8981\u4F1A\u9488\u5BF9\u4E8E Chrome \u7684 <strong>V8</strong> \u5F15\u64CE\u6765\u8BB2\u89E3\u3002</p>
<p>\u5728\u8FD9\u4E00\u8FC7\u7A0B\u4E2D\uFF0CJS \u4EE3\u7801\u9996\u5148\u4F1A\u89E3\u6790\u4E3A\u62BD\u8C61\u8BED\u6CD5\u6811\uFF08AST\uFF09\uFF0C\u7136\u540E\u4F1A\u901A\u8FC7\u89E3\u91CA\u5668\u6216\u8005\u7F16\u8BD1\u5668\u8F6C\u5316\u4E3A <strong>Bytecode</strong> \u6216\u8005 <strong>Machine Code</strong></p>
<p><img src="https://p6-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/423e54a54a8f413b91059a369fc516b7~tplv-k3u1fbpfcp-watermark.awebp" alt="" loading="lazy" class="medium-zoom-image"></p>
<p>\u4ECE\u4E0A\u56FE\u4E2D\u6211\u4EEC\u53EF\u4EE5\u53D1\u73B0\uFF0CJS \u4F1A\u9996\u5148\u88AB\u89E3\u6790\u4E3A AST\uFF0C\u89E3\u6790\u7684\u8FC7\u7A0B\u5176\u5B9E\u662F\u7565\u6162\u7684\u3002\u4EE3\u7801\u8D8A\u591A\uFF0C\u89E3\u6790\u7684\u8FC7\u7A0B\u4E5F\u5C31\u8017\u8D39\u8D8A\u957F\uFF0C\u8FD9\u4E5F\u662F\u6211\u4EEC\u9700\u8981\u538B\u7F29\u4EE3\u7801\u7684\u539F\u56E0\u4E4B\u4E00\u3002\u53E6\u5916\u4E00\u79CD\u51CF\u5C11\u89E3\u6790\u65F6\u95F4\u7684\u65B9\u5F0F\u662F\u9884\u89E3\u6790\uFF0C\u4F1A\u4F5C\u7528\u4E8E\u672A\u6267\u884C\u7684\u51FD\u6570\uFF0C\u8FD9\u4E2A\u6211\u4EEC\u4E0B\u9762\u518D\u8C08\u3002</p>
<p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/337c4db7205349fd990ccda88dc057a7~tplv-k3u1fbpfcp-watermark.awebp" alt="" title="2016 \u5E74\u624B\u673A\u89E3\u6790 JS \u4EE3\u7801\u7684\u901F\u5EA6" loading="lazy" class="medium-zoom-image"></p>
<p>\u8FD9\u91CC\u9700\u8981\u6CE8\u610F\u4E00\u70B9\uFF0C\u5BF9\u4E8E\u51FD\u6570\u6765\u8BF4\uFF0C\u5E94\u8BE5\u5C3D\u53EF\u80FD\u907F\u514D\u58F0\u660E\u5D4C\u5957\u51FD\u6570\uFF08\u7C7B\u4E5F\u662F\u51FD\u6570\uFF09\uFF0C\u56E0\u4E3A\u8FD9\u6837\u4F1A\u9020\u6210\u51FD\u6570\u7684\u91CD\u590D\u89E3\u6790\u3002</p>
<pre><code class="hljs language-js" lang="js"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">test1</span>(<span class="hljs-params"></span>) </span>{
  <span class="hljs-comment">// \u4F1A\u88AB\u91CD\u590D\u89E3\u6790</span>
  <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">test2</span>(<span class="hljs-params"></span>) </span>{}
}
</code></pre>
<p>\u7136\u540E <strong>Ignition</strong> \u8D1F\u8D23\u5C06 AST \u8F6C\u5316\u4E3A Bytecode\uFF0C<strong>TurboFan</strong> \u8D1F\u8D23\u7F16\u8BD1\u51FA\u4F18\u5316\u540E\u7684 Machine Code\uFF0C\u5E76\u4E14 Machine Code \u5728\u6267\u884C\u6548\u7387\u4E0A\u4F18\u4E8E Bytecode</p>
<p><img src="https://p9-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/22416b9f98de4980a97ee95e6a4f0b4a~tplv-k3u1fbpfcp-watermark.awebp" alt="" loading="lazy" class="medium-zoom-image"></p>
<p>\u90A3\u4E48\u6211\u4EEC\u5C31\u4EA7\u751F\u4E86\u4E00\u4E2A\u7591\u95EE\uFF0C<strong>\u4EC0\u4E48\u60C5\u51B5\u4E0B\u4EE3\u7801\u4F1A\u7F16\u8BD1\u4E3A Machine Code\uFF1F</strong></p>
<p>JS \u662F\u4E00\u95E8<strong>\u52A8\u6001\u7C7B\u578B</strong>\u7684\u8BED\u8A00\uFF0C\u5E76\u4E14\u8FD8\u6709\u4E00\u5927\u5806\u7684\u89C4\u5219\u3002\u7B80\u5355\u7684\u52A0\u6CD5\u8FD0\u7B97\u4EE3\u7801\uFF0C\u5185\u90E8\u5C31\u9700\u8981\u8003\u8651\u597D\u51E0\u79CD\u89C4\u5219\uFF0C\u6BD4\u5982\u6570\u5B57\u76F8\u52A0\u3001\u5B57\u7B26\u4E32\u76F8\u52A0\u3001\u5BF9\u8C61\u548C\u5B57\u7B26\u4E32\u76F8\u52A0\u7B49\u7B49\u3002\u8FD9\u6837\u7684\u60C5\u51B5\u4E5F\u5C31\u52BF\u5FC5\u5BFC\u81F4\u4E86\u5185\u90E8\u8981\u589E\u52A0\u5F88\u591A\u5224\u65AD\u903B\u8F91\uFF0C\u964D\u4F4E\u8FD0\u884C\u6548\u7387\u3002</p>
<pre><code class="hljs language-js" lang="js"><span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">test</span>(<span class="hljs-params">x</span>) </span>{
  <span class="hljs-keyword">return</span> x + x
}

test(<span class="hljs-number">1</span>)
test(<span class="hljs-number">2</span>)
test(<span class="hljs-number">3</span>)
test(<span class="hljs-number">4</span>)
</code></pre>
<p>\u5BF9\u4E8E\u4EE5\u4E0A\u4EE3\u7801\u6765\u8BF4\uFF0C\u5982\u679C\u4E00\u4E2A\u51FD\u6570\u88AB<strong>\u591A\u6B21\u8C03\u7528</strong>\u5E76\u4E14\u53C2\u6570\u4E00\u76F4\u4F20\u5165 <code>number</code> \u7C7B\u578B\uFF0C\u90A3\u4E48 V8 \u5C31\u4F1A\u8BA4\u4E3A\u8BE5\u6BB5\u4EE3\u7801\u53EF\u4EE5\u7F16\u8BD1\u4E3A Machine Code\uFF0C\u56E0\u4E3A\u4F60<strong>\u56FA\u5B9A\u4E86\u7C7B\u578B</strong>\uFF0C\u4E0D\u9700\u8981\u518D\u6267\u884C\u5F88\u591A\u5224\u65AD\u903B\u8F91\u4E86\u3002</p>
<p>\u4F46\u662F\u5982\u679C\u4E00\u65E6\u6211\u4EEC\u4F20\u5165\u7684\u53C2\u6570<strong>\u7C7B\u578B\u6539\u53D8</strong>\uFF0C\u90A3\u4E48 Machine Code \u5C31\u4F1A\u88AB <strong>DeOptimized</strong> \u4E3A Bytecode\uFF0C\u8FD9\u6837\u5C31\u6709\u6027\u80FD\u4E0A\u7684\u4E00\u4E2A\u635F\u8017\u4E86\u3002\u6240\u4EE5\u5982\u679C\u6211\u4EEC\u5E0C\u671B\u4EE3\u7801\u80FD\u591A\u7684\u7F16\u8BD1\u4E3A Machine Code \u5E76\u4E14 DeOptimized \u7684\u6B21\u6570\u51CF\u5C11\uFF0C\u5C31\u5E94\u8BE5\u5C3D\u53EF\u80FD\u4FDD\u8BC1\u4F20\u5165\u7684<strong>\u7C7B\u578B\u4E00\u81F4</strong>\u3002</p>
<p>\u90A3\u4E48\u4F60\u53EF\u80FD\u4F1A\u6709\u4E00\u4E2A\u7591\u95EE\uFF0C\u5230\u5E95\u4F18\u5316\u524D\u540E\u6709\u591A\u5C11\u7684\u63D0\u5347\u5462\uFF0C\u63A5\u4E0B\u6765\u6211\u4EEC\u5C31\u6765\u5B9E\u8DF5\u6D4B\u8BD5\u4E00\u4E0B\u5230\u5E95\u6709\u591A\u5C11\u7684\u63D0\u5347\u3002</p>
<pre><code class="hljs language-js" lang="js"><span class="hljs-keyword">const</span> { performance, PerformanceObserver } = <span class="hljs-built_in">require</span>(<span class="hljs-string">'perf_hooks'</span>)

<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">test</span>(<span class="hljs-params">x</span>) </span>{
  <span class="hljs-keyword">return</span> x + x
}
<span class="hljs-comment">// node 10 \u4E2D\u624D\u6709 PerformanceObserver</span>
<span class="hljs-comment">// \u5728\u8FD9\u4E4B\u524D\u7684 node \u7248\u672C\u53EF\u4EE5\u76F4\u63A5\u4F7F\u7528 performance \u4E2D\u7684 API</span>
<span class="hljs-keyword">const</span> obs = <span class="hljs-keyword">new</span> PerformanceObserver(<span class="hljs-function">(<span class="hljs-params">list, observer</span>) =&gt;</span> {
  <span class="hljs-built_in">console</span>.log(list.getEntries())
  observer.disconnect()
})
obs.observe({ <span class="hljs-attr">entryTypes</span>: [<span class="hljs-string">'measure'</span>], <span class="hljs-attr">buffered</span>: <span class="hljs-literal">true</span> })

performance.mark(<span class="hljs-string">'start'</span>)

<span class="hljs-keyword">let</span> number = <span class="hljs-number">10000000</span>
<span class="hljs-comment">// \u4E0D\u4F18\u5316\u4EE3\u7801</span>
%NeverOptimizeFunction(test)

<span class="hljs-keyword">while</span> (number--) {
  test(<span class="hljs-number">1</span>)
}

performance.mark(<span class="hljs-string">'end'</span>)
performance.measure(<span class="hljs-string">'test'</span>, <span class="hljs-string">'start'</span>, <span class="hljs-string">'end'</span>)
</code></pre>
<p>\u4EE5\u4E0A\u4EE3\u7801\u4E2D\u6211\u4EEC\u4F7F\u7528\u4E86 <code>performance</code> API\uFF0C\u8FD9\u4E2A API \u5728\u6027\u80FD\u6D4B\u8BD5\u4E0A\u5341\u5206\u597D\u7528\u3002\u4E0D\u4EC5\u53EF\u4EE5\u7528\u6765\u6D4B\u91CF\u4EE3\u7801\u7684\u6267\u884C\u65F6\u95F4\uFF0C\u8FD8\u80FD\u7528\u6765\u6D4B\u91CF\u5404\u79CD\u7F51\u7EDC\u8FDE\u63A5\u4E2D\u7684\u65F6\u95F4\u6D88\u8017\u7B49\u7B49\uFF0C\u5E76\u4E14\u8FD9\u4E2A API \u4E5F\u53EF\u4EE5\u5728\u6D4F\u89C8\u5668\u4E2D\u4F7F\u7528\u3002</p>
<p><img src="https://p1-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/8b17d9b088f84e11aab2111a70c5ab68~tplv-k3u1fbpfcp-watermark.awebp" alt="" loading="lazy" class="medium-zoom-image"></p>
<p>\u4ECE\u4E0A\u56FE\u4E2D\u6211\u4EEC\u53EF\u4EE5\u53D1\u73B0\uFF0C\u4F18\u5316\u8FC7\u7684\u4EE3\u7801\u6267\u884C\u65F6\u95F4\u53EA\u9700\u8981 9ms\uFF0C\u4F46\u662F\u4E0D\u4F18\u5316\u8FC7\u7684\u4EE3\u7801\u6267\u884C\u65F6\u95F4\u5374\u662F\u524D\u8005\u7684\u4E8C\u5341\u500D\uFF0C\u5DF2\u7ECF\u63A5\u8FD1 200ms \u4E86\u3002\u5728\u8FD9\u4E2A\u6848\u4F8B\u4E2D\uFF0C\u6211\u76F8\u4FE1\u5927\u5BB6\u5DF2\u7ECF\u770B\u5230\u4E86 V8 \u7684\u6027\u80FD\u4F18\u5316\u5230\u5E95\u6709\u591A\u5F3A\uFF0C\u53EA\u9700\u8981\u6211\u4EEC\u7B26\u5408\u4E00\u5B9A\u7684\u89C4\u5219\u4E66\u5199\u4EE3\u7801\uFF0C\u5F15\u64CE\u5E95\u5C42\u5C31\u80FD\u5E2E\u52A9\u6211\u4EEC\u81EA\u52A8\u4F18\u5316\u4EE3\u7801\u3002</p>
<p>\u53E6\u5916\uFF0C\u7F16\u8BD1\u5668\u8FD8\u6709\u4E2A\u9A9A\u64CD\u4F5C <strong>Lazy-Compile</strong>\uFF0C\u5F53\u51FD\u6570\u6CA1\u6709\u88AB\u6267\u884C\u7684\u65F6\u5019\uFF0C\u4F1A\u5BF9\u51FD\u6570\u8FDB\u884C\u4E00\u6B21\u9884\u89E3\u6790\uFF0C\u76F4\u5230\u4EE3\u7801\u88AB\u6267\u884C\u4EE5\u540E\u624D\u4F1A\u88AB\u89E3\u6790\u7F16\u8BD1\u3002\u5BF9\u4E8E\u4E0A\u8FF0\u4EE3\u7801\u6765\u8BF4\uFF0C<code>test</code> \u51FD\u6570\u9700\u8981\u88AB\u9884\u89E3\u6790\u4E00\u6B21\uFF0C\u7136\u540E\u5728\u8C03\u7528\u7684\u65F6\u5019\u518D\u88AB\u89E3\u6790\u7F16\u8BD1\u3002\u4F46\u662F\u5BF9\u4E8E\u8FD9\u79CD\u51FD\u6570\u9A6C\u4E0A\u5C31\u88AB\u8C03\u7528\u7684\u60C5\u51B5\u6765\u8BF4\uFF0C\u9884\u89E3\u6790\u8FD9\u4E2A\u8FC7\u7A0B\u5176\u5B9E\u662F\u591A\u4F59\u7684\uFF0C\u90A3\u4E48\u6709\u4EC0\u4E48\u529E\u6CD5\u80FD\u591F\u8BA9\u4EE3\u7801\u4E0D\u88AB\u9884\u89E3\u6790\u5462\uFF1F</p>
<p>\u5176\u5B9E\u5F88\u7B80\u5355\uFF0C\u6211\u4EEC\u53EA\u9700\u8981\u7ED9\u51FD\u6570<strong>\u5957\u4E0A\u62EC\u53F7</strong>\u5C31\u53EF\u4EE5\u4E86</p>
<pre><code class="hljs language-js" lang="js">(<span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">test</span>(<span class="hljs-params">obj</span>) </span>{
  <span class="hljs-keyword">return</span> x + x
})
</code></pre>
<p>\u4F46\u662F\u4E0D\u53EF\u80FD\u6211\u4EEC\u4E3A\u4E86\u6027\u80FD\u4F18\u5316\uFF0C\u7ED9\u6240\u6709\u7684\u51FD\u6570\u90FD\u53BB\u5957\u4E0A\u62EC\u53F7\uFF0C\u5E76\u4E14\u4E5F\u4E0D\u662F\u6240\u6709\u51FD\u6570\u90FD\u9700\u8981\u8FD9\u6837\u505A\u3002\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7 <a href="https://link.juejin.cn?target=https%3A%2F%2Fgithub.com%2Fnolanlawson%2Foptimize-js" target="_blank" rel="nofollow noopener noreferrer" title="https://github.com/nolanlawson/optimize-js" ref="nofollow noopener noreferrer">optimize-js</a> \u5B9E\u73B0\u8FD9\u4E2A\u529F\u80FD\uFF0C\u8FD9\u4E2A\u5E93\u4F1A\u5206\u6790\u4E00\u4E9B\u51FD\u6570\u7684\u4F7F\u7528\u60C5\u51B5\uFF0C\u7136\u540E\u7ED9\u9700\u8981\u7684\u51FD\u6570\u6DFB\u52A0\u62EC\u53F7\uFF0C\u5F53\u7136\u8FD9\u4E2A\u5E93\u5F88\u4E45\u6CA1\u4EBA\u7EF4\u62A4\u4E86\uFF0C\u5982\u679C\u9700\u8981\u4F7F\u7528\u7684\u8BDD\uFF0C\u8FD8\u662F\u9700\u8981\u6D4B\u8BD5\u8FC7\u76F8\u5173\u5185\u5BB9\u7684\u3002</p>
<h2 data-id="heading-3">\u5C0F\u7ED3</h2>
<p>\u603B\u7ED3\u4E00\u4E0B\u8FD9\u4E00\u7AE0\u8282\u6211\u4EEC\u5B66\u4E60\u7684\u77E5\u8BC6</p>
<ul>
<li>\u53EF\u4EE5\u901A\u8FC7 <strong>Audit</strong> \u5DE5\u5177\u83B7\u5F97\u7F51\u7AD9\u7684\u591A\u4E2A\u6307\u6807\u7684\u6027\u80FD\u62A5\u544A</li>
<li>\u53EF\u4EE5\u901A\u8FC7 <strong>Performance</strong> \u5DE5\u5177\u4E86\u89E3\u7F51\u7AD9\u7684\u6027\u80FD\u74F6\u9888</li>
<li>\u53EF\u4EE5\u901A\u8FC7 <strong>Performance</strong> API \u5177\u4F53\u6D4B\u91CF\u65F6\u95F4</li>
<li>\u4E3A\u4E86\u51CF\u5C11\u7F16\u8BD1\u65F6\u95F4\uFF0C\u6211\u4EEC\u53EF\u4EE5\u91C7\u7528<strong>\u51CF\u5C11\u4EE3\u7801\u6587\u4EF6\u7684\u5927\u5C0F</strong>\u6216\u8005<strong>\u51CF\u5C11\u4E66\u5199\u5D4C\u5957\u51FD\u6570</strong>\u7684\u65B9\u5F0F</li>
<li>\u4E3A\u4E86\u8BA9 V8 \u4F18\u5316\u4EE3\u7801\uFF0C\u6211\u4EEC\u5E94\u8BE5\u5C3D\u53EF\u80FD\u4FDD\u8BC1\u4F20\u5165\u53C2\u6570\u7684<strong>\u7C7B\u578B\u4E00\u81F4</strong>\u3002\u8FD9\u4E5F\u7ED9\u6211\u4EEC\u5E26\u6765\u4E86\u4E00\u4E2A\u601D\u8003\uFF0C\u8FD9\u662F\u4E0D\u662F\u4E5F\u662F\u4F7F\u7528 TypeScript \u80FD\u591F\u5E26\u6765\u7684\u597D\u5904\u4E4B\u4E00</li>
</ul><style>.markdown-body pre,.markdown-body pre>code.hljs{color:#333;background:#f8f8f8}.hljs-comment,.hljs-quote{color:#998;font-style:italic}.hljs-keyword,.hljs-selector-tag,.hljs-subst{color:#333;font-weight:700}.hljs-literal,.hljs-number,.hljs-tag .hljs-attr,.hljs-template-variable,.hljs-variable{color:teal}.hljs-doctag,.hljs-string{color:#d14}.hljs-section,.hljs-selector-id,.hljs-title{color:#900;font-weight:700}.hljs-subst{font-weight:400}.hljs-class .hljs-title,.hljs-type{color:#458;font-weight:700}.hljs-attribute,.hljs-name,.hljs-tag{color:navy;font-weight:400}.hljs-link,.hljs-regexp{color:#009926}.hljs-bullet,.hljs-symbol{color:#990073}.hljs-built_in,.hljs-builtin-name{color:#0086b3}.hljs-meta{color:#999;font-weight:700}.hljs-deletion{background:#fdd}.hljs-addition{background:#dfd}.hljs-emphasis{font-style:italic}.hljs-strong{font-weight:700}</style>`};export{n as default};
