var e={name:"\u4E0D\u597D\u770B\u5C31\u8981\u591A\u6574\u5BB9 \u2014\u2014 MySQL \u57FA\u4E8E\u89C4\u5219\u7684\u4F18\u5316\uFF08\u5185\u542B\u5173\u4E8E\u5B50\u67E5\u8BE2\u4F18\u5316\u4E8C\u4E09\u4E8B\u513F\uFF09",content:`<style>.markdown-body{word-break:break-word;line-height:1.75;font-weight:400;font-size:15px;overflow-x:hidden;color:#333}.markdown-body h1,.markdown-body h2,.markdown-body h3,.markdown-body h4,.markdown-body h5,.markdown-body h6{line-height:1.5;margin-top:35px;margin-bottom:10px;padding-bottom:5px}.markdown-body h1{font-size:30px;margin-bottom:5px}.markdown-body h2{padding-bottom:12px;font-size:24px;border-bottom:1px solid #ececec}.markdown-body h3{font-size:18px;padding-bottom:0}.markdown-body h4{font-size:16px}.markdown-body h5{font-size:15px}.markdown-body h6{margin-top:5px}.markdown-body p{line-height:inherit;margin-top:22px;margin-bottom:22px}.markdown-body img{max-width:100%}.markdown-body hr{border:none;border-top:1px solid #ddd;margin-top:32px;margin-bottom:32px}.markdown-body code{word-break:break-word;border-radius:2px;overflow-x:auto;background-color:#fff5f5;color:#ff502c;font-size:.87em;padding:.065em .4em}.markdown-body code,.markdown-body pre{font-family:Menlo,Monaco,Consolas,Courier New,monospace}.markdown-body pre{overflow:auto;position:relative;line-height:1.75}.markdown-body pre>code{font-size:12px;padding:15px 12px;margin:0;word-break:normal;display:block;overflow-x:auto;color:#333;background:#f8f8f8}.markdown-body a{text-decoration:none;color:#0269c8;border-bottom:1px solid #d1e9ff}.markdown-body a:active,.markdown-body a:hover{color:#275b8c}.markdown-body table{display:inline-block!important;font-size:12px;width:auto;max-width:100%;overflow:auto;border:1px solid #f6f6f6}.markdown-body thead{background:#f6f6f6;color:#000;text-align:left}.markdown-body tr:nth-child(2n){background-color:#fcfcfc}.markdown-body td,.markdown-body th{padding:12px 7px;line-height:24px}.markdown-body td{min-width:120px}.markdown-body blockquote{color:#666;padding:1px 23px;margin:22px 0;border-left:4px solid #cbcbcb;background-color:#f8f8f8}.markdown-body blockquote:after{display:block;content:""}.markdown-body blockquote>p{margin:10px 0}.markdown-body ol,.markdown-body ul{padding-left:28px}.markdown-body ol li,.markdown-body ul li{margin-bottom:0;list-style:inherit}.markdown-body ol li .task-list-item,.markdown-body ul li .task-list-item{list-style:none}.markdown-body ol li .task-list-item ol,.markdown-body ol li .task-list-item ul,.markdown-body ul li .task-list-item ol,.markdown-body ul li .task-list-item ul{margin-top:0}.markdown-body ol ol,.markdown-body ol ul,.markdown-body ul ol,.markdown-body ul ul{margin-top:3px}.markdown-body ol li{padding-left:6px}.markdown-body .contains-task-list{padding-left:0}.markdown-body .task-list-item{list-style:none}@media (max-width:720px){.markdown-body h1{font-size:24px}.markdown-body h2{font-size:20px}.markdown-body h3{font-size:18px}}</style><h1 data-id="heading-0">\u57FA\u4E8E\u89C4\u5219\u7684\u4F18\u5316</h1>
<p>\u6807\u7B7E\uFF1A MySQL\u662F\u600E\u6837\u8FD0\u884C\u7684</p>
<hr>
<p>\u5927\u5BB6\u522B\u5FD8\u4E86<code>MySQL</code>\u672C\u8D28\u4E0A\u662F\u4E00\u4E2A\u8F6F\u4EF6\uFF0C\u8BBE\u8BA1<code>MySQL</code>\u7684\u5927\u53D4\u5E76\u4E0D\u80FD\u8981\u6C42\u4F7F\u7528\u8FD9\u4E2A\u8F6F\u4EF6\u7684\u4EBA\u4E2A\u4E2A\u90FD\u662F\u6570\u636E\u5E93\u9AD8\u9AD8\u624B\uFF0C\u5C31\u50CF\u6211\u5199\u8FD9\u672C\u4E66\u7684\u65F6\u5019\u5E76\u4E0D\u80FD\u8981\u6C42\u5404\u4F4D\u5728\u5B66\u4E4B\u524D\u5C31\u4F1A\u4E86\u91CC\u8FB9\u513F\u7684\u77E5\u8BC6\u3002</p>
<blockquote><p>\u5410\u69FD\u4E00\u4E0B\uFF1A\u90FD\u4F1A\u4E86\u7684\u4EBA\u8C01\u8FD8\u770B\u5462\uFF0C\u96BE\u9053\u662F\u4E3A\u4E86\u7CBE\u795E\u4E0A\u53D7\u611F\u5316\uFF1F
</p></blockquote><p>\u4E5F\u5C31\u662F\u8BF4\u6211\u4EEC\u65E0\u6CD5\u907F\u514D\u67D0\u4E9B\u540C\u5B66\u5199\u4E00\u4E9B\u6267\u884C\u8D77\u6765\u5341\u5206\u8017\u8D39\u6027\u80FD\u7684\u8BED\u53E5\u3002\u5373\u4F7F\u662F\u8FD9\u6837\uFF0C\u8BBE\u8BA1<code>MySQL</code>\u7684\u5927\u53D4\u8FD8\u662F\u4F9D\u636E\u4E00\u4E9B\u89C4\u5219\uFF0C\u7AED\u5C3D\u5168\u529B\u7684\u628A\u8FD9\u4E2A\u5F88\u7CDF\u7CD5\u7684\u8BED\u53E5\u8F6C\u6362\u6210\u67D0\u79CD\u53EF\u4EE5\u6BD4\u8F83\u9AD8\u6548\u6267\u884C\u7684\u5F62\u5F0F\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u4E5F\u53EF\u4EE5\u88AB\u79F0\u4F5C<code>\u67E5\u8BE2\u91CD\u5199</code>\uFF08\u5C31\u662F\u4EBA\u5BB6\u89C9\u5F97\u4F60\u5199\u7684\u8BED\u53E5\u4E0D\u597D\uFF0C\u81EA\u5DF1\u518D\u91CD\u5199\u4E00\u904D\uFF09\u3002\u672C\u7AE0\u8BE6\u7EC6\u5520\u53E8\u4E00\u4E0B\u4E00\u4E9B\u6BD4\u8F83\u91CD\u8981\u7684\u91CD\u5199\u89C4\u5219\u3002</p>
<h2 data-id="heading-1">\u6761\u4EF6\u5316\u7B80</h2>
<p>\u6211\u4EEC\u7F16\u5199\u7684\u67E5\u8BE2\u8BED\u53E5\u7684\u641C\u7D22\u6761\u4EF6\u672C\u8D28\u4E0A\u662F\u4E00\u4E2A\u8868\u8FBE\u5F0F\uFF0C\u8FD9\u4E9B\u8868\u8FBE\u5F0F\u53EF\u80FD\u6BD4\u8F83\u7E41\u6742\uFF0C\u6216\u8005\u4E0D\u80FD\u9AD8\u6548\u7684\u6267\u884C\uFF0C<code>MySQL</code>\u7684\u67E5\u8BE2\u4F18\u5316\u5668\u4F1A\u4E3A\u6211\u4EEC\u7B80\u5316\u8FD9\u4E9B\u8868\u8FBE\u5F0F\u3002\u4E3A\u4E86\u65B9\u4FBF\u5927\u5BB6\u7406\u89E3\uFF0C\u6211\u4EEC\u540E\u8FB9\u4E3E\u4F8B\u5B50\u7684\u65F6\u5019\u90FD\u4F7F\u7528\u8BF8\u5982<code>a</code>\u3001<code>b</code>\u3001<code>c</code>\u4E4B\u7C7B\u7684\u7B80\u5355\u5B57\u6BCD\u4EE3\u8868\u67D0\u4E2A\u8868\u7684\u5217\u540D\u3002</p>
<h3 data-id="heading-2">\u79FB\u9664\u4E0D\u5FC5\u8981\u7684\u62EC\u53F7</h3>
<p>\u6709\u65F6\u5019\u8868\u8FBE\u5F0F\u91CC\u6709\u8BB8\u591A\u65E0\u7528\u7684\u62EC\u53F7\uFF0C\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">((a = 5 AND b = c) OR ((a &gt; c) AND (c &lt; 5)))
</code></pre><p>\u770B\u7740\u5C31\u5F88\u70E6\uFF0C\u4F18\u5316\u5668\u4F1A\u628A\u90A3\u4E9B\u7528\u4E0D\u5230\u7684\u62EC\u53F7\u7ED9\u5E72\u6389\uFF0C\u5C31\u662F\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">(a = 5 and b = c) OR (a &gt; c AND c &lt; 5)
</code></pre><h3 data-id="heading-3">\u5E38\u91CF\u4F20\u9012\uFF08constant_propagation\uFF09</h3>
<p>\u6709\u65F6\u5019\u67D0\u4E2A\u8868\u8FBE\u5F0F\u662F\u67D0\u4E2A\u5217\u548C\u67D0\u4E2A\u5E38\u91CF\u505A\u7B49\u503C\u5339\u914D\uFF0C\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">a = 5
</code></pre><p>\u5F53\u8FD9\u4E2A\u8868\u8FBE\u5F0F\u548C\u5176\u4ED6\u6D89\u53CA\u5217<code>a</code>\u7684\u8868\u8FBE\u5F0F\u4F7F\u7528<code>AND</code>\u8FDE\u63A5\u8D77\u6765\u65F6\uFF0C\u53EF\u4EE5\u5C06\u5176\u4ED6\u8868\u8FBE\u5F0F\u4E2D\u7684<code>a</code>\u7684\u503C\u66FF\u6362\u4E3A<code>5</code>\uFF0C\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">a = 5 AND b &gt; a
</code></pre><p>\u5C31\u53EF\u4EE5\u88AB\u8F6C\u6362\u4E3A\uFF1A</p>
<pre><code lang="bash" class="">a = 5 AND b &gt; 5
</code></pre><blockquote><p>\u5C0F\u8D34\u58EB\uFF1A

\u4E3A\u5565\u7528OR\u8FDE\u63A5\u8D77\u6765\u7684\u8868\u8FBE\u5F0F\u5C31\u4E0D\u80FD\u8FDB\u884C\u5E38\u91CF\u4F20\u9012\u5462\uFF1F\u81EA\u5DF1\u60F3\u60F3\u54C8\uFF5E
</p></blockquote><h3 data-id="heading-4">\u7B49\u503C\u4F20\u9012\uFF08equality_propagation\uFF09</h3>
<p>\u6709\u65F6\u5019\u591A\u4E2A\u5217\u4E4B\u95F4\u5B58\u5728\u7B49\u503C\u5339\u914D\u7684\u5173\u7CFB\uFF0C\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">a = b and b = c and c = 5
</code></pre><p>\u8FD9\u4E2A\u8868\u8FBE\u5F0F\u53EF\u4EE5\u88AB\u7B80\u5316\u4E3A\uFF1A</p>
<pre><code lang="bash" class="">a = 5 and b = 5 and c = 5
</code></pre><h3 data-id="heading-5">\u79FB\u9664\u6CA1\u7528\u7684\u6761\u4EF6\uFF08trivial_condition_removal\uFF09</h3>
<p>\u5BF9\u4E8E\u4E00\u4E9B\u660E\u663E\u6C38\u8FDC\u4E3A<code>TRUE</code>\u6216\u8005<code>FALSE</code>\u7684\u8868\u8FBE\u5F0F\uFF0C\u4F18\u5316\u5668\u4F1A\u79FB\u9664\u6389\u5B83\u4EEC\uFF0C\u6BD4\u5982\u8FD9\u4E2A\u8868\u8FBE\u5F0F\uFF1A</p>
<pre><code lang="bash" class="">(a &lt; 1 and b = b) OR (a = 6 OR 5 != 5)
</code></pre><p>\u5F88\u660E\u663E\uFF0C<code>b = b</code>\u8FD9\u4E2A\u8868\u8FBE\u5F0F\u6C38\u8FDC\u4E3A<code>TRUE</code>\uFF0C<code>5 != 5</code>\u8FD9\u4E2A\u8868\u8FBE\u5F0F\u6C38\u8FDC\u4E3A<code>FALSE</code>\uFF0C\u6240\u4EE5\u7B80\u5316\u540E\u7684\u8868\u8FBE\u5F0F\u5C31\u662F\u8FD9\u6837\u7684\uFF1A</p>
<pre><code lang="bash" class="">(a &lt; 1 and TRUE) OR (a = 6 OR FALSE)
</code></pre><p>\u53EF\u4EE5\u7EE7\u7EED\u88AB\u7B80\u5316\u4E3A</p>
<pre><code lang="bash" class="">a &lt; 1 OR a = 6
</code></pre><h3 data-id="heading-6">\u8868\u8FBE\u5F0F\u8BA1\u7B97</h3>
<p>\u5728\u67E5\u8BE2\u5F00\u59CB\u6267\u884C\u4E4B\u524D\uFF0C\u5982\u679C\u8868\u8FBE\u5F0F\u4E2D\u53EA\u5305\u542B\u5E38\u91CF\u7684\u8BDD\uFF0C\u5B83\u7684\u503C\u4F1A\u88AB\u5148\u8BA1\u7B97\u51FA\u6765\uFF0C\u6BD4\u5982\u8FD9\u4E2A\uFF1A</p>
<pre><code lang="bash" class="">a = 5 + 1
</code></pre><p>\u56E0\u4E3A<code>5 + 1</code>\u8FD9\u4E2A\u8868\u8FBE\u5F0F\u53EA\u5305\u542B\u5E38\u91CF\uFF0C\u6240\u4EE5\u5C31\u4F1A\u88AB\u5316\u7B80\u6210\uFF1A</p>
<pre><code lang="bash" class="">a = 6
</code></pre><p>\u4F46\u662F\u8FD9\u91CC\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u5982\u679C\u67D0\u4E2A\u5217\u5E76\u4E0D\u662F\u4EE5\u5355\u72EC\u7684\u5F62\u5F0F\u4F5C\u4E3A\u8868\u8FBE\u5F0F\u7684\u64CD\u4F5C\u6570\u65F6\uFF0C\u6BD4\u5982\u51FA\u73B0\u5728\u51FD\u6570\u4E2D\uFF0C\u51FA\u73B0\u5728\u67D0\u4E2A\u66F4\u590D\u6742\u8868\u8FBE\u5F0F\u4E2D\uFF0C\u5C31\u50CF\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">ABS(a) &gt; 5
</code></pre><p>\u6216\u8005\uFF1A</p>
<pre><code lang="bash" class=""><span>-a</span> &lt; -8
</code></pre><p><span>\u4F18\u5316\u5668\u662F\u4E0D\u4F1A\u5C1D\u8BD5\u5BF9\u8FD9\u4E9B\u8868\u8FBE\u5F0F\u8FDB\u884C\u5316\u7B80\u7684</span>\u3002\u6211\u4EEC\u524D\u8FB9\u8BF4\u8FC7\u53EA\u6709\u641C\u7D22\u6761\u4EF6\u4E2D\u7D22\u5F15\u5217\u548C\u5E38\u6570\u4F7F\u7528\u67D0\u4E9B\u8FD0\u7B97\u7B26\u8FDE\u63A5\u8D77\u6765\u624D\u53EF\u80FD\u4F7F\u7528\u5230\u7D22\u5F15\uFF0C\u6240\u4EE5\u5982\u679C\u53EF\u4EE5\u7684\u8BDD\uFF0C<span>\u6700\u597D\u8BA9\u7D22\u5F15\u5217\u4EE5\u5355\u72EC\u7684\u5F62\u5F0F\u51FA\u73B0\u5728\u8868\u8FBE\u5F0F\u4E2D</span>\u3002</p>
<h3 data-id="heading-7">HAVING\u5B50\u53E5\u548CWHERE\u5B50\u53E5\u7684\u5408\u5E76</h3>
<p>\u5982\u679C\u67E5\u8BE2\u8BED\u53E5\u4E2D\u6CA1\u6709\u51FA\u73B0\u8BF8\u5982<code>SUM</code>\u3001<code>MAX</code>\u7B49\u7B49\u7684\u805A\u96C6\u51FD\u6570\u4EE5\u53CA<code>GROUP BY</code>\u5B50\u53E5\uFF0C\u4F18\u5316\u5668\u5C31\u628A<code>HAVING</code>\u5B50\u53E5\u548C<code>WHERE</code>\u5B50\u53E5\u5408\u5E76\u8D77\u6765\u3002</p>
<h3 data-id="heading-8">\u5E38\u91CF\u8868\u68C0\u6D4B</h3>
<p>\u8BBE\u8BA1<code>MySQL</code>\u7684\u5927\u53D4\u89C9\u5F97\u4E0B\u8FB9\u8FD9\u4E24\u79CD\u67E5\u8BE2\u8FD0\u884C\u7684\u7279\u522B\u5FEB\uFF1A</p>
<ul>
<li>
<p>\u67E5\u8BE2\u7684\u8868\u4E2D\u4E00\u6761\u8BB0\u5F55\u6CA1\u6709\uFF0C\u6216\u8005\u53EA\u6709\u4E00\u6761\u8BB0\u5F55\u3002</p>
<blockquote><p>\u5C0F\u8D34\u58EB\uFF1A

\u5927\u5BB6\u6709\u6CA1\u6709\u89C9\u5F97\u8FD9\u4E00\u6761\u6709\u70B9\u513F\u4E0D\u5BF9\u52B2\uFF0C\u6211\u8FD8\u6CA1\u5F00\u59CB\u67E5\u8868\u5462\u548B\u5C31\u77E5\u9053\u8FD9\u8868\u91CC\u8FB9\u6709\u51E0\u6761\u8BB0\u5F55\u5462\uFF1F\u54C8\u54C8\uFF0C\u8FD9\u4E2A\u5176\u5B9E\u4F9D\u9760\u7684\u662F\u7EDF\u8BA1\u6570\u636E\u3002\u4E0D\u8FC7\u6211\u4EEC\u8BF4\u8FC7InnoDB\u7684\u7EDF\u8BA1\u6570\u636E\u6570\u636E\u4E0D\u51C6\u786E\uFF0C\u6240\u4EE5\u8FD9\u4E00\u6761\u4E0D\u80FD\u7528\u4E8E\u4F7F\u7528InnoDB\u4F5C\u4E3A\u5B58\u50A8\u5F15\u64CE\u7684\u8868\uFF0C\u53EA\u80FD\u9002\u7528\u4E8E\u4F7F\u7528Memory\u6216\u8005MyISAM\u5B58\u50A8\u5F15\u64CE\u7684\u8868\u3002
</p></blockquote></li>
<li>
<p>\u4F7F\u7528\u4E3B\u952E\u7B49\u503C\u5339\u914D\u6216\u8005\u552F\u4E00\u4E8C\u7EA7\u7D22\u5F15\u5217\u7B49\u503C\u5339\u914D\u4F5C\u4E3A\u641C\u7D22\u6761\u4EF6\u6765\u67E5\u8BE2\u67D0\u4E2A\u8868\u3002</p>
</li>
</ul>
<p>\u8BBE\u8BA1<code>MySQL</code>\u7684\u5927\u53D4\u89C9\u5F97\u8FD9\u4E24\u79CD\u67E5\u8BE2\u82B1\u8D39\u7684\u65F6\u95F4\u7279\u522B\u5C11\uFF0C\u5C11\u5230\u53EF\u4EE5\u5FFD\u7565\uFF0C\u6240\u4EE5\u4E5F\u628A\u901A\u8FC7\u8FD9\u4E24\u79CD\u65B9\u5F0F\u67E5\u8BE2\u7684\u8868\u79F0\u4E4B\u4E3A<code>\u5E38\u91CF\u8868</code>\uFF08\u82F1\u6587\u540D\uFF1A<code>constant tables</code>\uFF09\u3002\u4F18\u5316\u5668\u5728\u5206\u6790\u4E00\u4E2A\u67E5\u8BE2\u8BED\u53E5\u65F6\uFF0C\u5148\u9996\u5148\u6267\u884C\u5E38\u91CF\u8868\u67E5\u8BE2\uFF0C\u7136\u540E\u628A\u67E5\u8BE2\u4E2D\u6D89\u53CA\u5230\u8BE5\u8868\u7684\u6761\u4EF6\u5168\u90E8\u66FF\u6362\u6210\u5E38\u6570\uFF0C\u6700\u540E\u518D\u5206\u6790\u5176\u4F59\u8868\u7684\u67E5\u8BE2\u6210\u672C\uFF0C\u6BD4\u65B9\u8BF4\u8FD9\u4E2A\u67E5\u8BE2\u8BED\u53E5\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM table1 INNER JOIN table2
    ON table1.column1 = table2.column2 
    WHERE table1.primary_key = 1;
</code></pre><p>\u5F88\u660E\u663E\uFF0C\u8FD9\u4E2A\u67E5\u8BE2\u53EF\u4EE5\u4F7F\u7528\u4E3B\u952E\u548C\u5E38\u91CF\u503C\u7684\u7B49\u503C\u5339\u914D\u6765\u67E5\u8BE2<code>table1</code>\u8868\uFF0C\u4E5F\u5C31\u662F\u5728\u8FD9\u4E2A\u67E5\u8BE2\u4E2D<code>table1</code>\u8868\u76F8\u5F53\u4E8E<code>\u5E38\u91CF\u8868</code>\uFF0C\u5728\u5206\u6790\u5BF9<code>table2</code>\u8868\u7684\u67E5\u8BE2\u6210\u672C\u4E4B\u524D\uFF0C\u5C31\u4F1A\u6267\u884C\u5BF9<code>table1</code>\u8868\u7684\u67E5\u8BE2\uFF0C\u5E76\u628A\u67E5\u8BE2\u4E2D\u6D89\u53CA<code>table1</code>\u8868\u7684\u6761\u4EF6\u90FD\u66FF\u6362\u6389\uFF0C\u4E5F\u5C31\u662F\u4E0A\u8FB9\u7684\u8BED\u53E5\u4F1A\u88AB\u8F6C\u6362\u6210\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">SELECT table1\u8868\u8BB0\u5F55\u7684\u5404\u4E2A\u5B57\u6BB5\u7684\u5E38\u91CF\u503C, table2.* FROM table1 INNER JOIN table2 
    ON table1\u8868column1\u5217\u7684\u5E38\u91CF\u503C = table2.column2;
</code></pre><h2 data-id="heading-9">\u5916\u8FDE\u63A5\u6D88\u9664</h2>
<p>\u6211\u4EEC\u524D\u8FB9\u8BF4\u8FC7\uFF0C<code>\u5185\u8FDE\u63A5</code>\u7684\u9A71\u52A8\u8868\u548C\u88AB\u9A71\u52A8\u8868\u7684\u4F4D\u7F6E\u53EF\u4EE5\u76F8\u4E92\u8F6C\u6362\uFF0C\u800C<code>\u5DE6\uFF08\u5916\uFF09\u8FDE\u63A5</code>\u548C<code>\u53F3\uFF08\u5916\uFF09\u8FDE\u63A5</code>\u7684\u9A71\u52A8\u8868\u548C\u88AB\u9A71\u52A8\u8868\u662F\u56FA\u5B9A\u7684\u3002\u8FD9\u5C31\u5BFC\u81F4<code>\u5185\u8FDE\u63A5</code>\u53EF\u80FD\u901A\u8FC7\u4F18\u5316\u8868\u7684\u8FDE\u63A5\u987A\u5E8F\u6765\u964D\u4F4E\u6574\u4F53\u7684\u67E5\u8BE2\u6210\u672C\uFF0C\u800C<code>\u5916\u8FDE\u63A5</code>\u5374\u65E0\u6CD5\u4F18\u5316\u8868\u7684\u8FDE\u63A5\u987A\u5E8F\u3002\u4E3A\u4E86\u6545\u4E8B\u7684\u987A\u5229\u53D1\u5C55\uFF0C\u6211\u4EEC\u8FD8\u662F\u628A\u4E4B\u524D\u4ECB\u7ECD\u8FDE\u63A5\u539F\u7406\u65F6\u7528\u8FC7\u7684<code>t1</code>\u548C<code>t2</code>\u8868\u8BF7\u51FA\u6765\uFF0C\u4E3A\u4E86\u9632\u6B62\u5927\u5BB6\u65E9\u5C31\u5FD8\u6389\u4E86\uFF0C\u6211\u4EEC\u518D\u770B\u4E00\u4E0B\u8FD9\u4E24\u4E2A\u8868\u7684\u7ED3\u6784\uFF1A</p>
<pre><code lang="bash" class="">CREATE TABLE t1 (
    m1 int, 
    n1 char(1)
) Engine=InnoDB, CHARSET=utf8;

CREATE TABLE t2 (
    m2 int, 
    n2 char(1)
) Engine=InnoDB, CHARSET=utf8;
</code></pre><p>\u4E3A\u4E86\u5524\u9192\u5927\u5BB6\u7684\u8BB0\u5FC6\uFF0C\u6211\u4EEC\u518D\u628A\u8FD9\u4E24\u4E2A\u8868\u4E2D\u7684\u6570\u636E\u7ED9\u5C55\u793A\u4E00\u4E0B\uFF1A</p>
<pre><code lang="bash" class="">mysql&gt; SELECT * FROM t1;
+------+------+
| m1   | n1   |
+------+------+
|    1 | a    |
|    2 | b    |
|    3 | c    |
+------+------+
3 rows <span>in</span> <span>set</span> (0.00 sec)

mysql&gt; SELECT * FROM t2;
+------+------+
| m2   | n2   |
+------+------+
|    2 | b    |
|    3 | c    |
|    4 | d    |
+------+------+
3 rows <span>in</span> <span>set</span> (0.00 sec)
</code></pre><p>\u6211\u4EEC\u4E4B\u524D\u8BF4\u8FC7\uFF0C<span>\u5916\u8FDE\u63A5\u548C\u5185\u8FDE\u63A5\u7684\u672C\u8D28\u533A\u522B\u5C31\u662F\uFF1A\u5BF9\u4E8E\u5916\u8FDE\u63A5\u7684\u9A71\u52A8\u8868\u7684\u8BB0\u5F55\u6765\u8BF4\uFF0C\u5982\u679C\u65E0\u6CD5\u5728\u88AB\u9A71\u52A8\u8868\u4E2D\u627E\u5230\u5339\u914DON\u5B50\u53E5\u4E2D\u7684\u8FC7\u6EE4\u6761\u4EF6\u7684\u8BB0\u5F55\uFF0C\u90A3\u4E48\u8BE5\u8BB0\u5F55\u4ECD\u7136\u4F1A\u88AB\u52A0\u5165\u5230\u7ED3\u679C\u96C6\u4E2D\uFF0C\u5BF9\u5E94\u7684\u88AB\u9A71\u52A8\u8868\u8BB0\u5F55\u7684\u5404\u4E2A\u5B57\u6BB5\u4F7F\u7528NULL\u503C\u586B\u5145\uFF1B\u800C\u5185\u8FDE\u63A5\u7684\u9A71\u52A8\u8868\u7684\u8BB0\u5F55\u5982\u679C\u65E0\u6CD5\u5728\u88AB\u9A71\u52A8\u8868\u4E2D\u627E\u5230\u5339\u914DON\u5B50\u53E5\u4E2D\u7684\u8FC7\u6EE4\u6761\u4EF6\u7684\u8BB0\u5F55\uFF0C\u90A3\u4E48\u8BE5\u8BB0\u5F55\u4F1A\u88AB\u820D\u5F03</span>\u3002\u67E5\u8BE2\u6548\u679C\u5C31\u662F\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">mysql&gt; SELECT * FROM t1 INNER JOIN t2 ON t1.m1 = t2.m2;
+------+------+------+------+
| m1   | n1   | m2   | n2   |
+------+------+------+------+
|    2 | b    |    2 | b    |
|    3 | c    |    3 | c    |
+------+------+------+------+
2 rows <span>in</span> <span>set</span> (0.00 sec)

mysql&gt; SELECT * FROM t1 LEFT JOIN t2 ON t1.m1 = t2.m2;
+------+------+------+------+
| m1   | n1   | m2   | n2   |
+------+------+------+------+
|    2 | b    |    2 | b    |
|    3 | c    |    3 | c    |
|    1 | a    | NULL | NULL |
+------+------+------+------+
3 rows <span>in</span> <span>set</span> (0.00 sec)
</code></pre><p>\u5BF9\u4E8E\u4E0A\u8FB9\u4F8B\u5B50\u4E2D\u7684\uFF08\u5DE6\uFF09\u5916\u8FDE\u63A5\u6765\u8BF4\uFF0C\u7531\u4E8E\u9A71\u52A8\u8868<code>t1</code>\u4E2D<code>m1=1, n1='a'</code>\u7684\u8BB0\u5F55\u65E0\u6CD5\u5728\u88AB\u9A71\u52A8\u8868<code>t2</code>\u4E2D\u627E\u5230\u7B26\u5408<code>ON</code>\u5B50\u53E5\u6761\u4EF6<code>t1.m1 = t2.m2</code>\u7684\u8BB0\u5F55\uFF0C\u6240\u4EE5\u5C31\u76F4\u63A5\u628A\u8FD9\u6761\u8BB0\u5F55\u52A0\u5165\u5230\u7ED3\u679C\u96C6\uFF0C\u5BF9\u5E94\u7684<code>t2</code>\u8868\u7684<code>m2</code>\u548C<code>n2</code>\u5217\u7684\u503C\u90FD\u8BBE\u7F6E\u4E3A<code>NULL</code>\u3002</p>
<blockquote><p>\u5C0F\u8D34\u58EB\uFF1A

\u53F3\uFF08\u5916\uFF09\u8FDE\u63A5\u548C\u5DE6\uFF08\u5916\uFF09\u8FDE\u63A5\u5176\u5B9E\u53EA\u5728\u9A71\u52A8\u8868\u7684\u9009\u53D6\u65B9\u5F0F\u4E0A\u662F\u4E0D\u540C\u7684\uFF0C\u5176\u4F59\u65B9\u9762\u90FD\u662F\u4E00\u6837\u7684\uFF0C\u6240\u4EE5\u4F18\u5316\u5668\u4F1A\u9996\u5148\u628A\u53F3\uFF08\u5916\uFF09\u8FDE\u63A5\u67E5\u8BE2\u8F6C\u6362\u6210\u5DE6\uFF08\u5916\uFF09\u8FDE\u63A5\u67E5\u8BE2\u3002\u6211\u4EEC\u540E\u8FB9\u5C31\u4E0D\u518D\u5520\u53E8\u53F3\uFF08\u5916\uFF09\u8FDE\u63A5\u4E86\u3002
</p></blockquote><p>\u6211\u4EEC\u77E5\u9053<code>WHERE</code>\u5B50\u53E5\u7684\u6740\u4F24\u529B\u6BD4\u8F83\u5927\uFF0C<span>\u51E1\u662F\u4E0D\u7B26\u5408WHERE\u5B50\u53E5\u4E2D\u6761\u4EF6\u7684\u8BB0\u5F55\u90FD\u4E0D\u4F1A\u53C2\u4E0E\u8FDE\u63A5</span>\u3002\u53EA\u8981\u6211\u4EEC\u5728\u641C\u7D22\u6761\u4EF6\u4E2D\u6307\u5B9A\u5173\u4E8E\u88AB\u9A71\u52A8\u8868\u76F8\u5173\u5217\u7684\u503C\u4E0D\u4E3A<code>NULL</code>\uFF0C\u90A3\u4E48\u5916\u8FDE\u63A5\u4E2D\u5728\u88AB\u9A71\u52A8\u8868\u4E2D\u627E\u4E0D\u5230\u7B26\u5408<code>ON</code>\u5B50\u53E5\u6761\u4EF6\u7684\u9A71\u52A8\u8868\u8BB0\u5F55\u4E5F\u5C31\u88AB\u6392\u9664\u51FA\u6700\u540E\u7684\u7ED3\u679C\u96C6\u4E86\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF1A<span>\u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF1A\u5916\u8FDE\u63A5\u548C\u5185\u8FDE\u63A5\u4E5F\u5C31\u6CA1\u6709\u4EC0\u4E48\u533A\u522B\u4E86</span>\uFF01\u6BD4\u65B9\u8BF4\u8FD9\u4E2A\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">mysql&gt; SELECT * FROM t1 LEFT JOIN t2 ON t1.m1 = t2.m2 WHERE t2.n2 IS NOT NULL;
+------+------+------+------+
| m1   | n1   | m2   | n2   |
+------+------+------+------+
|    2 | b    |    2 | b    |
|    3 | c    |    3 | c    |
+------+------+------+------+
2 rows <span>in</span> <span>set</span> (0.01 sec)
</code></pre><p>\u7531\u4E8E\u6307\u5B9A\u4E86\u88AB\u9A71\u52A8\u8868<code>t2</code>\u7684<code>n2</code>\u5217\u4E0D\u5141\u8BB8\u4E3A<code>NULL</code>\uFF0C\u6240\u4EE5\u4E0A\u8FB9\u7684<code>t1</code>\u548C<code>t2</code>\u8868\u7684\u5DE6\uFF08\u5916\uFF09\u8FDE\u63A5\u67E5\u8BE2\u548C\u5185\u8FDE\u63A5\u67E5\u8BE2\u662F\u4E00\u6837\u4E00\u6837\u7684\u3002\u5F53\u7136\uFF0C\u6211\u4EEC\u4E5F\u53EF\u4EE5\u4E0D\u7528\u663E\u5F0F\u7684\u6307\u5B9A\u88AB\u9A71\u52A8\u8868\u7684\u67D0\u4E2A\u5217<code>IS NOT NULL</code>\uFF0C\u53EA\u8981\u9690\u542B\u7684\u6709\u8FD9\u4E2A\u610F\u601D\u5C31\u884C\u4E86\uFF0C\u6BD4\u65B9\u8BF4\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">mysql&gt; SELECT * FROM t1 LEFT JOIN t2 ON t1.m1 = t2.m2 WHERE t2.m2 = 2;
+------+------+------+------+
| m1   | n1   | m2   | n2   |
+------+------+------+------+
|    2 | b    |    2 | b    |
+------+------+------+------+
1 row <span>in</span> <span>set</span> (0.00 sec)
</code></pre><p>\u5728\u8FD9\u4E2A\u4F8B\u5B50\u4E2D\uFF0C\u6211\u4EEC\u5728<code>WHERE</code>\u5B50\u53E5\u4E2D\u6307\u5B9A\u4E86\u88AB\u9A71\u52A8\u8868<code>t2</code>\u7684<code>m2</code>\u5217\u7B49\u4E8E<code>2</code>\uFF0C\u4E5F\u5C31\u76F8\u5F53\u4E8E\u95F4\u63A5\u7684\u6307\u5B9A\u4E86<code>m2</code>\u5217\u4E0D\u4E3A<code>NULL</code>\u503C\uFF0C\u6240\u4EE5\u4E0A\u8FB9\u7684\u8FD9\u4E2A\u5DE6\uFF08\u5916\uFF09\u8FDE\u63A5\u67E5\u8BE2\u5176\u5B9E\u548C\u4E0B\u8FB9\u8FD9\u4E2A\u5185\u8FDE\u63A5\u67E5\u8BE2\u662F\u7B49\u4EF7\u7684\uFF1A</p>
<pre><code lang="bash" class="">mysql&gt; SELECT * FROM t1 INNER JOIN t2 ON t1.m1 = t2.m2 WHERE t2.m2 = 2;
+------+------+------+------+
| m1   | n1   | m2   | n2   |
+------+------+------+------+
|    2 | b    |    2 | b    |
+------+------+------+------+
1 row <span>in</span> <span>set</span> (0.00 sec)
</code></pre><p>\u6211\u4EEC\u628A\u8FD9\u79CD\u5728\u5916\u8FDE\u63A5\u67E5\u8BE2\u4E2D\uFF0C\u6307\u5B9A\u7684<code>WHERE</code>\u5B50\u53E5\u4E2D\u5305\u542B\u88AB\u9A71\u52A8\u8868\u4E2D\u7684\u5217\u4E0D\u4E3A<code>NULL</code>\u503C\u7684\u6761\u4EF6\u79F0\u4E4B\u4E3A<code>\u7A7A\u503C\u62D2\u7EDD</code>\uFF08\u82F1\u6587\u540D\uFF1A<code>reject-NULL</code>\uFF09\u3002<span>\u5728\u88AB\u9A71\u52A8\u8868\u7684WHERE\u5B50\u53E5\u7B26\u5408\u7A7A\u503C\u62D2\u7EDD\u7684\u6761\u4EF6\u540E\uFF0C\u5916\u8FDE\u63A5\u548C\u5185\u8FDE\u63A5\u53EF\u4EE5\u76F8\u4E92\u8F6C\u6362</span>\u3002\u8FD9\u79CD\u8F6C\u6362\u5E26\u6765\u7684\u597D\u5904\u5C31\u662F<span>\u67E5\u8BE2\u4F18\u5316\u5668\u53EF\u4EE5\u901A\u8FC7\u8BC4\u4F30\u8868\u7684\u4E0D\u540C\u8FDE\u63A5\u987A\u5E8F\u7684\u6210\u672C\uFF0C\u9009\u51FA\u6210\u672C\u6700\u4F4E\u7684\u90A3\u79CD\u8FDE\u63A5\u987A\u5E8F\u6765\u6267\u884C\u67E5\u8BE2</span>\u3002</p>
<h2 data-id="heading-10">\u5B50\u67E5\u8BE2\u4F18\u5316</h2>
<p>\u6211\u4EEC\u7684\u4E3B\u9898\u672C\u6765\u662F\u5520\u53E8<code>MySQL</code>\u67E5\u8BE2\u4F18\u5316\u5668\u662F\u5982\u4F55\u5904\u7406\u5B50\u67E5\u8BE2\u7684\uFF0C\u4F46\u662F\u6211\u8FD8\u662F\u6709\u4E00\u4E07\u4E2A\u62C5\u5FC3\u597D\u591A\u540C\u5B66\u8FDE\u5B50\u67E5\u8BE2\u7684\u8BED\u6CD5\u90FD\u6CA1\u638C\u63E1\u5168\uFF0C\u6240\u4EE5\u6211\u4EEC\u5C31\u5148\u5520\u53E8\u5520\u53E8\u4EC0\u4E48\u662F\u4E2A\u5B50\u67E5\u8BE2\uFF08\u5F53\u7136\u4E0D\u4F1A\u9762\u9762\u4FF1\u5230\u5566\uFF0C\u53EA\u662F\u8BF4\u4E2A\u5927\u6982\u54C8\uFF09\uFF0C\u7136\u540E\u518D\u5520\u53E8\u5173\u4E8E\u5B50\u67E5\u8BE2\u4F18\u5316\u7684\u4E8B\u513F\u3002</p>
<h3 data-id="heading-11">\u5B50\u67E5\u8BE2\u8BED\u6CD5</h3>
<p>\u60F3\u5FC5\u5927\u5BB6\u90FD\u662F\u5988\u5988\u751F\u4E0B\u6765\u7684\u5427\uFF0C\u8FDE\u5B59\u7334\u5B50\u90FD\u6709\u5988\u5988\u2014\u2014<span>\u77F3\u5934\u4EBA</span>\u3002\u6000\u5B55\u5988\u5988\u809A\u5B50\u91CC\u7684\u90A3\u4E2A\u4E1C\u4E1C\u5C31\u662F\u5979\u7684\u5B69\u5B50\uFF0C\u7C7B\u4F3C\u7684\uFF0C\u5728\u4E00\u4E2A\u67E5\u8BE2\u8BED\u53E5\u91CC\u7684\u67D0\u4E2A\u4F4D\u7F6E\u4E5F\u53EF\u4EE5\u6709\u53E6\u4E00\u4E2A\u67E5\u8BE2\u8BED\u53E5\uFF0C\u8FD9\u4E2A\u51FA\u73B0\u5728\u67D0\u4E2A\u67E5\u8BE2\u8BED\u53E5\u7684\u67D0\u4E2A\u4F4D\u7F6E\u4E2D\u7684\u67E5\u8BE2\u5C31\u88AB\u79F0\u4E3A<code>\u5B50\u67E5\u8BE2</code>\uFF08\u6211\u4EEC\u4E5F\u53EF\u4EE5\u79F0\u5B83\u4E3A\u5B9D\u5B9D\u67E5\u8BE2\u54C8\u54C8\uFF09\uFF0C\u90A3\u4E2A\u5145\u5F53\u201C\u5988\u5988\u201D\u89D2\u8272\u7684\u67E5\u8BE2\u4E5F\u88AB\u79F0\u4E4B\u4E3A<code>\u5916\u5C42\u67E5\u8BE2</code>\u3002\u4E0D\u50CF\u4EBA\u4EEC\u6000\u5B55\u65F6\u5B9D\u5B9D\u4EEC\u90FD\u53EA\u5728\u809A\u5B50\u91CC\uFF0C\u5B50\u67E5\u8BE2\u53EF\u4EE5\u5728\u4E00\u4E2A\u5916\u5C42\u67E5\u8BE2\u7684\u5404\u79CD\u4F4D\u7F6E\u51FA\u73B0\uFF0C\u6BD4\u5982\uFF1A</p>
<ul>
<li>
<p><code>SELECT</code>\u5B50\u53E5\u4E2D</p>
<p>\u4E5F\u5C31\u662F\u6211\u4EEC\u5E73\u65F6\u8BF4\u7684\u67E5\u8BE2\u5217\u8868\u4E2D\uFF0C\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">mysql&gt; SELECT (SELECT m1 FROM t1 LIMIT 1);
+-----------------------------+
| (SELECT m1 FROM t1 LIMIT 1) |
+-----------------------------+
|                           1 |
+-----------------------------+
1 row <span>in</span> <span>set</span> (0.00 sec)
</code></pre><p>\u5176\u4E2D\u7684<code>(SELECT m1 FROM t1 LIMIT 1)</code>\u5C31\u662F\u6211\u4EEC\u5520\u53E8\u7684\u6240\u8C13\u7684<code>\u5B50\u67E5\u8BE2</code>\u3002</p>
</li>
<li>
<p><code>FROM</code>\u5B50\u53E5\u4E2D</p>
<p>\u6BD4\u5982\uFF1A</p>
<pre><code lang="bash" class="">SELECT m, n FROM (SELECT m2 + 1 AS m, n2 AS n FROM t2 WHERE m2 &gt; 2) AS t;
+------+------+
| m    | n    |
+------+------+
|    4 | c    |
|    5 | d    |
+------+------+
2 rows <span>in</span> <span>set</span> (0.00 sec)
</code></pre><p>\u8FD9\u4E2A\u4F8B\u5B50\u4E2D\u7684\u5B50\u67E5\u8BE2\u662F\uFF1A<code>(SELECT m2 + 1 AS m, n2 AS n FROM t2 WHERE m2 &gt; 2)</code>\uFF0C\u5F88\u7279\u522B\u7684\u5730\u65B9\u662F\u5B83\u51FA\u73B0\u5728\u4E86<code>FROM</code>\u5B50\u53E5\u4E2D\u3002<code>FROM</code>\u5B50\u53E5\u91CC\u8FB9\u513F\u4E0D\u662F\u5B58\u653E\u6211\u4EEC\u8981\u67E5\u8BE2\u7684\u8868\u7684\u540D\u79F0\u4E48\uFF0C\u8FD9\u91CC\u653E\u8FDB\u6765\u4E00\u4E2A\u5B50\u67E5\u8BE2\u662F\u4E2A\u4EC0\u4E48\u9B3C\uFF1F\u5176\u5B9E\u8FD9\u91CC\u6211\u4EEC\u53EF\u4EE5\u628A\u5B50\u67E5\u8BE2\u7684\u67E5\u8BE2\u7ED3\u679C\u5F53\u4F5C\u662F\u4E00\u4E2A\u8868\uFF0C\u5B50\u67E5\u8BE2\u540E\u8FB9\u7684<code>AS t</code>\u8868\u660E\u8FD9\u4E2A\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u5C31\u76F8\u5F53\u4E8E\u4E00\u4E2A\u540D\u79F0\u4E3A<code>t</code>\u7684\u8868\uFF0C\u8FD9\u4E2A\u540D\u53EB<code>t</code>\u7684\u8868\u7684\u5217\u5C31\u662F\u5B50\u67E5\u8BE2\u7ED3\u679C\u4E2D\u7684\u5217\uFF0C\u6BD4\u5982\u4F8B\u5B50\u4E2D\u8868<code>t</code>\u5C31\u6709\u4E24\u4E2A\u5217\uFF1A<code>m</code>\u5217\u548C<code>n</code>\u5217\u3002\u8FD9\u4E2A\u653E\u5728<code>FROM</code>\u5B50\u53E5\u4E2D\u7684\u5B50\u67E5\u8BE2\u672C\u8D28\u4E0A\u76F8\u5F53\u4E8E\u4E00\u4E2A<code>\u8868</code>\uFF0C\u4F46\u53C8\u548C\u6211\u4EEC\u5E73\u5E38\u4F7F\u7528\u7684\u8868\u6709\u70B9\u513F\u4E0D\u4E00\u6837\uFF0C\u8BBE\u8BA1<code>MySQL</code>\u7684\u5927\u53D4\u628A\u8FD9\u79CD\u7531\u5B50\u67E5\u8BE2\u7ED3\u679C\u96C6\u7EC4\u6210\u7684\u8868\u79F0\u4E4B\u4E3A<code>\u6D3E\u751F\u8868</code>\u3002</p>
</li>
<li>
<p><code>WHERE</code>\u6216<code>ON</code>\u5B50\u53E5\u4E2D</p>
<p>\u628A\u5B50\u67E5\u8BE2\u653E\u5728\u5916\u5C42\u67E5\u8BE2\u7684<code>WHERE</code>\u5B50\u53E5\u6216\u8005<code>ON</code>\u5B50\u53E5\u4E2D\u53EF\u80FD\u662F\u6211\u4EEC\u6700\u5E38\u7528\u7684\u4E00\u79CD\u4F7F\u7528\u5B50\u67E5\u8BE2\u7684\u65B9\u5F0F\u4E86\uFF0C\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">mysql&gt; SELECT * FROM t1 WHERE m1 IN (SELECT m2 FROM t2);
+------+------+
| m1   | n1   |
+------+------+
|    2 | b    |
|    3 | c    |
+------+------+
2 rows <span>in</span> <span>set</span> (0.00 sec)
</code></pre><p>\u8FD9\u4E2A\u67E5\u8BE2\u8868\u660E\u6211\u4EEC\u60F3\u8981\u5C06<code>(SELECT m2 FROM t2)</code>\u8FD9\u4E2A\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u4F5C\u4E3A\u5916\u5C42\u67E5\u8BE2\u7684<code>IN</code>\u8BED\u53E5\u53C2\u6570\uFF0C\u6574\u4E2A\u67E5\u8BE2\u8BED\u53E5\u7684\u610F\u601D\u5C31\u662F\u6211\u4EEC\u60F3\u627E<code>t1</code>\u8868\u4E2D\u7684\u67D0\u4E9B\u8BB0\u5F55\uFF0C\u8FD9\u4E9B\u8BB0\u5F55\u7684<code>m1</code>\u5217\u7684\u503C\u80FD\u5728<code>t2</code>\u8868\u7684<code>m2</code>\u5217\u627E\u5230\u5339\u914D\u7684\u503C\u3002</p>
</li>
<li>
<p><code>ORDER BY</code>\u5B50\u53E5\u4E2D</p>
<p>\u867D\u7136\u8BED\u6CD5\u652F\u6301\uFF0C\u4F46\u6CA1\u5565\u5B50\u610F\u4E49\uFF0C\u4E0D\u5520\u53E8\u8FD9\u79CD\u60C5\u51B5\u4E86\u3002</p>
</li>
<li>
<p><code>GROUP BY</code>\u5B50\u53E5\u4E2D</p>
<p>\u540C\u4E0A\uFF5E</p>
</li>
</ul>
<h4 data-id="heading-12">\u6309\u8FD4\u56DE\u7684\u7ED3\u679C\u96C6\u533A\u5206\u5B50\u67E5\u8BE2</h4>
<p>\u56E0\u4E3A\u5B50\u67E5\u8BE2\u672C\u8EAB\u4E5F\u7B97\u662F\u4E00\u4E2A\u67E5\u8BE2\uFF0C\u6240\u4EE5\u53EF\u4EE5\u6309\u7167\u5B83\u4EEC\u8FD4\u56DE\u7684\u4E0D\u540C\u7ED3\u679C\u96C6\u7C7B\u578B\u800C\u628A\u8FD9\u4E9B\u5B50\u67E5\u8BE2\u5206\u4E3A\u4E0D\u540C\u7684\u7C7B\u578B\uFF1A</p>
<ul>
<li>
<p>\u6807\u91CF\u5B50\u67E5\u8BE2</p>
<p>\u90A3\u4E9B\u53EA\u8FD4\u56DE\u4E00\u4E2A\u5355\u4E00\u503C\u7684\u5B50\u67E5\u8BE2\u79F0\u4E4B\u4E3A<code>\u6807\u91CF\u5B50\u67E5\u8BE2</code>\uFF0C\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">SELECT (SELECT m1 FROM t1 LIMIT 1);
</code></pre><p>\u6216\u8005\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE m1 = (SELECT MIN(m2) FROM t2);
</code></pre><p>\u8FD9\u4E24\u4E2A\u67E5\u8BE2\u8BED\u53E5\u4E2D\u7684\u5B50\u67E5\u8BE2\u90FD\u8FD4\u56DE\u4E00\u4E2A\u5355\u4E00\u7684\u503C\uFF0C\u4E5F\u5C31\u662F\u4E00\u4E2A<code>\u6807\u91CF</code>\u3002\u8FD9\u4E9B\u6807\u91CF\u5B50\u67E5\u8BE2\u53EF\u4EE5\u4F5C\u4E3A\u4E00\u4E2A\u5355\u4E00\u503C\u6216\u8005\u8868\u8FBE\u5F0F\u7684\u4E00\u90E8\u5206\u51FA\u73B0\u5728\u67E5\u8BE2\u8BED\u53E5\u7684\u5404\u4E2A\u5730\u65B9\u3002</p>
</li>
<li>
<p>\u884C\u5B50\u67E5\u8BE2</p>
<p>\u987E\u540D\u601D\u4E49\uFF0C\u5C31\u662F\u8FD4\u56DE\u4E00\u6761\u8BB0\u5F55\u7684\u5B50\u67E5\u8BE2\uFF0C\u4E0D\u8FC7\u8FD9\u6761\u8BB0\u5F55\u9700\u8981\u5305\u542B\u591A\u4E2A\u5217\uFF08\u53EA\u5305\u542B\u4E00\u4E2A\u5217\u5C31\u6210\u4E86\u6807\u91CF\u5B50\u67E5\u8BE2\u4E86\uFF09\u3002\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE (m1, n1) = (SELECT m2, n2 FROM t2 LIMIT 1);
</code></pre><p>\u5176\u4E2D\u7684<code>(SELECT m2, n2 FROM t2 LIMIT 1)</code>\u5C31\u662F\u4E00\u4E2A\u884C\u5B50\u67E5\u8BE2\uFF0C\u6574\u6761\u8BED\u53E5\u7684\u542B\u4E49\u5C31\u662F\u8981\u4ECE<code>t1</code>\u8868\u4E2D\u627E\u4E00\u4E9B\u8BB0\u5F55\uFF0C\u8FD9\u4E9B\u8BB0\u5F55\u7684<code>m1</code>\u548C<code>n1</code>\u5217\u5206\u522B\u7B49\u4E8E\u5B50\u67E5\u8BE2\u7ED3\u679C\u4E2D\u7684<code>m2</code>\u548C<code>n2</code>\u5217\u3002</p>
</li>
<li>
<p>\u5217\u5B50\u67E5\u8BE2</p>
<p>\u5217\u5B50\u67E5\u8BE2\u81EA\u7136\u5C31\u662F\u67E5\u8BE2\u51FA\u4E00\u4E2A\u5217\u7684\u6570\u636E\u55BD\uFF0C\u4E0D\u8FC7\u8FD9\u4E2A\u5217\u7684\u6570\u636E\u9700\u8981\u5305\u542B\u591A\u6761\u8BB0\u5F55\uFF08\u53EA\u5305\u542B\u4E00\u6761\u8BB0\u5F55\u5C31\u6210\u4E86\u6807\u91CF\u5B50\u67E5\u8BE2\u4E86\uFF09\u3002\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE m1 IN (SELECT m2 FROM t2);
</code></pre><p>\u5176\u4E2D\u7684<code>(SELECT m2 FROM t2)</code>\u5C31\u662F\u4E00\u4E2A\u5217\u5B50\u67E5\u8BE2\uFF0C\u8868\u660E\u67E5\u8BE2\u51FA<code>t2</code>\u8868\u7684<code>m2</code>\u5217\u7684\u503C\u4F5C\u4E3A\u5916\u5C42\u67E5\u8BE2<code>IN</code>\u8BED\u53E5\u7684\u53C2\u6570\u3002</p>
</li>
<li>
<p>\u8868\u5B50\u67E5\u8BE2</p>
<p>\u987E\u540D\u601D\u4E49\uFF0C\u5C31\u662F\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u65E2\u5305\u542B\u5F88\u591A\u6761\u8BB0\u5F55\uFF0C\u53C8\u5305\u542B\u5F88\u591A\u4E2A\u5217\uFF0C\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE (m1, n1) IN (SELECT m2, n2 FROM t2);
</code></pre><p>\u5176\u4E2D\u7684<code>(SELECT m2, n2 FROM t2)</code>\u5C31\u662F\u4E00\u4E2A\u8868\u5B50\u67E5\u8BE2\uFF0C\u8FD9\u91CC\u9700\u8981\u548C\u884C\u5B50\u67E5\u8BE2\u5BF9\u6BD4\u4E00\u4E0B\uFF0C\u884C\u5B50\u67E5\u8BE2\u4E2D\u6211\u4EEC\u7528\u4E86<code>LIMIT 1</code>\u6765\u4FDD\u8BC1\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u53EA\u6709\u4E00\u6761\u8BB0\u5F55\uFF0C\u8868\u5B50\u67E5\u8BE2\u4E2D\u4E0D\u9700\u8981\u8FD9\u4E2A\u9650\u5236\u3002</p>
</li>
</ul>
<h4 data-id="heading-13">\u6309\u4E0E\u5916\u5C42\u67E5\u8BE2\u5173\u7CFB\u6765\u533A\u5206\u5B50\u67E5\u8BE2</h4>
<ul>
<li>
<p>\u4E0D\u76F8\u5173\u5B50\u67E5\u8BE2</p>
<p>\u5982\u679C\u5B50\u67E5\u8BE2\u53EF\u4EE5\u5355\u72EC\u8FD0\u884C\u51FA\u7ED3\u679C\uFF0C\u800C\u4E0D\u4F9D\u8D56\u4E8E\u5916\u5C42\u67E5\u8BE2\u7684\u503C\uFF0C\u6211\u4EEC\u5C31\u53EF\u4EE5\u628A\u8FD9\u4E2A\u5B50\u67E5\u8BE2\u79F0\u4E4B\u4E3A<code>\u4E0D\u76F8\u5173\u5B50\u67E5\u8BE2</code>\u3002\u6211\u4EEC\u524D\u8FB9\u4ECB\u7ECD\u7684\u90A3\u4E9B\u5B50\u67E5\u8BE2\u5168\u90E8\u90FD\u53EF\u4EE5\u770B\u4F5C\u4E0D\u76F8\u5173\u5B50\u67E5\u8BE2\uFF0C\u6240\u4EE5\u4E5F\u5C31\u4E0D\u4E3E\u4F8B\u5B50\u4E86\u54C8\u3002</p>
</li>
<li>
<p>\u76F8\u5173\u5B50\u67E5\u8BE2</p>
<p>\u5982\u679C\u5B50\u67E5\u8BE2\u7684\u6267\u884C\u9700\u8981\u4F9D\u8D56\u4E8E\u5916\u5C42\u67E5\u8BE2\u7684\u503C\uFF0C\u6211\u4EEC\u5C31\u53EF\u4EE5\u628A\u8FD9\u4E2A\u5B50\u67E5\u8BE2\u79F0\u4E4B\u4E3A<code>\u76F8\u5173\u5B50\u67E5\u8BE2</code>\u3002\u6BD4\u5982\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE m1 IN (SELECT m2 FROM t2 WHERE n1 = n2);
</code></pre><p>\u4F8B\u5B50\u4E2D\u7684\u5B50\u67E5\u8BE2\u662F<code>(SELECT m2 FROM t2 WHERE n1 = n2)</code>\uFF0C\u53EF\u662F\u8FD9\u4E2A\u67E5\u8BE2\u4E2D\u6709\u4E00\u4E2A\u641C\u7D22\u6761\u4EF6\u662F<code>n1 = n2</code>\uFF0C\u522B\u5FD8\u4E86<code>n1</code>\u662F\u8868<code>t1</code>\u7684\u5217\uFF0C\u4E5F\u5C31\u662F\u5916\u5C42\u67E5\u8BE2\u7684\u5217\uFF0C\u4E5F\u5C31\u662F\u8BF4\u5B50\u67E5\u8BE2\u7684\u6267\u884C\u9700\u8981\u4F9D\u8D56\u4E8E\u5916\u5C42\u67E5\u8BE2\u7684\u503C\uFF0C\u6240\u4EE5\u8FD9\u4E2A\u5B50\u67E5\u8BE2\u5C31\u662F\u4E00\u4E2A<code>\u76F8\u5173\u5B50\u67E5\u8BE2</code>\u3002</p>
</li>
</ul>
<h4 data-id="heading-14">\u5B50\u67E5\u8BE2\u5728\u5E03\u5C14\u8868\u8FBE\u5F0F\u4E2D\u7684\u4F7F\u7528</h4>
<p>\u4F60\u8BF4\u5199\u4E0B\u8FB9\u8FD9\u6837\u7684\u5B50\u67E5\u8BE2\u6709\u5565\u610F\u4E49\uFF1A</p>
<pre><code lang="bash" class="">SELECT (SELECT m1 FROM t1 LIMIT 1);
</code></pre><p>\u8C8C\u4F3C\u6CA1\u5565\u610F\u4E49\uFF5E \u6211\u4EEC\u5E73\u65F6\u7528\u5B50\u67E5\u8BE2\u6700\u591A\u7684\u5730\u65B9\u5C31\u662F\u628A\u5B83\u4F5C\u4E3A\u5E03\u5C14\u8868\u8FBE\u5F0F\u7684\u4E00\u90E8\u5206\u6765\u4F5C\u4E3A\u641C\u7D22\u6761\u4EF6\u7528\u5728<code>WHERE</code>\u5B50\u53E5\u6216\u8005<code>ON</code>\u5B50\u53E5\u91CC\u3002\u6240\u4EE5\u6211\u4EEC\u8FD9\u91CC\u6765\u603B\u7ED3\u4E00\u4E0B\u5B50\u67E5\u8BE2\u5728\u5E03\u5C14\u8868\u8FBE\u5F0F\u4E2D\u7684\u4F7F\u7528\u573A\u666F\u3002</p>
<ul>
<li>
<p>\u4F7F\u7528<code>=</code>\u3001<code>&gt;</code>\u3001<code>&lt;</code>\u3001<code>&gt;=</code>\u3001<code>&lt;=</code>\u3001<code>&lt;&gt;</code>\u3001<code>!=</code>\u3001<code>&lt;=&gt;</code>\u4F5C\u4E3A\u5E03\u5C14\u8868\u8FBE\u5F0F\u7684\u64CD\u4F5C\u7B26</p>
<p>\u8FD9\u4E9B\u64CD\u4F5C\u7B26\u5177\u4F53\u662F\u5565\u610F\u601D\u5C31\u4E0D\u7528\u6211\u591A\u4ECB\u7ECD\u4E86\u5427\uFF0C\u5982\u679C\u4F60\u4E0D\u77E5\u9053\u7684\u8BDD\uFF0C\u90A3\u6211\u771F\u7684\u5F88\u4F69\u670D\u4F60\u662F\u9760\u7740\u5565\u52C7\u6C14\u4E00\u53E3\u6C14\u770B\u5230\u8FD9\u91CC\u7684\uFF5E \u4E3A\u4E86\u65B9\u4FBF\uFF0C\u6211\u4EEC\u5C31\u628A\u8FD9\u4E9B\u64CD\u4F5C\u7B26\u79F0\u4E3A<code>comparison_operator</code>\u5427\uFF0C\u6240\u4EE5\u5B50\u67E5\u8BE2\u7EC4\u6210\u7684\u5E03\u5C14\u8868\u8FBE\u5F0F\u5C31\u957F\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">\u64CD\u4F5C\u6570 comparison_operator (\u5B50\u67E5\u8BE2)
</code></pre><p>\u8FD9\u91CC\u7684<code>\u64CD\u4F5C\u6570</code>\u53EF\u4EE5\u662F\u67D0\u4E2A\u5217\u540D\uFF0C\u6216\u8005\u662F\u4E00\u4E2A\u5E38\u91CF\uFF0C\u6216\u8005\u662F\u4E00\u4E2A\u66F4\u590D\u6742\u7684\u8868\u8FBE\u5F0F\uFF0C\u751A\u81F3\u53EF\u4EE5\u662F\u53E6\u4E00\u4E2A\u5B50\u67E5\u8BE2\u3002\u4F46\u662F\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C<span>\u8FD9\u91CC\u7684\u5B50\u67E5\u8BE2\u53EA\u80FD\u662F\u6807\u91CF\u5B50\u67E5\u8BE2\u6216\u8005\u884C\u5B50\u67E5\u8BE2\uFF0C\u4E5F\u5C31\u662F\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u53EA\u80FD\u8FD4\u56DE\u4E00\u4E2A\u5355\u4E00\u7684\u503C\u6216\u8005\u53EA\u80FD\u662F\u4E00\u6761\u8BB0\u5F55</span>\u3002\u6BD4\u5982\u8FD9\u6837\uFF08\u6807\u91CF\u5B50\u67E5\u8BE2\uFF09\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE m1 &lt; (SELECT MIN(m2) FROM t2);
</code></pre><p>\u6216\u8005\u8FD9\u6837\uFF08\u884C\u5B50\u67E5\u8BE2\uFF09\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE (m1, n1) = (SELECT m2, n2 FROM t2 LIMIT 1);
</code></pre></li>
<li>
<p>[NOT] IN/ANY/SOME/ALL\u5B50\u67E5\u8BE2</p>
<p>\u5BF9\u4E8E\u5217\u5B50\u67E5\u8BE2\u548C\u8868\u5B50\u67E5\u8BE2\u6765\u8BF4\uFF0C\u5B83\u4EEC\u7684\u7ED3\u679C\u96C6\u4E2D\u5305\u542B\u5F88\u591A\u6761\u8BB0\u5F55\uFF0C\u8FD9\u4E9B\u8BB0\u5F55\u76F8\u5F53\u4E8E\u662F\u4E00\u4E2A\u96C6\u5408\uFF0C\u6240\u4EE5\u5C31\u4E0D\u80FD\u5355\u7EAF\u7684\u548C\u53E6\u5916\u4E00\u4E2A\u64CD\u4F5C\u6570\u4F7F\u7528<code>comparison_operator</code>\u6765\u7EC4\u6210\u5E03\u5C14\u8868\u8FBE\u5F0F\u4E86\uFF0C<code>MySQL</code>\u901A\u8FC7\u4E0B\u9762\u7684\u8BED\u6CD5\u6765\u652F\u6301\u67D0\u4E2A\u64CD\u4F5C\u6570\u548C\u4E00\u4E2A\u96C6\u5408\u7EC4\u6210\u4E00\u4E2A\u5E03\u5C14\u8868\u8FBE\u5F0F\uFF1A</p>
<ul>
<li>
<p><code>IN</code>\u6216\u8005<code>NOT IN</code></p>
<p>\u5177\u4F53\u7684\u8BED\u6CD5\u5F62\u5F0F\u5982\u4E0B\uFF1A</p>
<pre><code lang="bash" class="">\u64CD\u4F5C\u6570 [NOT] IN (\u5B50\u67E5\u8BE2)
</code></pre><p>\u8FD9\u4E2A\u5E03\u5C14\u8868\u8FBE\u5F0F\u7684\u610F\u601D\u662F\u7528\u6765\u5224\u65AD\u67D0\u4E2A\u64CD\u4F5C\u6570\u5728\u4E0D\u5728\u7531\u5B50\u67E5\u8BE2\u7ED3\u679C\u96C6\u7EC4\u6210\u7684\u96C6\u5408\u4E2D\uFF0C\u6BD4\u5982\u4E0B\u8FB9\u7684\u67E5\u8BE2\u7684\u610F\u601D\u662F\u627E\u51FA<code>t1</code>\u8868\u4E2D\u7684\u67D0\u4E9B\u8BB0\u5F55\uFF0C\u8FD9\u4E9B\u8BB0\u5F55\u5B58\u5728\u4E8E\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u96C6\u4E2D\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE (m1, n1) IN (SELECT m2, n2 FROM t2);
</code></pre></li>
<li>
<p><code>ANY/SOME</code>\uFF08<code>ANY</code>\u548C<code>SOME</code>\u662F\u540C\u4E49\u8BCD\uFF09</p>
<p>\u5177\u4F53\u7684\u8BED\u6CD5\u5F62\u5F0F\u5982\u4E0B\uFF1A</p>
<pre><code lang="bash" class="">\u64CD\u4F5C\u6570 comparison_operator ANY/SOME(\u5B50\u67E5\u8BE2)
</code></pre><p>\u8FD9\u4E2A\u5E03\u5C14\u8868\u8FBE\u5F0F\u7684\u610F\u601D\u662F\u53EA\u8981\u5B50\u67E5\u8BE2\u7ED3\u679C\u96C6\u4E2D\u5B58\u5728\u67D0\u4E2A\u503C\u548C\u7ED9\u5B9A\u7684\u64CD\u4F5C\u6570\u505A<code>comparison_operator</code>\u6BD4\u8F83\u7ED3\u679C\u4E3A<code>TRUE</code>\uFF0C\u90A3\u4E48\u6574\u4E2A\u8868\u8FBE\u5F0F\u7684\u7ED3\u679C\u5C31\u4E3A<code>TRUE</code>\uFF0C\u5426\u5219\u6574\u4E2A\u8868\u8FBE\u5F0F\u7684\u7ED3\u679C\u5C31\u4E3A<code>FALSE</code>\u3002\u6BD4\u65B9\u8BF4\u4E0B\u8FB9\u8FD9\u4E2A\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE m1 &gt; ANY(SELECT m2 FROM t2);
</code></pre><p>\u8FD9\u4E2A\u67E5\u8BE2\u7684\u610F\u601D\u5C31\u662F\u5BF9\u4E8E<code>t1</code>\u8868\u7684\u67D0\u6761\u8BB0\u5F55\u7684<code>m1</code>\u5217\u7684\u503C\u6765\u8BF4\uFF0C\u5982\u679C\u5B50\u67E5\u8BE2<code>(SELECT m2 FROM t2)</code>\u7684\u7ED3\u679C\u96C6\u4E2D\u5B58\u5728\u4E00\u4E2A\u5C0F\u4E8E<code>m1</code>\u5217\u7684\u503C\uFF0C\u90A3\u4E48\u6574\u4E2A\u5E03\u5C14\u8868\u8FBE\u5F0F\u7684\u503C\u5C31\u662F<code>TRUE</code>\uFF0C\u5426\u5219\u4E3A<code>FALSE</code>\uFF0C\u4E5F\u5C31\u662F\u8BF4\u53EA\u8981<code>m1</code>\u5217\u7684\u503C\u5927\u4E8E\u5B50\u67E5\u8BE2\u7ED3\u679C\u96C6\u4E2D\u6700\u5C0F\u7684\u503C\uFF0C\u6574\u4E2A\u8868\u8FBE\u5F0F\u7684\u7ED3\u679C\u5C31\u662F<code>TRUE</code>\uFF0C\u6240\u4EE5\u4E0A\u8FB9\u7684\u67E5\u8BE2\u672C\u8D28\u4E0A\u7B49\u4EF7\u4E8E\u8FD9\u4E2A\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE m1 &gt; (SELECT MIN(m2) FROM t2);
</code></pre><p>\u53E6\u5916\uFF0C<span>=ANY\u76F8\u5F53\u4E8E\u5224\u65AD\u5B50\u67E5\u8BE2\u7ED3\u679C\u96C6\u4E2D\u662F\u5426\u5B58\u5728\u67D0\u4E2A\u503C\u548C\u7ED9\u5B9A\u7684\u64CD\u4F5C\u6570\u76F8\u7B49\uFF0C\u5B83\u7684\u542B\u4E49\u548CIN\u662F\u76F8\u540C\u7684</span>\u3002</p>
</li>
<li>
<p><code>ALL</code></p>
<p>\u5177\u4F53\u7684\u8BED\u6CD5\u5F62\u5F0F\u5982\u4E0B\uFF1A</p>
<pre><code lang="bash" class="">\u64CD\u4F5C\u6570 comparison_operator ALL(\u5B50\u67E5\u8BE2)
</code></pre><p>\u8FD9\u4E2A\u5E03\u5C14\u8868\u8FBE\u5F0F\u7684\u610F\u601D\u662F\u5B50\u67E5\u8BE2\u7ED3\u679C\u96C6\u4E2D\u6240\u6709\u7684\u503C\u548C\u7ED9\u5B9A\u7684\u64CD\u4F5C\u6570\u505A<code>comparison_operator</code>\u6BD4\u8F83\u7ED3\u679C\u4E3A<code>TRUE</code>\uFF0C\u90A3\u4E48\u6574\u4E2A\u8868\u8FBE\u5F0F\u7684\u7ED3\u679C\u5C31\u4E3A<code>TRUE</code>\uFF0C\u5426\u5219\u6574\u4E2A\u8868\u8FBE\u5F0F\u7684\u7ED3\u679C\u5C31\u4E3A<code>FALSE</code>\u3002\u6BD4\u65B9\u8BF4\u4E0B\u8FB9\u8FD9\u4E2A\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE m1 &gt; ALL(SELECT m2 FROM t2);
</code></pre><p>\u8FD9\u4E2A\u67E5\u8BE2\u7684\u610F\u601D\u5C31\u662F\u5BF9\u4E8E<code>t1</code>\u8868\u7684\u67D0\u6761\u8BB0\u5F55\u7684<code>m1</code>\u5217\u7684\u503C\u6765\u8BF4\uFF0C\u5982\u679C\u5B50\u67E5\u8BE2<code>(SELECT m2 FROM t2)</code>\u7684\u7ED3\u679C\u96C6\u4E2D\u7684\u6240\u6709\u503C\u90FD\u5C0F\u4E8E<code>m1</code>\u5217\u7684\u503C\uFF0C\u90A3\u4E48\u6574\u4E2A\u5E03\u5C14\u8868\u8FBE\u5F0F\u7684\u503C\u5C31\u662F<code>TRUE</code>\uFF0C\u5426\u5219\u4E3A<code>FALSE</code>\uFF0C\u4E5F\u5C31\u662F\u8BF4\u53EA\u8981<code>m1</code>\u5217\u7684\u503C\u5927\u4E8E\u5B50\u67E5\u8BE2\u7ED3\u679C\u96C6\u4E2D\u6700\u5927\u7684\u503C\uFF0C\u6574\u4E2A\u8868\u8FBE\u5F0F\u7684\u7ED3\u679C\u5C31\u662F<code>TRUE</code>\uFF0C\u6240\u4EE5\u4E0A\u8FB9\u7684\u67E5\u8BE2\u672C\u8D28\u4E0A\u7B49\u4EF7\u4E8E\u8FD9\u4E2A\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE m1 &gt; (SELECT MAX(m2) FROM t2);

</code></pre><blockquote><p>\u5C0F\u8D34\u58EB\uFF1A

\u89C9\u5F97ANY\u548CALL\u6709\u70B9\u6655\u7684\u540C\u5B66\u591A\u770B\u4E24\u904D\u54C8\uFF5E
</p></blockquote></li>
</ul>
</li>
<li>
<p>EXISTS\u5B50\u67E5\u8BE2</p>
<p>\u6709\u7684\u65F6\u5019\u6211\u4EEC\u4EC5\u4EC5\u9700\u8981\u5224\u65AD\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u96C6\u4E2D\u662F\u5426\u6709\u8BB0\u5F55\uFF0C\u800C\u4E0D\u5728\u4E4E\u5B83\u7684\u8BB0\u5F55\u5177\u4F53\u662F\u4E2A\u5565\uFF0C\u53EF\u4EE5\u4F7F\u7528\u628A<code>EXISTS</code>\u6216\u8005<code>NOT EXISTS</code>\u653E\u5728\u5B50\u67E5\u8BE2\u8BED\u53E5\u524D\u8FB9\uFF0C\u5C31\u50CF\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">[NOT] EXISTS (\u5B50\u67E5\u8BE2)
</code></pre><p>\u6211\u4EEC\u4E3E\u4E00\u4E2A\u4F8B\u5B50\u554A\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE EXISTS (SELECT 1 FROM t2);
</code></pre><p>\u5BF9\u4E8E\u5B50\u67E5\u8BE2<code>(SELECT 1 FROM t2)</code>\u6765\u8BF4\uFF0C\u6211\u4EEC\u5E76\u4E0D\u5173\u5FC3\u8FD9\u4E2A\u5B50\u67E5\u8BE2\u6700\u540E\u5230\u5E95\u67E5\u8BE2\u51FA\u7684\u7ED3\u679C\u662F\u4EC0\u4E48\uFF0C\u6240\u4EE5\u67E5\u8BE2\u5217\u8868\u91CC\u586B<code>*</code>\u3001\u67D0\u4E2A\u5217\u540D\uFF0C\u6216\u8005\u5176\u4ED6\u5565\u4E1C\u897F\u90FD\u65E0\u6240\u8C13\uFF0C\u6211\u4EEC\u771F\u6B63\u5173\u5FC3\u7684\u662F\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u96C6\u4E2D\u662F\u5426\u5B58\u5728\u8BB0\u5F55\u3002\u4E5F\u5C31\u662F\u8BF4\u53EA\u8981<code>(SELECT 1 FROM t2)</code>\u8FD9\u4E2A\u67E5\u8BE2\u4E2D\u6709\u8BB0\u5F55\uFF0C\u90A3\u4E48\u6574\u4E2A<code>EXISTS</code>\u8868\u8FBE\u5F0F\u7684\u7ED3\u679C\u5C31\u4E3A<code>TRUE</code>\u3002</p>
</li>
</ul>
<h4 data-id="heading-15">\u5B50\u67E5\u8BE2\u8BED\u6CD5\u6CE8\u610F\u4E8B\u9879</h4>
<ul>
<li>
<p>\u5B50\u67E5\u8BE2\u5FC5\u987B\u7528\u5C0F\u62EC\u53F7\u6269\u8D77\u6765\u3002</p>
<p>\u4E0D\u6269\u8D77\u6765\u7684\u5B50\u67E5\u8BE2\u662F\u975E\u6CD5\u7684\uFF0C\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">mysql&gt; SELECT SELECT m1 FROM t1;

ERROR 1064 (42000): You have an error <span>in</span> your SQL syntax; check the manual that corresponds to your MySQL server version <span>for</span> the right syntax to use near <span>'SELECT m1 FROM t1'</span> at line 1
</code></pre></li>
<li>
<p>\u5728<code>SELECT</code>\u5B50\u53E5\u4E2D\u7684\u5B50\u67E5\u8BE2\u5FC5\u987B\u662F\u6807\u91CF\u5B50\u67E5\u8BE2\u3002</p>
<p>\u5982\u679C\u5B50\u67E5\u8BE2\u7ED3\u679C\u96C6\u4E2D\u6709\u591A\u4E2A\u5217\u6216\u8005\u591A\u4E2A\u884C\uFF0C\u90FD\u4E0D\u5141\u8BB8\u653E\u5728<code>SELECT</code>\u5B50\u53E5\u4E2D\uFF0C\u4E5F\u5C31\u662F\u67E5\u8BE2\u5217\u8868\u4E2D\uFF0C\u6BD4\u5982\u8FD9\u6837\u5C31\u662F\u975E\u6CD5\u7684\uFF1A</p>
<pre><code lang="bash" class="">mysql&gt; SELECT (SELECT m1, n1 FROM t1);

ERROR 1241 (21000): Operand should contain 1 column(s)
</code></pre></li>
<li>
<p>\u5728\u60F3\u8981\u5F97\u5230\u6807\u91CF\u5B50\u67E5\u8BE2\u6216\u8005\u884C\u5B50\u67E5\u8BE2\uFF0C\u4F46\u53C8\u4E0D\u80FD\u4FDD\u8BC1\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u96C6\u53EA\u6709\u4E00\u6761\u8BB0\u5F55\u65F6\uFF0C\u5E94\u8BE5\u4F7F\u7528<code>LIMIT 1</code>\u8BED\u53E5\u6765\u9650\u5236\u8BB0\u5F55\u6570\u91CF\u3002</p>
</li>
<li>
<p>\u5BF9\u4E8E<code>[NOT] IN/ANY/SOME/ALL</code>\u5B50\u67E5\u8BE2\u6765\u8BF4\uFF0C\u5B50\u67E5\u8BE2\u4E2D\u4E0D\u5141\u8BB8\u6709<code>LIMIT</code>\u8BED\u53E5\u3002</p>
<p>\u6BD4\u5982\u8FD9\u6837\u662F\u975E\u6CD5\u7684\uFF1A</p>
<pre><code lang="bash" class="">mysql&gt; SELECT * FROM t1 WHERE m1 IN (SELECT * FROM t2 LIMIT 2);

ERROR 1235 (42000): This version of MySQL doesn<span>'t yet support '</span>LIMIT &amp; IN/ALL/ANY/SOME subquery<span>'
</span></code></pre><p>\u4E3A\u5565\u4E0D\u5408\u6CD5\uFF1F\u4EBA\u5BB6\u5C31\u8FD9\u4E48\u89C4\u5B9A\u7684\uFF0C\u4E0D\u89E3\u91CA\uFF5E \u53EF\u80FD\u4EE5\u540E\u7684\u7248\u672C\u4F1A\u652F\u6301\u5427\u3002\u6B63\u56E0\u4E3A<code>[NOT] IN/ANY/SOME/ALL</code>\u5B50\u67E5\u8BE2\u4E0D\u652F\u6301<code>LIMIT</code>\u8BED\u53E5\uFF0C\u6240\u4EE5\u5B50\u67E5\u8BE2\u4E2D\u7684\u8FD9\u4E9B\u8BED\u53E5\u4E5F\u5C31\u662F\u591A\u4F59\u7684\u4E86\uFF1A</p>
<ul>
<li>
<p><code>ORDER BY</code>\u5B50\u53E5</p>
<p>\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u5176\u5B9E\u5C31\u76F8\u5F53\u4E8E\u4E00\u4E2A\u96C6\u5408\uFF0C\u96C6\u5408\u91CC\u7684\u503C\u6392\u4E0D\u6392\u5E8F\u4E00\u70B9\u513F\u90FD\u4E0D\u91CD\u8981\uFF0C\u6BD4\u5982\u4E0B\u8FB9\u8FD9\u4E2A\u8BED\u53E5\u4E2D\u7684<code>ORDER BY</code>\u5B50\u53E5\u7B80\u76F4\u5C31\u662F\u753B\u86C7\u6DFB\u8DB3\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE m1 IN (SELECT m2 FROM t2 ORDER BY m2);
</code></pre></li>
<li>
<p><code>DISTINCT</code>\u8BED\u53E5</p>
<p>\u96C6\u5408\u91CC\u7684\u503C\u53BB\u4E0D\u53BB\u91CD\u4E5F\u6CA1\u5565\u610F\u4E49\uFF0C\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE m1 IN (SELECT DISTINCT m2 FROM t2);
</code></pre></li>
<li>
<p>\u6CA1\u6709\u805A\u96C6\u51FD\u6570\u4EE5\u53CA<code>HAVING</code>\u5B50\u53E5\u7684<code>GROUP BY</code>\u5B50\u53E5\u3002</p>
<p>\u5728\u6CA1\u6709\u805A\u96C6\u51FD\u6570\u4EE5\u53CA<code>HAVING</code>\u5B50\u53E5\u65F6\uFF0C<code>GROUP BY</code>\u5B50\u53E5\u5C31\u662F\u4E2A\u6446\u8BBE\uFF0C\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM t1 WHERE m1 IN (SELECT m2 FROM t2 GROUP BY m2);
</code></pre></li>
</ul>
<p>\u5BF9\u4E8E\u8FD9\u4E9B\u5197\u4F59\u7684\u8BED\u53E5\uFF0C<span>\u67E5\u8BE2\u4F18\u5316\u5668\u5728\u4E00\u5F00\u59CB\u5C31\u628A\u5B83\u4EEC\u7ED9\u5E72\u6389\u4E86</span>\u3002</p>
</li>
<li>
<p>\u4E0D\u5141\u8BB8\u5728\u4E00\u6761\u8BED\u53E5\u4E2D\u589E\u5220\u6539\u67D0\u4E2A\u8868\u7684\u8BB0\u5F55\u65F6\u540C\u65F6\u8FD8\u5BF9\u8BE5\u8868\u8FDB\u884C\u5B50\u67E5\u8BE2\u3002</p>
<p>\u6BD4\u65B9\u8BF4\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">mysql&gt; DELETE FROM t1 WHERE m1 &lt; (SELECT MAX(m1) FROM t1);

ERROR 1093 (HY000): You can<span>'t specify target table '</span>t1<span>' for update in FROM clause
</span></code></pre></li>
</ul>
<h3 data-id="heading-16">\u5B50\u67E5\u8BE2\u5728MySQL\u4E2D\u662F\u600E\u4E48\u6267\u884C\u7684</h3>
<p>\u597D\u4E86\uFF0C\u5173\u4E8E\u5B50\u67E5\u8BE2\u7684\u57FA\u7840\u8BED\u6CD5\u6211\u4EEC\u7528\u6700\u5FEB\u7684\u901F\u5EA6\u6E29\u4E60\u4E86\u4E00\u904D\uFF0C\u5982\u679C\u60F3\u4E86\u89E3\u66F4\u591A\u8BED\u6CD5\u7EC6\u8282\uFF0C\u5927\u5BB6\u53EF\u4EE5\u53BB\u67E5\u770B\u4E00\u4E0B<code>MySQL</code>\u7684\u6587\u6863\u54C8\uFF0C\u73B0\u5728\u6211\u4EEC\u5C31\u5047\u8BBE\u5404\u4F4D\u90FD\u61C2\u4E86\u5565\u662F\u4E2A\u5B50\u67E5\u8BE2\u4E86\u5594\uFF0C\u63A5\u4E0B\u6765\u5C31\u8981\u5520\u53E8\u5177\u4F53\u67D0\u79CD\u7C7B\u578B\u7684\u5B50\u67E5\u8BE2\u5728<code>MySQL</code>\u4E2D\u662F\u600E\u4E48\u6267\u884C\u7684\u4E86\uFF0C\u60F3\u60F3\u5C31\u6709\u70B9\u513F\u5C0F\u6FC0\u52A8\u5462\uFF5E \u5F53\u7136\uFF0C\u4E3A\u4E86\u6545\u4E8B\u7684\u987A\u5229\u53D1\u5C55\uFF0C\u6211\u4EEC\u7684\u4F8B\u5B50\u4E5F\u9700\u8981\u8DDF\u968F\u5F62\u52BF\u9E1F\u67AA\u6362\u70AE\uFF0C\u8FD8\u662F\u8981\u796D\u51FA\u6211\u4EEC\u7528\u4E86n\u904D\u7684<code>single_table</code>\u8868\uFF1A</p>
<pre><code lang="bash" class="">CREATE TABLE single_table (
    id INT NOT NULL AUTO_INCREMENT,
    key1 VARCHAR(100),
    key2 INT,
    key3 VARCHAR(100),
    key_part1 VARCHAR(100),
    key_part2 VARCHAR(100),
    key_part3 VARCHAR(100),
    common_field VARCHAR(100),
    PRIMARY KEY (id),
    KEY idx_key1 (key1),
    UNIQUE KEY idx_key2 (key2),
    KEY idx_key3 (key3),
    KEY idx_key_part(key_part1, key_part2, key_part3)
) Engine=InnoDB CHARSET=utf8;
</code></pre><p>\u4E3A\u4E86\u65B9\u4FBF\uFF0C\u6211\u4EEC\u5047\u8BBE\u6709\u4E24\u4E2A\u8868<code>s1</code>\u3001<code>s2</code>\u4E0E\u8FD9\u4E2A<code>single_table</code>\u8868\u7684\u6784\u9020\u662F\u76F8\u540C\u7684\uFF0C\u800C\u4E14\u8FD9\u4E24\u4E2A\u8868\u91CC\u8FB9\u513F\u670910000\u6761\u8BB0\u5F55\uFF0C\u9664id\u5217\u5916\u5176\u4F59\u7684\u5217\u90FD\u63D2\u5165\u968F\u673A\u503C\u3002\u4E0B\u8FB9\u6B63\u5F0F\u5F00\u59CB\u6211\u4EEC\u7684\u8868\u6F14\u3002</p>
<h4 data-id="heading-17">\u5C0F\u767D\u4EEC\u773C\u4E2D\u5B50\u67E5\u8BE2\u7684\u6267\u884C\u65B9\u5F0F</h4>
<p>\u5728\u6211\u8FD8\u662F\u4E00\u4E2A\u5355\u7EAF\u65E0\u77E5\u7684\u5C11\u5E74\u65F6\uFF0C\u89C9\u5F97\u5B50\u67E5\u8BE2\u7684\u6267\u884C\u65B9\u5F0F\u662F\u8FD9\u6837\u7684\uFF1A</p>
<ul>
<li>
<p>\u5982\u679C\u8BE5\u5B50\u67E5\u8BE2\u662F\u4E0D\u76F8\u5173\u5B50\u67E5\u8BE2\uFF0C\u6BD4\u5982\u4E0B\u8FB9\u8FD9\u4E2A\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE key1 IN (SELECT common_field FROM s2);
</code></pre><p>\u6211\u5E74\u5C11\u65F6\u89C9\u5F97\u8FD9\u4E2A\u67E5\u8BE2\u662F\u7684\u6267\u884C\u65B9\u5F0F\u662F\u8FD9\u6837\u7684\uFF1A</p>
<ul>
<li>
<p>\u5148\u5355\u72EC\u6267\u884C<code>(SELECT common_field FROM s2)</code>\u8FD9\u4E2A\u5B50\u67E5\u8BE2\u3002</p>
</li>
<li>
<p>\u7136\u540E\u5728\u5C06\u4E0A\u4E00\u6B65\u5B50\u67E5\u8BE2\u5F97\u5230\u7684\u7ED3\u679C\u5F53\u4F5C\u5916\u5C42\u67E5\u8BE2\u7684\u53C2\u6570\u518D\u6267\u884C\u5916\u5C42\u67E5\u8BE2<code>SELECT * FROM s1 WHERE key1 IN (...)</code>\u3002</p>
</li>
</ul>
</li>
<li>
<p>\u5982\u679C\u8BE5\u5B50\u67E5\u8BE2\u662F\u76F8\u5173\u5B50\u67E5\u8BE2\uFF0C\u6BD4\u5982\u4E0B\u8FB9\u8FD9\u4E2A\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE key1 IN (SELECT common_field FROM s2 WHERE s1.key2 = s2.key2);
</code></pre><p>\u8FD9\u4E2A\u67E5\u8BE2\u4E2D\u7684\u5B50\u67E5\u8BE2\u4E2D\u51FA\u73B0\u4E86<code>s1.key2 = s2.key2</code>\u8FD9\u6837\u7684\u6761\u4EF6\uFF0C\u610F\u5473\u7740\u8BE5\u5B50\u67E5\u8BE2\u7684\u6267\u884C\u4F9D\u8D56\u7740\u5916\u5C42\u67E5\u8BE2\u7684\u503C\uFF0C\u6240\u4EE5\u6211\u5E74\u5C11\u65F6\u89C9\u5F97\u8FD9\u4E2A\u67E5\u8BE2\u7684\u6267\u884C\u65B9\u5F0F\u662F\u8FD9\u6837\u7684\uFF1A</p>
<ul>
<li>
<p>\u5148\u4ECE\u5916\u5C42\u67E5\u8BE2\u4E2D\u83B7\u53D6\u4E00\u6761\u8BB0\u5F55\uFF0C\u672C\u4F8B\u4E2D\u4E5F\u5C31\u662F\u5148\u4ECE<code>s1</code>\u8868\u4E2D\u83B7\u53D6\u4E00\u6761\u8BB0\u5F55\u3002</p>
</li>
<li>
<p>\u7136\u540E\u4ECE\u4E0A\u4E00\u6B65\u9AA4\u4E2D\u83B7\u53D6\u7684\u90A3\u6761\u8BB0\u5F55\u4E2D\u627E\u51FA\u5B50\u67E5\u8BE2\u4E2D\u6D89\u53CA\u5230\u7684\u503C\uFF0C\u672C\u4F8B\u4E2D\u5C31\u662F\u4ECE<code>s1</code>\u8868\u4E2D\u83B7\u53D6\u7684\u90A3\u6761\u8BB0\u5F55\u4E2D\u627E\u51FA<code>s1.key2</code>\u5217\u7684\u503C\uFF0C\u7136\u540E\u6267\u884C\u5B50\u67E5\u8BE2\u3002</p>
</li>
<li>
<p>\u6700\u540E\u6839\u636E\u5B50\u67E5\u8BE2\u7684\u67E5\u8BE2\u7ED3\u679C\u6765\u68C0\u6D4B\u5916\u5C42\u67E5\u8BE2<code>WHERE</code>\u5B50\u53E5\u7684\u6761\u4EF6\u662F\u5426\u6210\u7ACB\uFF0C\u5982\u679C\u6210\u7ACB\uFF0C\u5C31\u628A\u5916\u5C42\u67E5\u8BE2\u7684\u90A3\u6761\u8BB0\u5F55\u52A0\u5165\u5230\u7ED3\u679C\u96C6\uFF0C\u5426\u5219\u5C31\u4E22\u5F03\u3002</p>
</li>
<li>
<p>\u518D\u6B21\u6267\u884C\u7B2C\u4E00\u6B65\uFF0C\u83B7\u53D6\u7B2C\u4E8C\u6761\u5916\u5C42\u67E5\u8BE2\u4E2D\u7684\u8BB0\u5F55\uFF0C\u4F9D\u6B21\u7C7B\u63A8\uFF5E</p>
</li>
</ul>
</li>
</ul>
<p>\u544A\u8BC9\u6211\u4E0D\u53EA\u662F\u6211\u4E00\u4E2A\u4EBA\u662F\u8FD9\u6837\u8BA4\u4E3A\u7684\uFF0C\u8FD9\u6837\u8BA4\u4E3A\u7684\u540C\u5B66\u8BF7\u4E3E\u8D77\u4F60\u4EEC\u7684\u53CC\u624B\uFF5E\uFF5E\uFF5E \u54C7\u5514\uFF0C\u8FD8\u771F\u4E0D\u5C11\uFF5E</p>
<p>\u5176\u5B9E\u8BBE\u8BA1<code>MySQL</code>\u7684\u5927\u53D4\u60F3\u4E86\u4E00\u7CFB\u5217\u7684\u529E\u6CD5\u6765\u4F18\u5316\u5B50\u67E5\u8BE2\u7684\u6267\u884C\uFF0C\u5927\u90E8\u5206\u60C5\u51B5\u4E0B\u8FD9\u4E9B\u4F18\u5316\u63AA\u65BD\u5176\u5B9E\u633A\u6709\u6548\u7684\uFF0C\u4F46\u662F\u4FDD\u4E0D\u9F50\u6709\u7684\u65F6\u5019\u9A6C\u5931\u524D\u8E44\uFF0C\u4E0B\u8FB9\u6211\u4EEC\u8BE6\u7EC6\u5520\u53E8\u5404\u79CD\u4E0D\u540C\u7C7B\u578B\u7684\u5B50\u67E5\u8BE2\u5177\u4F53\u662F\u600E\u4E48\u6267\u884C\u7684\u3002</p>
<blockquote><p>\u5C0F\u8D34\u58EB\uFF1A

\u6211\u4EEC\u4E0B\u8FB9\u5373\u5C06\u5520\u53E8\u7684\u5173\u4E8EMySQL\u4F18\u5316\u5B50\u67E5\u8BE2\u7684\u6267\u884C\u65B9\u5F0F\u7684\u4E8B\u513F\u90FD\u662F\u57FA\u4E8EMySQL5.7\u8FD9\u4E2A\u7248\u672C\u7684\uFF0C\u4EE5\u540E\u7248\u672C\u53EF\u80FD\u6709\u66F4\u65B0\u7684\u4F18\u5316\u7B56\u7565\uFF01
</p></blockquote><h4 data-id="heading-18">\u6807\u91CF\u5B50\u67E5\u8BE2\u3001\u884C\u5B50\u67E5\u8BE2\u7684\u6267\u884C\u65B9\u5F0F</h4>
<p>\u6211\u4EEC\u7ECF\u5E38\u5728\u4E0B\u8FB9\u4E24\u4E2A\u573A\u666F\u4E2D\u4F7F\u7528\u5230\u6807\u91CF\u5B50\u67E5\u8BE2\u6216\u8005\u884C\u5B50\u67E5\u8BE2\uFF1A</p>
<ul>
<li>
<p><code>SELECT</code>\u5B50\u53E5\u4E2D\uFF0C\u6211\u4EEC\u524D\u8FB9\u8BF4\u8FC7\u7684\u5728\u67E5\u8BE2\u5217\u8868\u4E2D\u7684\u5B50\u67E5\u8BE2\u5FC5\u987B\u662F\u6807\u91CF\u5B50\u67E5\u8BE2\u3002</p>
</li>
<li>
<p>\u5B50\u67E5\u8BE2\u4F7F\u7528<code>=</code>\u3001<code>&gt;</code>\u3001<code>&lt;</code>\u3001<code>&gt;=</code>\u3001<code>&lt;=</code>\u3001<code>&lt;&gt;</code>\u3001<code>!=</code>\u3001<code>&lt;=&gt;</code>\u7B49\u64CD\u4F5C\u7B26\u548C\u67D0\u4E2A\u64CD\u4F5C\u6570\u7EC4\u6210\u4E00\u4E2A\u5E03\u5C14\u8868\u8FBE\u5F0F\uFF0C\u8FD9\u6837\u7684\u5B50\u67E5\u8BE2\u5FC5\u987B\u662F\u6807\u91CF\u5B50\u67E5\u8BE2\u6216\u8005\u884C\u5B50\u67E5\u8BE2\u3002</p>
</li>
</ul>
<p>\u5BF9\u4E8E\u4E0A\u8FF0\u4E24\u79CD\u573A\u666F\u4E2D\u7684<span>\u4E0D\u76F8\u5173</span>\u6807\u91CF\u5B50\u67E5\u8BE2\u6216\u8005\u884C\u5B50\u67E5\u8BE2\u6765\u8BF4\uFF0C\u5B83\u4EEC\u7684\u6267\u884C\u65B9\u5F0F\u662F\u7B80\u5355\u7684\uFF0C\u6BD4\u65B9\u8BF4\u4E0B\u8FB9\u8FD9\u4E2A\u67E5\u8BE2\u8BED\u53E5\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE key1 = (SELECT common_field FROM s2 WHERE key3 = <span>'a'</span> LIMIT 1);
</code></pre><p>\u5B83\u7684\u6267\u884C\u65B9\u5F0F\u548C\u5E74\u5C11\u7684\u6211\u60F3\u7684\u4E00\u6837\uFF1A</p>
<ul>
<li>
<p>\u5148\u5355\u72EC\u6267\u884C<code>(SELECT common_field FROM s2 WHERE key3 = 'a' LIMIT 1)</code>\u8FD9\u4E2A\u5B50\u67E5\u8BE2\u3002</p>
</li>
<li>
<p>\u7136\u540E\u5728\u5C06\u4E0A\u4E00\u6B65\u5B50\u67E5\u8BE2\u5F97\u5230\u7684\u7ED3\u679C\u5F53\u4F5C\u5916\u5C42\u67E5\u8BE2\u7684\u53C2\u6570\u518D\u6267\u884C\u5916\u5C42\u67E5\u8BE2<code>SELECT * FROM s1 WHERE key1 = ...</code>\u3002</p>
</li>
</ul>
<p>\u4E5F\u5C31\u662F\u8BF4\uFF0C<span>\u5BF9\u4E8E\u5305\u542B\u4E0D\u76F8\u5173\u7684\u6807\u91CF\u5B50\u67E5\u8BE2\u6216\u8005\u884C\u5B50\u67E5\u8BE2\u7684\u67E5\u8BE2\u8BED\u53E5\u6765\u8BF4\uFF0CMySQL\u4F1A\u5206\u522B\u72EC\u7ACB\u7684\u6267\u884C\u5916\u5C42\u67E5\u8BE2\u548C\u5B50\u67E5\u8BE2\uFF0C\u5C31\u5F53\u4F5C\u4E24\u4E2A\u5355\u8868\u67E5\u8BE2\u5C31\u597D\u4E86</span>\u3002</p>
<p>\u5BF9\u4E8E<span>\u76F8\u5173</span>\u7684\u6807\u91CF\u5B50\u67E5\u8BE2\u6216\u8005\u884C\u5B50\u67E5\u8BE2\u6765\u8BF4\uFF0C\u6BD4\u5982\u4E0B\u8FB9\u8FD9\u4E2A\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 WHERE 
    key1 = (SELECT common_field FROM s2 WHERE s1.key3 = s2.key3 LIMIT 1);
</code></pre><p>\u4E8B\u60C5\u4E5F\u548C\u5E74\u5C11\u7684\u6211\u60F3\u7684\u4E00\u6837\uFF0C\u5B83\u7684\u6267\u884C\u65B9\u5F0F\u5C31\u662F\u8FD9\u6837\u7684\uFF1A</p>
<ul>
<li>
<p>\u5148\u4ECE\u5916\u5C42\u67E5\u8BE2\u4E2D\u83B7\u53D6\u4E00\u6761\u8BB0\u5F55\uFF0C\u672C\u4F8B\u4E2D\u4E5F\u5C31\u662F\u5148\u4ECE<code>s1</code>\u8868\u4E2D\u83B7\u53D6\u4E00\u6761\u8BB0\u5F55\u3002</p>
</li>
<li>
<p>\u7136\u540E\u4ECE\u4E0A\u4E00\u6B65\u9AA4\u4E2D\u83B7\u53D6\u7684\u90A3\u6761\u8BB0\u5F55\u4E2D\u627E\u51FA\u5B50\u67E5\u8BE2\u4E2D\u6D89\u53CA\u5230\u7684\u503C\uFF0C\u672C\u4F8B\u4E2D\u5C31\u662F\u4ECE<code>s1</code>\u8868\u4E2D\u83B7\u53D6\u7684\u90A3\u6761\u8BB0\u5F55\u4E2D\u627E\u51FA<code>s1.key3</code>\u5217\u7684\u503C\uFF0C\u7136\u540E\u6267\u884C\u5B50\u67E5\u8BE2\u3002</p>
</li>
<li>
<p>\u6700\u540E\u6839\u636E\u5B50\u67E5\u8BE2\u7684\u67E5\u8BE2\u7ED3\u679C\u6765\u68C0\u6D4B\u5916\u5C42\u67E5\u8BE2<code>WHERE</code>\u5B50\u53E5\u7684\u6761\u4EF6\u662F\u5426\u6210\u7ACB\uFF0C\u5982\u679C\u6210\u7ACB\uFF0C\u5C31\u628A\u5916\u5C42\u67E5\u8BE2\u7684\u90A3\u6761\u8BB0\u5F55\u52A0\u5165\u5230\u7ED3\u679C\u96C6\uFF0C\u5426\u5219\u5C31\u4E22\u5F03\u3002</p>
</li>
<li>
<p>\u518D\u6B21\u6267\u884C\u7B2C\u4E00\u6B65\uFF0C\u83B7\u53D6\u7B2C\u4E8C\u6761\u5916\u5C42\u67E5\u8BE2\u4E2D\u7684\u8BB0\u5F55\uFF0C\u4F9D\u6B21\u7C7B\u63A8\uFF5E</p>
</li>
</ul>
<p>\u4E5F\u5C31\u662F\u8BF4\u5BF9\u4E8E\u4E00\u5F00\u59CB\u5520\u53E8\u7684\u4E24\u79CD\u4F7F\u7528\u6807\u91CF\u5B50\u67E5\u8BE2\u4EE5\u53CA\u884C\u5B50\u67E5\u8BE2\u7684\u573A\u666F\u4E2D\uFF0C<code>MySQL</code>\u4F18\u5316\u5668\u7684\u6267\u884C\u65B9\u5F0F\u5E76\u6CA1\u6709\u4EC0\u4E48\u65B0\u9C9C\u7684\u3002</p>
<h4 data-id="heading-19">IN\u5B50\u67E5\u8BE2\u4F18\u5316</h4>
<h5 data-id="heading-20">\u7269\u5316\u8868\u7684\u63D0\u51FA</h5>
<p>\u5BF9\u4E8E\u4E0D\u76F8\u5173\u7684<code>IN</code>\u5B50\u67E5\u8BE2\uFF0C\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE key1 IN (SELECT common_field FROM s2 WHERE key3 = <span>'a'</span>);
</code></pre><p>\u6211\u4EEC\u6700\u5F00\u59CB\u7684\u611F\u89C9\u5C31\u662F\u8FD9\u79CD\u4E0D\u76F8\u5173\u7684<code>IN</code>\u5B50\u67E5\u8BE2\u548C\u4E0D\u76F8\u5173\u7684\u6807\u91CF\u5B50\u67E5\u8BE2\u6216\u8005\u884C\u5B50\u67E5\u8BE2\u662F\u4E00\u6837\u4E00\u6837\u7684\uFF0C\u90FD\u662F\u628A\u5916\u5C42\u67E5\u8BE2\u548C\u5B50\u67E5\u8BE2\u5F53\u4F5C\u4E24\u4E2A\u72EC\u7ACB\u7684\u5355\u8868\u67E5\u8BE2\u6765\u5BF9\u5F85\uFF0C\u53EF\u662F\u5F88\u9057\u61BE\u7684\u662F\u8BBE\u8BA1<code>MySQL</code>\u7684\u5927\u53D4\u4E3A\u4E86\u4F18\u5316<code>IN</code>\u5B50\u67E5\u8BE2\u503E\u6CE8\u4E86\u592A\u591A\u5FC3\u8840\uFF08\u6BD5\u7ADF<code>IN</code>\u5B50\u67E5\u8BE2\u662F\u6211\u4EEC\u65E5\u5E38\u751F\u6D3B\u4E2D\u6700\u5E38\u7528\u7684\u5B50\u67E5\u8BE2\u7C7B\u578B\uFF09\uFF0C\u6240\u4EE5\u6574\u4E2A\u6267\u884C\u8FC7\u7A0B\u5E76\u4E0D\u50CF\u6211\u4EEC\u60F3\u8C61\u7684\u90A3\u4E48\u7B80\u5355(&gt;_&lt;)\u3002</p>
<p>\u5176\u5B9E\u8BF4\u53E5\u8001\u5B9E\u8BDD\uFF0C\u5BF9\u4E8E\u4E0D\u76F8\u5173\u7684<code>IN</code>\u5B50\u67E5\u8BE2\u6765\u8BF4\uFF0C\u5982\u679C\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u96C6\u4E2D\u7684\u8BB0\u5F55\u6761\u6570\u5F88\u5C11\uFF0C\u90A3\u4E48\u628A\u5B50\u67E5\u8BE2\u548C\u5916\u5C42\u67E5\u8BE2\u5206\u522B\u770B\u6210\u4E24\u4E2A\u5355\u72EC\u7684\u5355\u8868\u67E5\u8BE2\u6548\u7387\u8FD8\u662F\u86EE\u9AD8\u7684\uFF0C\u4F46\u662F\u5982\u679C\u5355\u72EC\u6267\u884C\u5B50\u67E5\u8BE2\u540E\u7684\u7ED3\u679C\u96C6\u592A\u591A\u7684\u8BDD\uFF0C\u5C31\u4F1A\u5BFC\u81F4\u8FD9\u4E9B\u95EE\u9898\uFF1A</p>
<ul>
<li>
<p>\u7ED3\u679C\u96C6\u592A\u591A\uFF0C\u53EF\u80FD\u5185\u5B58\u4E2D\u90FD\u653E\u4E0D\u4E0B\uFF5E</p>
</li>
<li>
<p>\u5BF9\u4E8E\u5916\u5C42\u67E5\u8BE2\u6765\u8BF4\uFF0C\u5982\u679C\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u96C6\u592A\u591A\uFF0C\u90A3\u5C31\u610F\u5473\u7740<code>IN</code>\u5B50\u53E5\u4E2D\u7684\u53C2\u6570\u7279\u522B\u591A\uFF0C\u8FD9\u5C31\u5BFC\u81F4\uFF1A</p>
<ul>
<li>
<p>\u65E0\u6CD5\u6709\u6548\u7684\u4F7F\u7528\u7D22\u5F15\uFF0C\u53EA\u80FD\u5BF9\u5916\u5C42\u67E5\u8BE2\u8FDB\u884C\u5168\u8868\u626B\u63CF\u3002</p>
</li>
<li>
<p>\u5728\u5BF9\u5916\u5C42\u67E5\u8BE2\u6267\u884C\u5168\u8868\u626B\u63CF\u65F6\uFF0C\u7531\u4E8E<code>IN</code>\u5B50\u53E5\u4E2D\u7684\u53C2\u6570\u592A\u591A\uFF0C\u8FD9\u4F1A\u5BFC\u81F4\u68C0\u6D4B\u4E00\u6761\u8BB0\u5F55\u662F\u5426\u7B26\u5408\u548C<code>IN</code>\u5B50\u53E5\u4E2D\u7684\u53C2\u6570\u5339\u914D\u82B1\u8D39\u7684\u65F6\u95F4\u592A\u957F\u3002</p>
<p>\u6BD4\u5982\u8BF4<code>IN</code>\u5B50\u53E5\u4E2D\u7684\u53C2\u6570\u53EA\u6709\u4E24\u4E2A\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM tbl_name WHERE column IN (a, b);
</code></pre><p>\u8FD9\u6837\u76F8\u5F53\u4E8E\u9700\u8981\u5BF9<code>tbl_name</code>\u8868\u4E2D\u7684\u6BCF\u6761\u8BB0\u5F55\u5224\u65AD\u4E00\u4E0B\u5B83\u7684<code>column</code>\u5217\u662F\u5426\u7B26\u5408<code>column = a OR column = b</code>\u3002\u5728<code>IN</code>\u5B50\u53E5\u4E2D\u7684\u53C2\u6570\u6BD4\u8F83\u5C11\u65F6\u8FD9\u5E76\u4E0D\u662F\u4EC0\u4E48\u95EE\u9898\uFF0C\u5982\u679C<code>IN</code>\u5B50\u53E5\u4E2D\u7684\u53C2\u6570\u6BD4\u8F83\u591A\u65F6\uFF0C\u6BD4\u5982\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM tbl_name WHERE column IN (a, b, c ..., ...);
</code></pre><p>\u90A3\u4E48\u8FD9\u6837\u6BCF\u6761\u8BB0\u5F55\u9700\u8981\u5224\u65AD\u4E00\u4E0B\u5B83\u7684<code>column</code>\u5217\u662F\u5426\u7B26\u5408<code>column = a OR column = b OR column = c OR ...</code>\uFF0C\u8FD9\u6837\u6027\u80FD\u8017\u8D39\u53EF\u5C31\u591A\u4E86\u3002</p>
</li>
</ul>
</li>
</ul>
<p>\u4E8E\u662F\u4E4E\u8BBE\u8BA1<code>MySQL</code>\u7684\u5927\u53D4\u60F3\u4E86\u4E00\u4E2A\u62DB\uFF1A<span>\u4E0D\u76F4\u63A5\u5C06\u4E0D\u76F8\u5173\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u96C6\u5F53\u4F5C\u5916\u5C42\u67E5\u8BE2\u7684\u53C2\u6570\uFF0C\u800C\u662F\u5C06\u8BE5\u7ED3\u679C\u96C6\u5199\u5165\u4E00\u4E2A\u4E34\u65F6\u8868\u91CC</span>\u3002\u5199\u5165\u4E34\u65F6\u8868\u7684\u8FC7\u7A0B\u662F\u8FD9\u6837\u7684\uFF1A</p>
<ul>
<li>
<p>\u8BE5\u4E34\u65F6\u8868\u7684\u5217\u5C31\u662F\u5B50\u67E5\u8BE2\u7ED3\u679C\u96C6\u4E2D\u7684\u5217\u3002</p>
</li>
<li>
<p>\u5199\u5165\u4E34\u65F6\u8868\u7684\u8BB0\u5F55\u4F1A\u88AB\u53BB\u91CD\u3002</p>
<p>\u6211\u4EEC\u8BF4<code>IN</code>\u8BED\u53E5\u662F\u5224\u65AD\u67D0\u4E2A\u64CD\u4F5C\u6570\u5728\u4E0D\u5728\u67D0\u4E2A\u96C6\u5408\u4E2D\uFF0C\u96C6\u5408\u4E2D\u7684\u503C\u91CD\u4E0D\u91CD\u590D\u5BF9\u6574\u4E2A<code>IN</code>\u8BED\u53E5\u7684\u7ED3\u679C\u5E76\u6CA1\u6709\u5565\u5B50\u5173\u7CFB\uFF0C\u6240\u4EE5\u6211\u4EEC\u5728\u5C06\u7ED3\u679C\u96C6\u5199\u5165\u4E34\u65F6\u8868\u65F6\u5BF9\u8BB0\u5F55\u8FDB\u884C\u53BB\u91CD\u53EF\u4EE5\u8BA9\u4E34\u65F6\u8868\u53D8\u5F97\u66F4\u5C0F\uFF0C\u66F4\u7701\u5730\u65B9\uFF5E</p>
<blockquote><p>\u5C0F\u8D34\u58EB\uFF1A

\u4E34\u65F6\u8868\u5982\u4F55\u5BF9\u8BB0\u5F55\u8FDB\u884C\u53BB\u91CD\uFF1F\u8FD9\u4E0D\u662F\u5C0F\u610F\u601D\u561B\uFF0C\u4E34\u65F6\u8868\u4E5F\u662F\u4E2A\u8868\uFF0C\u53EA\u8981\u4E3A\u8868\u4E2D\u8BB0\u5F55\u7684\u6240\u6709\u5217\u5EFA\u7ACB\u4E3B\u952E\u6216\u8005\u552F\u4E00\u7D22\u5F15\u5C31\u597D\u4E86\u561B\uFF5E
</p></blockquote></li>
<li>
<p>\u4E00\u822C\u60C5\u51B5\u4E0B\u5B50\u67E5\u8BE2\u7ED3\u679C\u96C6\u4E0D\u4F1A\u5927\u7684\u79BB\u8C31\uFF0C\u6240\u4EE5\u4F1A\u4E3A\u5B83\u5EFA\u7ACB\u57FA\u4E8E\u5185\u5B58\u7684\u4F7F\u7528<code>Memory</code>\u5B58\u50A8\u5F15\u64CE\u7684\u4E34\u65F6\u8868\uFF0C\u800C\u4E14\u4F1A\u4E3A\u8BE5\u8868\u5EFA\u7ACB\u54C8\u5E0C\u7D22\u5F15\u3002</p>
<blockquote><p>\u5C0F\u8D34\u58EB\uFF1A

IN\u8BED\u53E5\u7684\u672C\u8D28\u5C31\u662F\u5224\u65AD\u67D0\u4E2A\u64CD\u4F5C\u6570\u5728\u4E0D\u5728\u67D0\u4E2A\u96C6\u5408\u91CC\uFF0C\u5982\u679C\u96C6\u5408\u4E2D\u7684\u6570\u636E\u5EFA\u7ACB\u4E86\u54C8\u5E0C\u7D22\u5F15\uFF0C\u90A3\u4E48\u8FD9\u4E2A\u5339\u914D\u7684\u8FC7\u7A0B\u5C31\u662F\u8D85\u7EA7\u5FEB\u7684\u3002

\u6709\u540C\u5B66\u4E0D\u77E5\u9053\u54C8\u5E0C\u7D22\u5F15\u662F\u4EC0\u4E48\uFF1F\u6211\u8FD9\u91CC\u5C31\u4E0D\u5C55\u5F00\u4E86\uFF0C\u81EA\u5DF1\u4E0A\u7F51\u627E\u627E\u5427\uFF0C\u4E0D\u4F1A\u4E86\u518D\u6765\u95EE\u6211\uFF5E
</p></blockquote><p>\u5982\u679C\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u96C6\u975E\u5E38\u5927\uFF0C\u8D85\u8FC7\u4E86\u7CFB\u7EDF\u53D8\u91CF<code>tmp_table_size</code>\u6216\u8005<code>max_heap_table_size</code>\uFF0C\u4E34\u65F6\u8868\u4F1A\u8F6C\u800C\u4F7F\u7528\u57FA\u4E8E\u78C1\u76D8\u7684\u5B58\u50A8\u5F15\u64CE\u6765\u4FDD\u5B58\u7ED3\u679C\u96C6\u4E2D\u7684\u8BB0\u5F55\uFF0C\u7D22\u5F15\u7C7B\u578B\u4E5F\u5BF9\u5E94\u8F6C\u53D8\u4E3A<code>B+</code>\u6811\u7D22\u5F15\u3002</p>
</li>
</ul>
<p>\u8BBE\u8BA1<code>MySQL</code>\u7684\u5927\u53D4\u628A\u8FD9\u4E2A\u5C06\u5B50\u67E5\u8BE2\u7ED3\u679C\u96C6\u4E2D\u7684\u8BB0\u5F55\u4FDD\u5B58\u5230\u4E34\u65F6\u8868\u7684\u8FC7\u7A0B\u79F0\u4E4B\u4E3A<code>\u7269\u5316</code>\uFF08\u82F1\u6587\u540D\uFF1A<code>Materialize</code>\uFF09\u3002\u4E3A\u4E86\u65B9\u4FBF\u8D77\u89C1\uFF0C\u6211\u4EEC\u5C31\u628A\u90A3\u4E2A\u5B58\u50A8\u5B50\u67E5\u8BE2\u7ED3\u679C\u96C6\u7684\u4E34\u65F6\u8868\u79F0\u4E4B\u4E3A<code>\u7269\u5316\u8868</code>\u3002\u6B63\u56E0\u4E3A\u7269\u5316\u8868\u4E2D\u7684\u8BB0\u5F55\u90FD\u5EFA\u7ACB\u4E86\u7D22\u5F15\uFF08\u57FA\u4E8E\u5185\u5B58\u7684\u7269\u5316\u8868\u6709\u54C8\u5E0C\u7D22\u5F15\uFF0C\u57FA\u4E8E\u78C1\u76D8\u7684\u6709B+\u6811\u7D22\u5F15\uFF09\uFF0C\u901A\u8FC7\u7D22\u5F15\u6267\u884C<code>IN</code>\u8BED\u53E5\u5224\u65AD\u67D0\u4E2A\u64CD\u4F5C\u6570\u5728\u4E0D\u5728\u5B50\u67E5\u8BE2\u7ED3\u679C\u96C6\u4E2D\u53D8\u5F97\u975E\u5E38\u5FEB\uFF0C\u4ECE\u800C\u63D0\u5347\u4E86\u5B50\u67E5\u8BE2\u8BED\u53E5\u7684\u6027\u80FD\u3002</p>
<h5 data-id="heading-21">\u7269\u5316\u8868\u8F6C\u8FDE\u63A5</h5>
<p>\u4E8B\u60C5\u5230\u8FD9\u5C31\u5B8C\u4E86\uFF1F\u6211\u4EEC\u8FD8\u5F97\u91CD\u65B0\u5BA1\u89C6\u4E00\u4E0B\u6700\u5F00\u59CB\u7684\u90A3\u4E2A\u67E5\u8BE2\u8BED\u53E5\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE key1 IN (SELECT common_field FROM s2 WHERE key3 = <span>'a'</span>);
</code></pre><p>\u5F53\u6211\u4EEC\u628A\u5B50\u67E5\u8BE2\u8FDB\u884C\u7269\u5316\u4E4B\u540E\uFF0C\u5047\u8BBE\u5B50\u67E5\u8BE2\u7269\u5316\u8868\u7684\u540D\u79F0\u4E3A<code>materialized_table</code>\uFF0C\u8BE5\u7269\u5316\u8868\u5B58\u50A8\u7684\u5B50\u67E5\u8BE2\u7ED3\u679C\u96C6\u7684\u5217\u4E3A<code>m_val</code>\uFF0C\u90A3\u4E48\u8FD9\u4E2A\u67E5\u8BE2\u5176\u5B9E\u53EF\u4EE5\u4ECE\u4E0B\u8FB9\u4E24\u79CD\u89D2\u5EA6\u6765\u770B\u5F85\uFF1A</p>
<ul>
<li>
<p>\u4ECE\u8868<code>s1</code>\u7684\u89D2\u5EA6\u6765\u770B\u5F85\uFF0C\u6574\u4E2A\u67E5\u8BE2\u7684\u610F\u601D\u5176\u5B9E\u662F\uFF1A\u5BF9\u4E8E<code>s1</code>\u8868\u4E2D\u7684\u6BCF\u6761\u8BB0\u5F55\u6765\u8BF4\uFF0C\u5982\u679C\u8BE5\u8BB0\u5F55\u7684<code>key1</code>\u5217\u7684\u503C\u5728\u5B50\u67E5\u8BE2\u5BF9\u5E94\u7684\u7269\u5316\u8868\u4E2D\uFF0C\u5219\u8BE5\u8BB0\u5F55\u4F1A\u88AB\u52A0\u5165\u6700\u7EC8\u7684\u7ED3\u679C\u96C6\u3002\u753B\u4E2A\u56FE\u8868\u793A\u4E00\u4E0B\u5C31\u662F\u8FD9\u6837\uFF1A</p>
<p></p><figure><img alt="image_1cvfj9up26i518t91li5ooq1r0u2d.png-84.9kB" src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2019/5/6/16a8dda5369e68c5~tplv-t2oaga2asx-watermark.awebp" loading="lazy" class="medium-zoom-image"><figcaption></figcaption></figure><p></p>
</li>
<li>
<p>\u4ECE\u5B50\u67E5\u8BE2\u7269\u5316\u8868\u7684\u89D2\u5EA6\u6765\u770B\u5F85\uFF0C\u6574\u4E2A\u67E5\u8BE2\u7684\u610F\u601D\u5176\u5B9E\u662F\uFF1A\u5BF9\u4E8E\u5B50\u67E5\u8BE2\u7269\u5316\u8868\u7684\u6BCF\u4E2A\u503C\u6765\u8BF4\uFF0C\u5982\u679C\u80FD\u5728<code>s1</code>\u8868\u4E2D\u627E\u5230\u5BF9\u5E94\u7684<code>key1</code>\u5217\u7684\u503C\u4E0E\u8BE5\u503C\u76F8\u7B49\u7684\u8BB0\u5F55\uFF0C\u90A3\u4E48\u5C31\u628A\u8FD9\u4E9B\u8BB0\u5F55\u52A0\u5165\u5230\u6700\u7EC8\u7684\u7ED3\u679C\u96C6\u3002\u753B\u4E2A\u56FE\u8868\u793A\u4E00\u4E0B\u5C31\u662F\u8FD9\u6837\uFF1A</p>
<p></p><figure><img alt="image_1cvfjg3os1oh1e3o5c11dhd1odd2q.png-67.4kB" src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2019/5/6/16a8dda5394d3903~tplv-t2oaga2asx-watermark.awebp" loading="lazy" class="medium-zoom-image"><figcaption></figcaption></figure><p></p>
</li>
</ul>
<p>\u4E5F\u5C31\u662F\u8BF4\u5176\u5B9E\u4E0A\u8FB9\u7684\u67E5\u8BE2\u5C31\u76F8\u5F53\u4E8E\u8868<code>s1</code>\u548C\u5B50\u67E5\u8BE2\u7269\u5316\u8868<code>materialized_table</code>\u8FDB\u884C\u5185\u8FDE\u63A5\uFF1A</p>
<pre><code lang="bash" class="">SELECT s1.* FROM s1 INNER JOIN materialized_table ON key1 = m_val;
</code></pre><p>\u8F6C\u5316\u6210\u5185\u8FDE\u63A5\u4E4B\u540E\u5C31\u6709\u610F\u601D\u4E86\uFF0C\u67E5\u8BE2\u4F18\u5316\u5668\u53EF\u4EE5\u8BC4\u4F30\u4E0D\u540C\u8FDE\u63A5\u987A\u5E8F\u9700\u8981\u7684\u6210\u672C\u662F\u591A\u5C11\uFF0C\u9009\u53D6\u6210\u672C\u6700\u4F4E\u7684\u90A3\u79CD\u67E5\u8BE2\u65B9\u5F0F\u6267\u884C\u67E5\u8BE2\u3002\u6211\u4EEC\u5206\u6790\u4E00\u4E0B\u4E0A\u8FF0\u67E5\u8BE2\u4E2D\u4F7F\u7528\u5916\u5C42\u67E5\u8BE2\u7684\u8868<code>s1</code>\u548C\u7269\u5316\u8868<code>materialized_table</code>\u8FDB\u884C\u5185\u8FDE\u63A5\u7684\u6210\u672C\u90FD\u662F\u7531\u54EA\u51E0\u90E8\u5206\u7EC4\u6210\u7684\uFF1A</p>
<ul>
<li>
<p>\u5982\u679C\u4F7F\u7528<code>s1</code>\u8868\u4F5C\u4E3A\u9A71\u52A8\u8868\u7684\u8BDD\uFF0C\u603B\u67E5\u8BE2\u6210\u672C\u7531\u4E0B\u8FB9\u51E0\u4E2A\u90E8\u5206\u7EC4\u6210\uFF1A</p>
<ul>
<li>
<p>\u7269\u5316\u5B50\u67E5\u8BE2\u65F6\u9700\u8981\u7684\u6210\u672C</p>
</li>
<li>
<p>\u626B\u63CF<code>s1</code>\u8868\u65F6\u7684\u6210\u672C</p>
</li>
<li>
<p>s1\u8868\u4E2D\u7684\u8BB0\u5F55\u6570\u91CF \xD7 \u901A\u8FC7<code>m_val = xxx</code>\u5BF9<code>materialized_table</code>\u8868\u8FDB\u884C\u5355\u8868\u8BBF\u95EE\u7684\u6210\u672C\uFF08\u6211\u4EEC\u524D\u8FB9\u8BF4\u8FC7\u7269\u5316\u8868\u4E2D\u7684\u8BB0\u5F55\u662F\u4E0D\u91CD\u590D\u7684\uFF0C\u5E76\u4E14\u4E3A\u7269\u5316\u8868\u4E2D\u7684\u5217\u5EFA\u7ACB\u4E86\u7D22\u5F15\uFF0C\u6240\u4EE5\u8FD9\u4E2A\u6B65\u9AA4\u663E\u7136\u662F\u975E\u5E38\u5FEB\u7684\uFF09\u3002</p>
</li>
</ul>
</li>
<li>
<p>\u5982\u679C\u4F7F\u7528<code>materialized_table</code>\u8868\u4F5C\u4E3A\u9A71\u52A8\u8868\u7684\u8BDD\uFF0C\u603B\u67E5\u8BE2\u6210\u672C\u7531\u4E0B\u8FB9\u51E0\u4E2A\u90E8\u5206\u7EC4\u6210\uFF1A</p>
<ul>
<li>
<p>\u7269\u5316\u5B50\u67E5\u8BE2\u65F6\u9700\u8981\u7684\u6210\u672C</p>
</li>
<li>
<p>\u626B\u63CF\u7269\u5316\u8868\u65F6\u7684\u6210\u672C</p>
</li>
<li>
<p>\u7269\u5316\u8868\u4E2D\u7684\u8BB0\u5F55\u6570\u91CF \xD7 \u901A\u8FC7<code>key1 = xxx</code>\u5BF9<code>s1</code>\u8868\u8FDB\u884C\u5355\u8868\u8BBF\u95EE\u7684\u6210\u672C\uFF08\u975E\u5E38\u5E86\u5E78<code>key1</code>\u5217\u4E0A\u5EFA\u7ACB\u4E86\u7D22\u5F15\uFF0C\u6240\u4EE5\u8FD9\u4E2A\u6B65\u9AA4\u662F\u975E\u5E38\u5FEB\u7684\uFF09\u3002</p>
</li>
</ul>
</li>
</ul>
<p><code>MySQL</code>\u67E5\u8BE2\u4F18\u5316\u5668\u4F1A\u901A\u8FC7\u8FD0\u7B97\u6765\u9009\u62E9\u4E0A\u8FF0\u6210\u672C\u66F4\u4F4E\u7684\u65B9\u6848\u6765\u6267\u884C\u67E5\u8BE2\u3002</p>
<h5 data-id="heading-22">\u5C06\u5B50\u67E5\u8BE2\u8F6C\u6362\u4E3Asemi-join</h5>
<p>\u867D\u7136\u5C06\u5B50\u67E5\u8BE2\u8FDB\u884C\u7269\u5316\u4E4B\u540E\u518D\u6267\u884C\u67E5\u8BE2\u90FD\u4F1A\u6709\u5EFA\u7ACB\u4E34\u65F6\u8868\u7684\u6210\u672C\uFF0C\u4F46\u662F\u4E0D\u7BA1\u600E\u4E48\u8BF4\uFF0C\u6211\u4EEC\u89C1\u8BC6\u5230\u4E86\u5C06\u5B50\u67E5\u8BE2\u8F6C\u6362\u4E3A\u8FDE\u63A5\u7684\u5F3A\u5927\u4F5C\u7528\uFF0C\u8BBE\u8BA1<code>MySQL</code>\u7684\u5927\u53D4\u7EE7\u7EED\u5F00\u8111\u6D1E\uFF1A\u80FD\u4E0D\u80FD\u4E0D\u8FDB\u884C\u7269\u5316\u64CD\u4F5C\u76F4\u63A5\u628A\u5B50\u67E5\u8BE2\u8F6C\u6362\u4E3A\u8FDE\u63A5\u5462\uFF1F\u8BA9\u6211\u4EEC\u91CD\u65B0\u5BA1\u89C6\u4E00\u4E0B\u4E0A\u8FB9\u7684\u67E5\u8BE2\u8BED\u53E5\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE key1 IN (SELECT common_field FROM s2 WHERE key3 = <span>'a'</span>);
</code></pre><p>\u6211\u4EEC\u53EF\u4EE5\u628A\u8FD9\u4E2A\u67E5\u8BE2\u7406\u89E3\u6210\uFF1A\u5BF9\u4E8E<code>s1</code>\u8868\u4E2D\u7684\u67D0\u6761\u8BB0\u5F55\uFF0C\u5982\u679C\u6211\u4EEC\u80FD\u5728<code>s2</code>\u8868\uFF08\u51C6\u786E\u7684\u8BF4\u662F\u6267\u884C\u5B8C<code>WHERE s2.key3 = 'a'</code>\u4E4B\u540E\u7684\u7ED3\u679C\u96C6\uFF09\u4E2D\u627E\u5230\u4E00\u6761\u6216\u591A\u6761\u8BB0\u5F55\uFF0C\u8FD9\u4E9B\u8BB0\u5F55\u7684<code>common_field</code>\u7684\u503C\u7B49\u4E8E<code>s1</code>\u8868\u8BB0\u5F55\u7684<code>key1</code>\u5217\u7684\u503C\uFF0C\u90A3\u4E48\u8BE5\u6761<code>s1</code>\u8868\u7684\u8BB0\u5F55\u5C31\u4F1A\u88AB\u52A0\u5165\u5230\u6700\u7EC8\u7684\u7ED3\u679C\u96C6\u3002\u8FD9\u4E2A\u8FC7\u7A0B\u5176\u5B9E\u548C\u628A<code>s1</code>\u548C<code>s2</code>\u4E24\u4E2A\u8868\u8FDE\u63A5\u8D77\u6765\u7684\u6548\u679C\u5F88\u50CF\uFF1A</p>
<pre><code lang="bash" class="">SELECT s1.* FROM s1 INNER JOIN s2 
    ON s1.key1 = s2.common_field 
    WHERE s2.key3 = <span>'a'</span>;
</code></pre><p>\u53EA\u4E0D\u8FC7\u6211\u4EEC\u4E0D\u80FD\u4FDD\u8BC1\u5BF9\u4E8E<code>s1</code>\u8868\u7684\u67D0\u6761\u8BB0\u5F55\u6765\u8BF4\uFF0C\u5728<code>s2</code>\u8868\uFF08\u51C6\u786E\u7684\u8BF4\u662F\u6267\u884C\u5B8C<code>WHERE s2.key3 = 'a'</code>\u4E4B\u540E\u7684\u7ED3\u679C\u96C6\uFF09\u4E2D\u6709\u591A\u5C11\u6761\u8BB0\u5F55\u6EE1\u8DB3<code>s1.key1 = s2.common_field</code>\u8FD9\u4E2A\u6761\u4EF6\uFF0C\u4E0D\u8FC7\u6211\u4EEC\u53EF\u4EE5\u5206\u4E09\u79CD\u60C5\u51B5\u8BA8\u8BBA\uFF1A</p>
<ul>
<li>
<p>\u60C5\u51B5\u4E00\uFF1A\u5BF9\u4E8E<code>s1</code>\u8868\u7684\u67D0\u6761\u8BB0\u5F55\u6765\u8BF4\uFF0C<code>s2</code>\u8868\u4E2D<span>\u6CA1\u6709</span>\u4EFB\u4F55\u8BB0\u5F55\u6EE1\u8DB3<code>s1.key1 = s2.common_field</code>\u8FD9\u4E2A\u6761\u4EF6\uFF0C\u90A3\u4E48\u8BE5\u8BB0\u5F55\u81EA\u7136\u4E5F\u4E0D\u4F1A\u52A0\u5165\u5230\u6700\u540E\u7684\u7ED3\u679C\u96C6\u3002</p>
</li>
<li>
<p>\u60C5\u51B5\u4E8C\uFF1A\u5BF9\u4E8E<code>s1</code>\u8868\u7684\u67D0\u6761\u8BB0\u5F55\u6765\u8BF4\uFF0C<code>s2</code>\u8868\u4E2D<span>\u6709\u4E14\u53EA\u6709</span>1\u6761\u8BB0\u5F55\u6EE1\u8DB3<code>s1.key1 = s2.common_field</code>\u8FD9\u4E2A\u6761\u4EF6\uFF0C\u90A3\u4E48\u8BE5\u8BB0\u5F55\u4F1A\u88AB\u52A0\u5165\u6700\u7EC8\u7684\u7ED3\u679C\u96C6\u3002</p>
</li>
<li>
<p>\u60C5\u51B5\u4E09\uFF1A\u5BF9\u4E8E<code>s1</code>\u8868\u7684\u67D0\u6761\u8BB0\u5F55\u6765\u8BF4\uFF0C<code>s2</code>\u8868\u4E2D<span>\u81F3\u5C11\u67092\u6761</span>\u8BB0\u5F55\u6EE1\u8DB3<code>s1.key1 = s2.common_field</code>\u8FD9\u4E2A\u6761\u4EF6\uFF0C\u90A3\u4E48\u8BE5\u8BB0\u5F55\u4F1A\u88AB<span>\u591A\u6B21</span>\u52A0\u5165\u6700\u7EC8\u7684\u7ED3\u679C\u96C6\u3002</p>
</li>
</ul>
<p>\u5BF9\u4E8E<code>s1</code>\u8868\u7684\u67D0\u6761\u8BB0\u5F55\u6765\u8BF4\uFF0C\u7531\u4E8E\u6211\u4EEC\u53EA\u5173\u5FC3<code>s2</code>\u8868\u4E2D<span>\u662F\u5426\u5B58\u5728</span>\u8BB0\u5F55\u6EE1\u8DB3<code>s1.key1 = s2.common_field</code>\u8FD9\u4E2A\u6761\u4EF6\uFF0C\u800C<span>\u4E0D\u5173\u5FC3\u5177\u4F53\u6709\u591A\u5C11\u6761\u8BB0\u5F55\u4E0E\u4E4B\u5339\u914D</span>\uFF0C\u53C8\u56E0\u4E3A\u6709<code>\u60C5\u51B5\u4E09</code>\u7684\u5B58\u5728\uFF0C\u6211\u4EEC\u4E0A\u8FB9\u6240\u8BF4\u7684<code>IN</code>\u5B50\u67E5\u8BE2\u548C\u4E24\u8868\u8FDE\u63A5\u4E4B\u95F4\u5E76\u4E0D\u5B8C\u5168\u7B49\u4EF7\u3002\u4F46\u662F\u5C06\u5B50\u67E5\u8BE2\u8F6C\u6362\u4E3A\u8FDE\u63A5\u53C8\u771F\u7684\u53EF\u4EE5\u5145\u5206\u53D1\u6325\u4F18\u5316\u5668\u7684\u4F5C\u7528\uFF0C\u6240\u4EE5\u8BBE\u8BA1<code>MySQL</code>\u7684\u5927\u53D4\u5728\u8FD9\u91CC\u63D0\u51FA\u4E86\u4E00\u4E2A\u65B0\u6982\u5FF5 --- <code>\u534A\u8FDE\u63A5</code>\uFF08\u82F1\u6587\u540D\uFF1A<code>semi-join</code>\uFF09\u3002\u5C06<code>s1</code>\u8868\u548C<code>s2</code>\u8868\u8FDB\u884C\u534A\u8FDE\u63A5\u7684\u610F\u601D\u5C31\u662F\uFF1A<span>\u5BF9\u4E8E<code>s1</code>\u8868\u7684\u67D0\u6761\u8BB0\u5F55\u6765\u8BF4\uFF0C\u6211\u4EEC\u53EA\u5173\u5FC3\u5728<code>s2</code>\u8868\u4E2D\u662F\u5426\u5B58\u5728\u4E0E\u4E4B\u5339\u914D\u7684\u8BB0\u5F55\uFF0C\u800C\u4E0D\u5173\u5FC3\u5177\u4F53\u6709\u591A\u5C11\u6761\u8BB0\u5F55\u4E0E\u4E4B\u5339\u914D\uFF0C\u6700\u7EC8\u7684\u7ED3\u679C\u96C6\u4E2D\u53EA\u4FDD\u7559<code>s1</code>\u8868\u7684\u8BB0\u5F55</span>\u3002\u4E3A\u4E86\u8BA9\u5927\u5BB6\u6709\u66F4\u76F4\u89C2\u7684\u611F\u53D7\uFF0C\u6211\u4EEC\u5047\u8BBEMySQL\u5185\u90E8\u662F\u8FD9\u4E48\u6539\u5199\u4E0A\u8FB9\u7684\u5B50\u67E5\u8BE2\u7684\uFF1A</p>
<pre><code lang="bash" class="">SELECT s1.* FROM s1 SEMI JOIN s2
    ON s1.key1 = s2.common_field
    WHERE key3 = <span>'a'</span>;
</code></pre><blockquote><p>\u5C0F\u8D34\u58EB\uFF1A

semi-join\u53EA\u662F\u5728MySQL\u5185\u90E8\u91C7\u7528\u7684\u4E00\u79CD\u6267\u884C\u5B50\u67E5\u8BE2\u7684\u65B9\u5F0F\uFF0CMySQL\u5E76\u6CA1\u6709\u63D0\u4F9B\u9762\u5411\u7528\u6237\u7684semi-join\u8BED\u6CD5\uFF0C\u6240\u4EE5\u6211\u4EEC\u4E0D\u9700\u8981\uFF0C\u4E5F\u4E0D\u80FD\u5C1D\u8BD5\u628A\u4E0A\u8FB9\u8FD9\u4E2A\u8BED\u53E5\u653E\u5230\u9ED1\u6846\u6846\u91CC\u8FD0\u884C\uFF0C\u6211\u53EA\u662F\u60F3\u8BF4\u660E\u4E00\u4E0B\u4E0A\u8FB9\u7684\u5B50\u67E5\u8BE2\u5728MySQL\u5185\u90E8\u4F1A\u88AB\u8F6C\u6362\u4E3A\u7C7B\u4F3C\u4E0A\u8FB9\u8BED\u53E5\u7684\u534A\u8FDE\u63A5\uFF5E
</p></blockquote><p>\u6982\u5FF5\u662F\u6709\u4E86\uFF0C\u600E\u4E48\u5B9E\u73B0\u8FD9\u79CD\u6240\u8C13\u7684<code>\u534A\u8FDE\u63A5</code>\u5462\uFF1F\u8BBE\u8BA1<code>MySQL</code>\u7684\u5927\u53D4\u51C6\u5907\u4E86\u597D\u51E0\u79CD\u529E\u6CD5\u3002</p>
<ul>
<li>
<p>Table pullout \uFF08\u5B50\u67E5\u8BE2\u4E2D\u7684\u8868\u4E0A\u62C9\uFF09</p>
<p>\u5F53<span>\u5B50\u67E5\u8BE2\u7684\u67E5\u8BE2\u5217\u8868\u5904\u53EA\u6709\u4E3B\u952E\u6216\u8005\u552F\u4E00\u7D22\u5F15\u5217</span>\u65F6\uFF0C\u53EF\u4EE5\u76F4\u63A5\u628A\u5B50\u67E5\u8BE2\u4E2D\u7684\u8868<code>\u4E0A\u62C9</code>\u5230\u5916\u5C42\u67E5\u8BE2\u7684<code>FROM</code>\u5B50\u53E5\u4E2D\uFF0C\u5E76\u628A\u5B50\u67E5\u8BE2\u4E2D\u7684\u641C\u7D22\u6761\u4EF6\u5408\u5E76\u5230\u5916\u5C42\u67E5\u8BE2\u7684\u641C\u7D22\u6761\u4EF6\u4E2D\uFF0C\u6BD4\u5982\u8FD9\u4E2A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE key2 IN (SELECT key2 FROM s2 WHERE key3 = <span>'a'</span>);
</code></pre><p>\u7531\u4E8E<code>key2</code>\u5217\u662F<code>s2</code>\u8868\u7684\u552F\u4E00\u4E8C\u7EA7\u7D22\u5F15\u5217\uFF0C\u6240\u4EE5\u6211\u4EEC\u53EF\u4EE5\u76F4\u63A5\u628A<code>s2</code>\u8868\u4E0A\u62C9\u5230\u5916\u5C42\u67E5\u8BE2\u7684<code>FROM</code>\u5B50\u53E5\u4E2D\uFF0C\u5E76\u4E14\u628A\u5B50\u67E5\u8BE2\u4E2D\u7684\u641C\u7D22\u6761\u4EF6\u5408\u5E76\u5230\u5916\u5C42\u67E5\u8BE2\u7684\u641C\u7D22\u6761\u4EF6\u4E2D\uFF0C\u4E0A\u62C9\u4E4B\u540E\u7684\u67E5\u8BE2\u5C31\u662F\u8FD9\u6837\u7684\uFF1A</p>
<pre><code lang="bash" class="">SELECT s1.* FROM s1 INNER JOIN s2 
    ON s1.key2 = s2.key2 
    WHERE s2.key3 = <span>'a'</span>;
</code></pre><p>\u4E3A\u5565\u5F53\u5B50\u67E5\u8BE2\u7684\u67E5\u8BE2\u5217\u8868\u5904\u53EA\u6709\u4E3B\u952E\u6216\u8005\u552F\u4E00\u7D22\u5F15\u5217\u65F6\uFF0C\u5C31\u53EF\u4EE5\u76F4\u63A5\u5C06\u5B50\u67E5\u8BE2\u8F6C\u6362\u4E3A\u8FDE\u63A5\u67E5\u8BE2\u5462\uFF1F\u54CE\u5440\uFF0C\u4E3B\u952E\u6216\u8005\u552F\u4E00\u7D22\u5F15\u5217\u4E2D\u7684\u6570\u636E\u672C\u8EAB\u5C31\u662F\u4E0D\u91CD\u590D\u7684\u561B\uFF01\u6240\u4EE5\u5BF9\u4E8E\u540C\u4E00\u6761<code>s1</code>\u8868\u4E2D\u7684\u8BB0\u5F55\uFF0C\u4F60\u4E0D\u53EF\u80FD\u627E\u5230\u4E24\u6761\u4EE5\u4E0A\u7684\u7B26\u5408<code>s1.key2 = s2.key2</code>\u7684\u8BB0\u5F55\u5440\uFF5E</p>
</li>
<li>
<p>DuplicateWeedout execution strategy \uFF08\u91CD\u590D\u503C\u6D88\u9664\uFF09</p>
<p>\u5BF9\u4E8E\u8FD9\u4E2A\u67E5\u8BE2\u6765\u8BF4\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE key1 IN (SELECT common_field FROM s2 WHERE key3 = <span>'a'</span>);
</code></pre><p>\u8F6C\u6362\u4E3A\u534A\u8FDE\u63A5\u67E5\u8BE2\u540E\uFF0C<code>s1</code>\u8868\u4E2D\u7684\u67D0\u6761\u8BB0\u5F55\u53EF\u80FD\u5728<code>s2</code>\u8868\u4E2D\u6709\u591A\u6761\u5339\u914D\u7684\u8BB0\u5F55\uFF0C\u6240\u4EE5\u8BE5\u6761\u8BB0\u5F55\u53EF\u80FD\u591A\u6B21\u88AB\u6DFB\u52A0\u5230\u6700\u540E\u7684\u7ED3\u679C\u96C6\u4E2D\uFF0C\u4E3A\u4E86\u6D88\u9664\u91CD\u590D\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5EFA\u7ACB\u4E00\u4E2A\u4E34\u65F6\u8868\uFF0C\u6BD4\u65B9\u8BF4\u8FD9\u4E2A\u4E34\u65F6\u8868\u957F\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">CREATE TABLE tmp (
    id PRIMARY KEY
);
</code></pre><p>\u8FD9\u6837\u5728\u6267\u884C\u8FDE\u63A5\u67E5\u8BE2\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u6BCF\u5F53\u67D0\u6761<code>s1</code>\u8868\u4E2D\u7684\u8BB0\u5F55\u8981\u52A0\u5165\u7ED3\u679C\u96C6\u65F6\uFF0C\u5C31\u9996\u5148\u628A\u8FD9\u6761\u8BB0\u5F55\u7684<code>id</code>\u503C\u52A0\u5165\u5230\u8FD9\u4E2A\u4E34\u65F6\u8868\u91CC\uFF0C\u5982\u679C\u6DFB\u52A0\u6210\u529F\uFF0C\u8BF4\u660E\u4E4B\u524D\u8FD9\u6761<code>s1</code>\u8868\u4E2D\u7684\u8BB0\u5F55\u5E76\u6CA1\u6709\u52A0\u5165\u6700\u7EC8\u7684\u7ED3\u679C\u96C6\uFF0C\u73B0\u5728\u628A\u8BE5\u8BB0\u5F55\u6DFB\u52A0\u5230\u6700\u7EC8\u7684\u7ED3\u679C\u96C6\uFF1B\u5982\u679C\u6DFB\u52A0\u5931\u8D25\uFF0C\u8BF4\u660E\u4E4B\u524D\u8FD9\u6761<code>s1</code>\u8868\u4E2D\u7684\u8BB0\u5F55\u5DF2\u7ECF\u52A0\u5165\u8FC7\u6700\u7EC8\u7684\u7ED3\u679C\u96C6\uFF0C\u8FD9\u91CC\u76F4\u63A5\u628A\u5B83\u4E22\u5F03\u5C31\u597D\u4E86\uFF0C\u8FD9\u79CD\u4F7F\u7528\u4E34\u65F6\u8868\u6D88\u9664<code>semi-join</code>\u7ED3\u679C\u96C6\u4E2D\u7684\u91CD\u590D\u503C\u7684\u65B9\u5F0F\u79F0\u4E4B\u4E3A<code>DuplicateWeedout</code>\u3002</p>
</li>
<li>
<p>LooseScan execution strategy \uFF08\u677E\u6563\u626B\u63CF\uFF09</p>
<p>\u5927\u5BB6\u770B\u8FD9\u4E2A\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE key3 IN (SELECT key1 FROM s2 WHERE key1 &gt; <span>'a'</span> AND key1 &lt; <span>'b'</span>);
</code></pre><p>\u5728\u5B50\u67E5\u8BE2\u4E2D\uFF0C\u5BF9\u4E8E<code>s2</code>\u8868\u7684\u8BBF\u95EE\u53EF\u4EE5\u4F7F\u7528\u5230<code>key1</code>\u5217\u7684\u7D22\u5F15\uFF0C\u800C\u6070\u597D\u5B50\u67E5\u8BE2\u7684\u67E5\u8BE2\u5217\u8868\u5904\u5C31\u662F<code>key1</code>\u5217\uFF0C\u8FD9\u6837\u5728\u5C06\u8BE5\u67E5\u8BE2\u8F6C\u6362\u4E3A\u534A\u8FDE\u63A5\u67E5\u8BE2\u540E\uFF0C\u5982\u679C\u5C06<code>s2</code>\u4F5C\u4E3A\u9A71\u52A8\u8868\u6267\u884C\u67E5\u8BE2\u7684\u8BDD\uFF0C\u90A3\u4E48\u6267\u884C\u8FC7\u7A0B\u5C31\u662F\u8FD9\u6837\uFF1A</p>
<p></p><figure><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2019/12/3/16ecb503727bb15e~tplv-t2oaga2asx-watermark.awebp" loading="lazy" class="medium-zoom-image"><figcaption></figcaption></figure><p></p>
<p>\u5982\u56FE\u6240\u793A\uFF0C\u5728<code>s2</code>\u8868\u7684<code>idx_key1</code>\u7D22\u5F15\u4E2D\uFF0C\u503C\u4E3A<code>'aa'</code>\u7684\u4E8C\u7EA7\u7D22\u5F15\u8BB0\u5F55\u4E00\u5171\u67093\u6761\uFF0C\u90A3\u4E48\u53EA\u9700\u8981\u53D6\u7B2C\u4E00\u6761\u7684\u503C\u5230<code>s1</code>\u8868\u4E2D\u67E5\u627E<code>s1.key3 = 'aa'</code>\u7684\u8BB0\u5F55\uFF0C\u5982\u679C\u80FD\u5728<code>s1</code>\u8868\u4E2D\u627E\u5230\u5BF9\u5E94\u7684\u8BB0\u5F55\uFF0C\u90A3\u4E48\u5C31\u628A\u5BF9\u5E94\u7684\u8BB0\u5F55\u52A0\u5165\u5230\u7ED3\u679C\u96C6\u3002\u4F9D\u6B64\u7C7B\u63A8\uFF0C\u5176\u4ED6\u503C\u76F8\u540C\u7684\u4E8C\u7EA7\u7D22\u5F15\u8BB0\u5F55\uFF0C\u4E5F\u53EA\u9700\u8981\u53D6\u7B2C\u4E00\u6761\u8BB0\u5F55\u7684\u503C\u5230<code>s1</code>\u8868\u4E2D\u627E\u5339\u914D\u7684\u8BB0\u5F55\uFF0C\u8FD9\u79CD\u867D\u7136\u662F\u626B\u63CF\u7D22\u5F15\uFF0C\u4F46\u53EA\u53D6\u503C\u76F8\u540C\u7684\u8BB0\u5F55\u7684\u7B2C\u4E00\u6761\u53BB\u505A\u5339\u914D\u64CD\u4F5C\u7684\u65B9\u5F0F\u79F0\u4E4B\u4E3A<code>\u677E\u6563\u626B\u63CF</code>\u3002</p>
</li>
<li>
<p>Semi-join Materialization execution strategy</p>
<p>\u6211\u4EEC\u4E4B\u524D\u4ECB\u7ECD\u7684\u5148\u628A\u5916\u5C42\u67E5\u8BE2\u7684<code>IN</code>\u5B50\u53E5\u4E2D\u7684\u4E0D\u76F8\u5173\u5B50\u67E5\u8BE2\u8FDB\u884C\u7269\u5316\uFF0C\u7136\u540E\u518D\u8FDB\u884C\u5916\u5C42\u67E5\u8BE2\u7684\u8868\u548C\u7269\u5316\u8868\u7684\u8FDE\u63A5\u672C\u8D28\u4E0A\u4E5F\u7B97\u662F\u4E00\u79CD<code>semi-join</code>\uFF0C\u53EA\u4E0D\u8FC7\u7531\u4E8E\u7269\u5316\u8868\u4E2D\u6CA1\u6709\u91CD\u590D\u7684\u8BB0\u5F55\uFF0C\u6240\u4EE5\u53EF\u4EE5\u76F4\u63A5\u5C06\u5B50\u67E5\u8BE2\u8F6C\u4E3A\u8FDE\u63A5\u67E5\u8BE2\u3002</p>
</li>
<li>
<p>FirstMatch execution strategy \uFF08\u9996\u6B21\u5339\u914D\uFF09</p>
<p><code>FirstMatch</code>\u662F\u4E00\u79CD\u6700\u539F\u59CB\u7684\u534A\u8FDE\u63A5\u6267\u884C\u65B9\u5F0F\uFF0C\u8DDF\u6211\u4EEC\u5E74\u5C11\u65F6\u8BA4\u4E3A\u7684\u76F8\u5173\u5B50\u67E5\u8BE2\u7684\u6267\u884C\u65B9\u5F0F\u662F\u4E00\u6837\u4E00\u6837\u7684\uFF0C\u5C31\u662F\u8BF4\u5148\u53D6\u4E00\u6761\u5916\u5C42\u67E5\u8BE2\u7684\u4E2D\u7684\u8BB0\u5F55\uFF0C\u7136\u540E\u5230\u5B50\u67E5\u8BE2\u7684\u8868\u4E2D\u5BFB\u627E\u7B26\u5408\u5339\u914D\u6761\u4EF6\u7684\u8BB0\u5F55\uFF0C\u5982\u679C\u80FD\u627E\u5230\u4E00\u6761\uFF0C\u5219\u5C06\u8BE5\u5916\u5C42\u67E5\u8BE2\u7684\u8BB0\u5F55\u653E\u5165\u6700\u7EC8\u7684\u7ED3\u679C\u96C6\u5E76\u4E14\u505C\u6B62\u67E5\u627E\u66F4\u591A\u5339\u914D\u7684\u8BB0\u5F55\uFF0C\u5982\u679C\u627E\u4E0D\u5230\u5219\u628A\u8BE5\u5916\u5C42\u67E5\u8BE2\u7684\u8BB0\u5F55\u4E22\u5F03\u6389\uFF1B\u7136\u540E\u518D\u5F00\u59CB\u53D6\u4E0B\u4E00\u6761\u5916\u5C42\u67E5\u8BE2\u4E2D\u7684\u8BB0\u5F55\uFF0C\u91CD\u590D\u4E0A\u8FB9\u8FD9\u4E2A\u8FC7\u7A0B\u3002</p>
</li>
</ul>
<p>\u5BF9\u4E8E\u67D0\u4E9B\u4F7F\u7528<code>IN</code>\u8BED\u53E5\u7684<span>\u76F8\u5173</span>\u5B50\u67E5\u8BE2\uFF0C\u6BD4\u65B9\u8FD9\u4E2A\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE key1 IN (SELECT common_field FROM s2 WHERE s1.key3 = s2.key3);
</code></pre><p>\u5B83\u4E5F\u53EF\u4EE5\u5F88\u65B9\u4FBF\u7684\u8F6C\u4E3A\u534A\u8FDE\u63A5\uFF0C\u8F6C\u6362\u540E\u7684\u8BED\u53E5\u7C7B\u4F3C\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">SELECT s1.* FROM s1 SEMI JOIN s2 
    ON s1.key1 = s2.common_field AND s1.key3 = s2.key3;
</code></pre><p>\u7136\u540E\u5C31\u53EF\u4EE5\u4F7F\u7528\u6211\u4EEC\u4E0A\u8FB9\u4ECB\u7ECD\u8FC7\u7684<code>DuplicateWeedout</code>\u3001<code>LooseScan</code>\u3001<code>FirstMatch</code>\u7B49\u534A\u8FDE\u63A5\u6267\u884C\u7B56\u7565\u6765\u6267\u884C\u67E5\u8BE2\uFF0C\u5F53\u7136\uFF0C\u5982\u679C\u5B50\u67E5\u8BE2\u7684\u67E5\u8BE2\u5217\u8868\u5904\u53EA\u6709\u4E3B\u952E\u6216\u8005\u552F\u4E00\u4E8C\u7EA7\u7D22\u5F15\u5217\uFF0C\u8FD8\u53EF\u4EE5\u76F4\u63A5\u4F7F\u7528<code>table pullout</code>\u7684\u7B56\u7565\u6765\u6267\u884C\u67E5\u8BE2\uFF0C\u4F46\u662F\u9700\u8981\u5927\u5BB6\u6CE8\u610F\u7684\u662F\uFF0C<span>\u7531\u4E8E\u76F8\u5173\u5B50\u67E5\u8BE2\u5E76\u4E0D\u662F\u4E00\u4E2A\u72EC\u7ACB\u7684\u67E5\u8BE2\uFF0C\u6240\u4EE5\u4E0D\u80FD\u8F6C\u6362\u4E3A\u7269\u5316\u8868\u6765\u6267\u884C\u67E5\u8BE2</span>\u3002</p>
<h5 data-id="heading-23">semi-join\u7684\u9002\u7528\u6761\u4EF6</h5>
<p>\u5F53\u7136\uFF0C\u5E76\u4E0D\u662F\u6240\u6709\u5305\u542B<code>IN</code>\u5B50\u67E5\u8BE2\u7684\u67E5\u8BE2\u8BED\u53E5\u90FD\u53EF\u4EE5\u8F6C\u6362\u4E3A<code>semi-join</code>\uFF0C\u53EA\u6709\u5F62\u5982\u8FD9\u6837\u7684\u67E5\u8BE2\u624D\u53EF\u4EE5\u88AB\u8F6C\u6362\u4E3A<code>semi-join</code>\uFF1A</p>
<pre><code lang="bash" class="">SELECT ... FROM outer_tables 
    WHERE expr IN (SELECT ... FROM inner_tables ...) AND ...

</code></pre><p>\u6216\u8005\u8FD9\u6837\u7684\u5F62\u5F0F\u4E5F\u53EF\u4EE5\uFF1A</p>
<pre><code lang="bash" class="">SELECT ... FROM outer_tables 
    WHERE (oe1, oe2, ...) IN (SELECT ie1, ie2, ... FROM inner_tables ...) AND ...
</code></pre><p>\u7528\u6587\u5B57\u603B\u7ED3\u4E00\u4E0B\uFF0C\u53EA\u6709\u7B26\u5408\u4E0B\u8FB9\u8FD9\u4E9B\u6761\u4EF6\u7684\u5B50\u67E5\u8BE2\u624D\u53EF\u4EE5\u88AB\u8F6C\u6362\u4E3A<code>semi-join</code>\uFF1A</p>
<ul>
<li>
<p>\u8BE5\u5B50\u67E5\u8BE2\u5FC5\u987B\u662F\u548C<code>IN</code>\u8BED\u53E5\u7EC4\u6210\u7684\u5E03\u5C14\u8868\u8FBE\u5F0F\uFF0C\u5E76\u4E14\u5728\u5916\u5C42\u67E5\u8BE2\u7684<code>WHERE</code>\u6216\u8005<code>ON</code>\u5B50\u53E5\u4E2D\u51FA\u73B0\u3002</p>
</li>
<li>
<p>\u5916\u5C42\u67E5\u8BE2\u4E5F\u53EF\u4EE5\u6709\u5176\u4ED6\u7684\u641C\u7D22\u6761\u4EF6\uFF0C\u53EA\u4E0D\u8FC7\u548C<code>IN</code>\u5B50\u67E5\u8BE2\u7684\u641C\u7D22\u6761\u4EF6\u5FC5\u987B\u4F7F\u7528<code>AND</code>\u8FDE\u63A5\u8D77\u6765\u3002</p>
</li>
<li>
<p>\u8BE5\u5B50\u67E5\u8BE2\u5FC5\u987B\u662F\u4E00\u4E2A\u5355\u4E00\u7684\u67E5\u8BE2\uFF0C\u4E0D\u80FD\u662F\u7531\u82E5\u5E72\u67E5\u8BE2\u7531<code>UNION</code>\u8FDE\u63A5\u8D77\u6765\u7684\u5F62\u5F0F\u3002</p>
</li>
<li>
<p>\u8BE5\u5B50\u67E5\u8BE2\u4E0D\u80FD\u5305\u542B<code>GROUP BY</code>\u6216\u8005<code>HAVING</code>\u8BED\u53E5\u6216\u8005\u805A\u96C6\u51FD\u6570\u3002</p>
</li>
<li>
<p>... \u8FD8\u6709\u4E00\u4E9B\u6761\u4EF6\u6BD4\u8F83\u5C11\u89C1\uFF0C\u5C31\u4E0D\u5520\u53E8\u5566\uFF5E</p>
</li>
</ul>
<h5 data-id="heading-24">\u4E0D\u9002\u7528\u4E8Esemi-join\u7684\u60C5\u51B5</h5>
<p>\u5BF9\u4E8E\u4E00\u4E9B\u4E0D\u80FD\u5C06\u5B50\u67E5\u8BE2\u8F6C\u4F4D<code>semi-join</code>\u7684\u60C5\u51B5\uFF0C\u5178\u578B\u7684\u6BD4\u5982\u4E0B\u8FB9\u8FD9\u51E0\u79CD\uFF1A</p>
<ul>
<li>
<p>\u5916\u5C42\u67E5\u8BE2\u7684WHERE\u6761\u4EF6\u4E2D\u6709\u5176\u4ED6\u641C\u7D22\u6761\u4EF6\u4E0EIN\u5B50\u67E5\u8BE2\u7EC4\u6210\u7684\u5E03\u5C14\u8868\u8FBE\u5F0F\u4F7F\u7528<code>OR</code>\u8FDE\u63A5\u8D77\u6765</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE key1 IN (SELECT common_field FROM s2 WHERE key3 = <span>'a'</span>)
        OR key2 &gt; 100;
</code></pre></li>
<li>
<p>\u4F7F\u7528<code>NOT IN</code>\u800C\u4E0D\u662F<code>IN</code>\u7684\u60C5\u51B5</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE key1 NOT IN (SELECT common_field FROM s2 WHERE key3 = <span>'a'</span>)
</code></pre></li>
<li>
<p>\u5728<code>SELECT</code>\u5B50\u53E5\u4E2D\u7684IN\u5B50\u67E5\u8BE2\u7684\u60C5\u51B5</p>
<pre><code lang="bash" class="">SELECT key1 IN (SELECT common_field FROM s2 WHERE key3 = <span>'a'</span>) FROM s1 ;
</code></pre></li>
<li>
<p>\u5B50\u67E5\u8BE2\u4E2D\u5305\u542B<code>GROUP BY</code>\u3001<code>HAVING</code>\u6216\u8005\u805A\u96C6\u51FD\u6570\u7684\u60C5\u51B5</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE key2 IN (SELECT COUNT(*) FROM s2 GROUP BY key1);
</code></pre></li>
<li>
<p>\u5B50\u67E5\u8BE2\u4E2D\u5305\u542B<code>UNION</code>\u7684\u60C5\u51B5</p>
<pre><code lang="bash" class="">SELECT * FROM s1 WHERE key1 IN (
    SELECT common_field FROM s2 WHERE key3 = <span>'a'</span> 
    UNION
    SELECT common_field FROM s2 WHERE key3 = <span>'b'</span>
);
</code></pre></li>
</ul>
<p><code>MySQL</code>\u4ECD\u7136\u7559\u4E86\u4E24\u624B\u7EDD\u6D3B\u6765\u4F18\u5316\u4E0D\u80FD\u8F6C\u4E3A<code>semi-join</code>\u67E5\u8BE2\u7684\u5B50\u67E5\u8BE2\uFF0C\u90A3\u5C31\u662F\uFF1A</p>
<ul>
<li>
<p>\u5BF9\u4E8E\u4E0D\u76F8\u5173\u5B50\u67E5\u8BE2\u6765\u8BF4\uFF0C\u53EF\u4EE5\u5C1D\u8BD5\u628A\u5B83\u4EEC\u7269\u5316\u4E4B\u540E\u518D\u53C2\u4E0E\u67E5\u8BE2</p>
<p>\u6BD4\u5982\u6211\u4EEC\u4E0A\u8FB9\u63D0\u5230\u7684\u8FD9\u4E2A\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE key1 NOT IN (SELECT common_field FROM s2 WHERE key3 = <span>'a'</span>)
</code></pre><p>\u5148\u5C06\u5B50\u67E5\u8BE2\u7269\u5316\uFF0C\u7136\u540E\u518D\u5224\u65AD<code>key1</code>\u662F\u5426\u5728\u7269\u5316\u8868\u7684\u7ED3\u679C\u96C6\u4E2D\u53EF\u4EE5\u52A0\u5FEB\u67E5\u8BE2\u6267\u884C\u7684\u901F\u5EA6\u3002</p>
<blockquote><p>\u5C0F\u8D34\u58EB\uFF1A

\u8BF7\u6CE8\u610F\u8FD9\u91CC\u5C06\u5B50\u67E5\u8BE2\u7269\u5316\u4E4B\u540E\u4E0D\u80FD\u8F6C\u4E3A\u548C\u5916\u5C42\u67E5\u8BE2\u7684\u8868\u7684\u8FDE\u63A5\uFF0C\u53EA\u80FD\u662F\u5148\u626B\u63CFs1\u8868\uFF0C\u7136\u540E\u5BF9s1\u8868\u7684\u67D0\u6761\u8BB0\u5F55\u6765\u8BF4\uFF0C\u5224\u65AD\u8BE5\u8BB0\u5F55\u7684key1\u503C\u5728\u4E0D\u5728\u7269\u5316\u8868\u4E2D\u3002
</p></blockquote></li>
<li>
<p>\u4E0D\u7BA1\u5B50\u67E5\u8BE2\u662F\u76F8\u5173\u7684\u8FD8\u662F\u4E0D\u76F8\u5173\u7684\uFF0C\u90FD\u53EF\u4EE5\u628A<code>IN</code>\u5B50\u67E5\u8BE2\u5C1D\u8BD5\u8F6C\u4E3A<code>EXISTS</code>\u5B50\u67E5\u8BE2</p>
<p>\u5176\u5B9E\u5BF9\u4E8E\u4EFB\u610F\u4E00\u4E2AIN\u5B50\u67E5\u8BE2\u6765\u8BF4\uFF0C\u90FD\u53EF\u4EE5\u88AB\u8F6C\u4E3A<code>EXISTS</code>\u5B50\u67E5\u8BE2\uFF0C\u901A\u7528\u7684\u4F8B\u5B50\u5982\u4E0B\uFF1A</p>
<pre><code lang="bash" class="">outer_expr IN (SELECT inner_expr FROM ... WHERE subquery_where)
</code></pre><p>\u53EF\u4EE5\u88AB\u8F6C\u6362\u4E3A\uFF1A</p>
<pre><code lang="bash" class="">EXISTS (SELECT inner_expr FROM ... WHERE subquery_where AND outer_expr=inner_expr)
</code></pre><p>\u5F53\u7136\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\u6709\u4E00\u4E9B\u7279\u6B8A\u60C5\u51B5\uFF0C\u6BD4\u5982\u5728<code>outer_expr</code>\u6216\u8005<code>inner_expr</code>\u503C\u4E3A<code>NULL</code>\u7684\u60C5\u51B5\u4E0B\u5C31\u6BD4\u8F83\u7279\u6B8A\u3002\u56E0\u4E3A\u6709<code>NULL</code>\u503C\u4F5C\u4E3A\u64CD\u4F5C\u6570\u7684\u8868\u8FBE\u5F0F\u7ED3\u679C\u5F80\u5F80\u662F<code>NULL</code>\uFF0C\u6BD4\u65B9\u8BF4\uFF1A</p>
<pre><code lang="bash" class="">mysql&gt; SELECT NULL IN (1, 2, 3);
+-------------------+
| NULL IN (1, 2, 3) |
+-------------------+
|              NULL |
+-------------------+
1 row <span>in</span> <span>set</span> (0.00 sec)

mysql&gt; SELECT 1 IN (1, 2, 3);
+----------------+
| 1 IN (1, 2, 3) |
+----------------+
|              1 |
+----------------+
1 row <span>in</span> <span>set</span> (0.00 sec)

mysql&gt; SELECT NULL IN (NULL);
+----------------+
| NULL IN (NULL) |
+----------------+
|           NULL |
+----------------+
1 row <span>in</span> <span>set</span> (0.00 sec)
</code></pre><p>\u800C<code>EXISTS</code>\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u80AF\u5B9A\u662F<code>TRUE</code>\u6216\u8005<code>FASLE</code>\uFF1A</p>
<pre><code lang="bash" class="">mysql&gt; SELECT EXISTS (SELECT 1 FROM s1 WHERE NULL = 1);
+------------------------------------------+
| EXISTS (SELECT 1 FROM s1 WHERE NULL = 1) |
+------------------------------------------+
|                                        0 |
+------------------------------------------+
1 row <span>in</span> <span>set</span> (0.01 sec)

mysql&gt; SELECT EXISTS (SELECT 1 FROM s1 WHERE 1 = NULL);
+------------------------------------------+
| EXISTS (SELECT 1 FROM s1 WHERE 1 = NULL) |
+------------------------------------------+
|                                        0 |
+------------------------------------------+
1 row <span>in</span> <span>set</span> (0.00 sec)

mysql&gt; SELECT EXISTS (SELECT 1 FROM s1 WHERE NULL = NULL);
+---------------------------------------------+
| EXISTS (SELECT 1 FROM s1 WHERE NULL = NULL) |
+---------------------------------------------+
|                                           0 |
+---------------------------------------------+
1 row <span>in</span> <span>set</span> (0.00 sec)
</code></pre><p>\u4F46\u662F\u5E78\u8FD0\u7684\u662F\uFF0C\u6211\u4EEC\u5927\u90E8\u5206\u4F7F\u7528<code>IN</code>\u5B50\u67E5\u8BE2\u7684\u573A\u666F\u662F\u628A\u5B83\u653E\u5728<code>WHERE</code>\u6216\u8005<code>ON</code>\u5B50\u53E5\u4E2D\uFF0C\u800C<code>WHERE</code>\u6216\u8005<code>ON</code>\u5B50\u53E5\u662F\u4E0D\u533A\u5206<code>NULL</code>\u548C<code>FALSE</code>\u7684\uFF0C\u6BD4\u65B9\u8BF4\uFF1A</p>
<pre><code lang="bash" class="">mysql&gt; SELECT 1 FROM s1 WHERE NULL;
Empty <span>set</span> (0.00 sec)

mysql&gt; SELECT 1 FROM s1 WHERE FALSE;
Empty <span>set</span> (0.00 sec)
</code></pre><p>\u6240\u4EE5\u53EA\u8981\u6211\u4EEC\u7684<code>IN</code>\u5B50\u67E5\u8BE2\u662F\u653E\u5728<code>WHERE</code>\u6216\u8005<code>ON</code>\u5B50\u53E5\u4E2D\u7684\uFF0C\u90A3\u4E48<code>IN -&gt; EXISTS</code>\u7684\u8F6C\u6362\u5C31\u662F\u6CA1\u95EE\u9898\u7684\u3002\u8BF4\u4E86\u8FD9\u4E48\u591A\uFF0C\u4E3A\u5565\u8981\u8F6C\u6362\u5462\uFF1F\u8FD9\u662F\u56E0\u4E3A\u4E0D\u8F6C\u6362\u7684\u8BDD\u53EF\u80FD\u7528\u4E0D\u5230\u7D22\u5F15\uFF0C\u6BD4\u65B9\u8BF4\u4E0B\u8FB9\u8FD9\u4E2A\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1
    WHERE key1 IN (SELECT key3 FROM s2 <span>where</span> s1.common_field = s2.common_field) 
        OR key2 &gt; 1000;
</code></pre><p>\u8FD9\u4E2A\u67E5\u8BE2\u4E2D\u7684\u5B50\u67E5\u8BE2\u662F\u4E00\u4E2A\u76F8\u5173\u5B50\u67E5\u8BE2\uFF0C\u800C\u4E14\u5B50\u67E5\u8BE2\u6267\u884C\u7684\u65F6\u5019\u4E0D\u80FD\u4F7F\u7528\u5230\u7D22\u5F15\uFF0C\u4F46\u662F\u5C06\u5B83\u8F6C\u4E3A<code>EXISTS</code>\u5B50\u67E5\u8BE2\u540E\u5374\u53EF\u4EE5\u4F7F\u7528\u5230\u7D22\u5F15\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1
    WHERE EXISTS (SELECT 1 FROM s2 <span>where</span> s1.common_field = s2.common_field AND s2.key3 = s1.key1) 
        OR key2 &gt; 1000;
</code></pre><p>\u8F6C\u4E3A<code>EXISTS</code>\u5B50\u67E5\u8BE2\u65F6\u4FBF\u53EF\u4EE5\u4F7F\u7528\u5230<code>s2</code>\u8868\u7684<code>idx_key3</code>\u7D22\u5F15\u4E86\u3002</p>
<p>\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u5982\u679C<code>IN</code>\u5B50\u67E5\u8BE2\u4E0D\u6EE1\u8DB3\u8F6C\u6362\u4E3A<code>semi-join</code>\u7684\u6761\u4EF6\uFF0C\u53C8\u4E0D\u80FD\u8F6C\u6362\u4E3A\u7269\u5316\u8868\u6216\u8005\u8F6C\u6362\u4E3A\u7269\u5316\u8868\u7684\u6210\u672C\u592A\u5927\uFF0C\u90A3\u4E48\u5B83\u5C31\u4F1A\u88AB\u8F6C\u6362\u4E3A<code>EXISTS</code>\u67E5\u8BE2\u3002</p>
<blockquote><p>\u5C0F\u8D34\u58EB\uFF1A

\u5728MySQL5.5\u4EE5\u53CA\u4E4B\u524D\u7684\u7248\u672C\u6CA1\u6709\u5F15\u8FDBsemi-join\u548C\u7269\u5316\u7684\u65B9\u5F0F\u4F18\u5316\u5B50\u67E5\u8BE2\u65F6\uFF0C\u4F18\u5316\u5668\u90FD\u4F1A\u628AIN\u5B50\u67E5\u8BE2\u8F6C\u6362\u4E3AEXISTS\u5B50\u67E5\u8BE2\uFF0C\u597D\u591A\u540C\u5B66\u5C31\u60CA\u547C\u6211\u660E\u660E\u5199\u7684\u662F\u4E00\u4E2A\u4E0D\u76F8\u5173\u5B50\u67E5\u8BE2\uFF0C\u4E3A\u5565\u8981\u6309\u7167\u6267\u884C\u76F8\u5173\u5B50\u67E5\u8BE2\u7684\u65B9\u5F0F\u6765\u6267\u884C\u5462\uFF1F\u6240\u4EE5\u5F53\u65F6\u597D\u591A\u58F0\u97F3\u90FD\u662F\u5EFA\u8BAE\u5927\u5BB6\u628A\u5B50\u67E5\u8BE2\u8F6C\u4E3A\u8FDE\u63A5\uFF0C\u4E0D\u8FC7\u968F\u7740MySQL\u7684\u53D1\u5C55\uFF0C\u6700\u8FD1\u7684\u7248\u672C\u4E2D\u5F15\u5165\u4E86\u975E\u5E38\u591A\u7684\u5B50\u67E5\u8BE2\u4F18\u5316\u7B56\u7565\uFF0C\u5927\u5BB6\u53EF\u4EE5\u7A0D\u5FAE\u653E\u5FC3\u7684\u4F7F\u7528\u5B50\u67E5\u8BE2\u4E86\uFF0C\u5185\u90E8\u7684\u8F6C\u6362\u5DE5\u4F5C\u4F18\u5316\u5668\u4F1A\u4E3A\u5927\u5BB6\u81EA\u52A8\u5B9E\u73B0\u3002
</p></blockquote></li>
</ul>
<h5 data-id="heading-25">\u5C0F\u7ED3\u4E00\u4E0B</h5>
<ul>
<li>
<p>\u5982\u679C<code>IN</code>\u5B50\u67E5\u8BE2\u7B26\u5408\u8F6C\u6362\u4E3A<code>semi-join</code>\u7684\u6761\u4EF6\uFF0C\u67E5\u8BE2\u4F18\u5316\u5668\u4F1A\u4F18\u5148\u628A\u8BE5\u5B50\u67E5\u8BE2\u8F6C\u6362\u4E3A<code>semi-join</code>\uFF0C\u7136\u540E\u518D\u8003\u8651\u4E0B\u8FB95\u79CD\u6267\u884C\u534A\u8FDE\u63A5\u7684\u7B56\u7565\u4E2D\u54EA\u4E2A\u6210\u672C\u6700\u4F4E\uFF1A</p>
<ul>
<li>Table pullout</li>
<li>DuplicateWeedout</li>
<li>LooseScan</li>
<li>Materialization</li>
<li>FirstMatch</li>
</ul>
<p>\u9009\u62E9\u6210\u672C\u6700\u4F4E\u7684\u90A3\u79CD\u6267\u884C\u7B56\u7565\u6765\u6267\u884C\u5B50\u67E5\u8BE2\u3002</p>
</li>
<li>
<p>\u5982\u679C<code>IN</code>\u5B50\u67E5\u8BE2\u4E0D\u7B26\u5408\u8F6C\u6362\u4E3A<code>semi-join</code>\u7684\u6761\u4EF6\uFF0C\u90A3\u4E48\u67E5\u8BE2\u4F18\u5316\u5668\u4F1A\u4ECE\u4E0B\u8FB9\u4E24\u79CD\u7B56\u7565\u4E2D\u627E\u51FA\u4E00\u79CD\u6210\u672C\u66F4\u4F4E\u7684\u65B9\u5F0F\u6267\u884C\u5B50\u67E5\u8BE2\uFF1A</p>
<ul>
<li>\u5148\u5C06\u5B50\u67E5\u8BE2\u7269\u5316\u4E4B\u540E\u518D\u6267\u884C\u67E5\u8BE2</li>
<li>\u6267\u884C<code>IN to EXISTS</code>\u8F6C\u6362\u3002</li>
</ul>
</li>
</ul>
<h4 data-id="heading-26">ANY/ALL\u5B50\u67E5\u8BE2\u4F18\u5316</h4>
<p>\u5982\u679CANY/ALL\u5B50\u67E5\u8BE2\u662F\u4E0D\u76F8\u5173\u5B50\u67E5\u8BE2\u7684\u8BDD\uFF0C\u5B83\u4EEC\u5728\u5F88\u591A\u573A\u5408\u90FD\u80FD\u8F6C\u6362\u6210\u6211\u4EEC\u719F\u6089\u7684\u65B9\u5F0F\u53BB\u6267\u884C\uFF0C\u6BD4\u65B9\u8BF4\uFF1A</p>
<table>
<thead>
<tr>
<th>\u539F\u59CB\u8868\u8FBE\u5F0F</th>
<th>\u8F6C\u6362\u4E3A</th>
</tr>
</thead>
<tbody>
<tr>
<td>&lt; ANY (SELECT inner_expr ...)</td>
<td>&lt; (SELECT MAX(inner_expr) ...)</td>
</tr>
<tr>
<td>&gt; ANY (SELECT inner_expr ...)</td>
<td>&gt; (SELECT MIN(inner_expr) ...)</td>
</tr>
<tr>
<td>&lt; ALL (SELECT inner_expr ...)</td>
<td>&lt; (SELECT MIN(inner_expr) ...)</td>
</tr>
<tr>
<td>&gt; ALL (SELECT inner_expr ...)</td>
<td>&gt; (SELECT MAX(inner_expr) ...)</td>
</tr>
</tbody>
</table>
<h4 data-id="heading-27">[NOT] EXISTS\u5B50\u67E5\u8BE2\u7684\u6267\u884C</h4>
<p>\u5982\u679C<code>[NOT] EXISTS</code>\u5B50\u67E5\u8BE2\u662F\u4E0D\u76F8\u5173\u5B50\u67E5\u8BE2\uFF0C\u53EF\u4EE5\u5148\u6267\u884C\u5B50\u67E5\u8BE2\uFF0C\u5F97\u51FA\u8BE5<code>[NOT] EXISTS</code>\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u662F<code>TRUE</code>\u8FD8\u662F<code>FALSE</code>\uFF0C\u5E76\u91CD\u5199\u539F\u5148\u7684\u67E5\u8BE2\u8BED\u53E5\uFF0C\u6BD4\u5982\u5BF9\u8FD9\u4E2A\u67E5\u8BE2\u6765\u8BF4\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE EXISTS (SELECT 1 FROM s2 WHERE key1 = <span>'a'</span>) 
        OR key2 &gt; 100;
</code></pre><p>\u56E0\u4E3A\u8FD9\u4E2A\u8BED\u53E5\u91CC\u7684\u5B50\u67E5\u8BE2\u662F\u4E0D\u76F8\u5173\u5B50\u67E5\u8BE2\uFF0C\u6240\u4EE5\u4F18\u5316\u5668\u4F1A\u9996\u5148\u6267\u884C\u8BE5\u5B50\u67E5\u8BE2\uFF0C\u5047\u8BBE\u8BE5EXISTS\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u4E3A<code>TRUE</code>\uFF0C\u90A3\u4E48\u63A5\u7740\u4F18\u5316\u5668\u4F1A\u91CD\u5199\u67E5\u8BE2\u4E3A\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE TRUE OR key2 &gt; 100;
</code></pre><p>\u8FDB\u4E00\u6B65\u7B80\u5316\u540E\u5C31\u53D8\u6210\u4E86\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE TRUE;
</code></pre><p>\u5BF9\u4E8E\u76F8\u5173\u7684<code>[NOT] EXISTS</code>\u5B50\u67E5\u8BE2\u6765\u8BF4\uFF0C\u6BD4\u5982\u8FD9\u4E2A\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE EXISTS (SELECT 1 FROM s2 WHERE s1.common_field = s2.common_field);
</code></pre><p>\u5F88\u4E0D\u5E78\uFF0C\u8FD9\u4E2A\u67E5\u8BE2\u53EA\u80FD\u6309\u7167\u6211\u4EEC\u5E74\u5C11\u65F6\u7684\u90A3\u79CD\u6267\u884C\u76F8\u5173\u5B50\u67E5\u8BE2\u7684\u65B9\u5F0F\u6765\u6267\u884C\u3002\u4E0D\u8FC7\u5982\u679C<code>[NOT] EXISTS</code>\u5B50\u67E5\u8BE2\u4E2D\u5982\u679C\u53EF\u4EE5\u4F7F\u7528\u7D22\u5F15\u7684\u8BDD\uFF0C\u90A3\u67E5\u8BE2\u901F\u5EA6\u4E5F\u4F1A\u52A0\u5FEB\u4E0D\u5C11\uFF0C\u6BD4\u5982\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 
    WHERE EXISTS (SELECT 1 FROM s2 WHERE s1.common_field = s2.key1);
</code></pre><p>\u4E0A\u8FB9\u8FD9\u4E2A<code>EXISTS</code>\u5B50\u67E5\u8BE2\u4E2D\u53EF\u4EE5\u4F7F\u7528<code>idx_key1</code>\u6765\u52A0\u5FEB\u67E5\u8BE2\u901F\u5EA6\u3002</p>
<h4 data-id="heading-28">\u5BF9\u4E8E\u6D3E\u751F\u8868\u7684\u4F18\u5316</h4>
<p>\u6211\u4EEC\u524D\u8FB9\u8BF4\u8FC7\u628A\u5B50\u67E5\u8BE2\u653E\u5728\u5916\u5C42\u67E5\u8BE2\u7684<code>FROM</code>\u5B50\u53E5\u540E\uFF0C\u90A3\u4E48\u8FD9\u4E2A\u5B50\u67E5\u8BE2\u7684\u7ED3\u679C\u76F8\u5F53\u4E8E\u4E00\u4E2A<code>\u6D3E\u751F\u8868</code>\uFF0C\u6BD4\u5982\u4E0B\u8FB9\u8FD9\u4E2A\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM  (
        SELECT id AS d_id,  key3 AS d_key3 FROM s2 WHERE key1 = <span>'a'</span>
    ) AS derived_s1 WHERE d_key3 = <span>'a'</span>;
</code></pre><p>\u5B50\u67E5\u8BE2<code>( SELECT id AS d_id, key3 AS d_key3 FROM s2 WHERE key1 = 'a')</code>\u7684\u7ED3\u679C\u5C31\u76F8\u5F53\u4E8E\u4E00\u4E2A\u6D3E\u751F\u8868\uFF0C\u8FD9\u4E2A\u8868\u7684\u540D\u79F0\u662F<code>derived_s1</code>\uFF0C\u8BE5\u8868\u6709\u4E24\u4E2A\u5217\uFF0C\u5206\u522B\u662F<code>d_id</code>\u548C<code>d_key3</code>\u3002</p>
<p>\u5BF9\u4E8E\u542B\u6709<code>\u6D3E\u751F\u8868</code>\u7684\u67E5\u8BE2\uFF0C<code>MySQL</code>\u63D0\u4F9B\u4E86\u4E24\u79CD\u6267\u884C\u7B56\u7565\uFF1A</p>
<ul>
<li>
<p>\u6700\u5BB9\u6613\u60F3\u5230\u7684\u5C31\u662F\u628A\u6D3E\u751F\u8868\u7269\u5316\u3002</p>
<p>\u6211\u4EEC\u53EF\u4EE5\u5C06\u6D3E\u751F\u8868\u7684\u7ED3\u679C\u96C6\u5199\u5230\u4E00\u4E2A\u5185\u90E8\u7684\u4E34\u65F6\u8868\u4E2D\uFF0C\u7136\u540E\u5C31\u628A\u8FD9\u4E2A\u7269\u5316\u8868\u5F53\u4F5C\u666E\u901A\u8868\u4E00\u6837\u53C2\u4E0E\u67E5\u8BE2\u3002\u5F53\u7136\uFF0C\u5728\u5BF9\u6D3E\u751F\u8868\u8FDB\u884C\u7269\u5316\u65F6\uFF0C\u8BBE\u8BA1<code>MySQL</code>\u7684\u5927\u53D4\u4F7F\u7528\u4E86\u4E00\u79CD\u79F0\u4E3A<code>\u5EF6\u8FDF\u7269\u5316</code>\u7684\u7B56\u7565\uFF0C\u4E5F\u5C31\u662F\u5728\u67E5\u8BE2\u4E2D\u771F\u6B63\u4F7F\u7528\u5230\u6D3E\u751F\u8868\u65F6\u624D\u56DE\u53BB\u5C1D\u8BD5\u7269\u5316\u6D3E\u751F\u8868\uFF0C\u800C\u4E0D\u662F\u8FD8\u6CA1\u5F00\u59CB\u6267\u884C\u67E5\u8BE2\u5462\u5C31\u628A\u6D3E\u751F\u8868\u7269\u5316\u6389\u3002\u6BD4\u65B9\u8BF4\u5BF9\u4E8E\u4E0B\u8FB9\u8FD9\u4E2A\u542B\u6709\u6D3E\u751F\u8868\u7684\u67E5\u8BE2\u6765\u8BF4\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM (
        SELECT * FROM s1 WHERE key1 = <span>'a'</span>
    ) AS derived_s1 INNER JOIN s2
    ON derived_s1.key1 = s2.key1
    WHERE s2.key2 = 1;
</code></pre><p>\u5982\u679C\u91C7\u7528\u7269\u5316\u6D3E\u751F\u8868\u7684\u65B9\u5F0F\u6765\u6267\u884C\u8FD9\u4E2A\u67E5\u8BE2\u7684\u8BDD\uFF0C\u90A3\u4E48\u6267\u884C\u65F6\u9996\u5148\u4F1A\u5230<code>s2</code>\u8868\u4E2D\u627E\u51FA\u6EE1\u8DB3<code>s2.key2 = 1</code>\u7684\u8BB0\u5F55\uFF0C\u5982\u679C\u538B\u6839\u513F\u627E\u4E0D\u5230\uFF0C\u8BF4\u660E\u53C2\u4E0E\u8FDE\u63A5\u7684<code>s2</code>\u8868\u8BB0\u5F55\u5C31\u662F\u7A7A\u7684\uFF0C\u6240\u4EE5\u6574\u4E2A\u67E5\u8BE2\u7684\u7ED3\u679C\u96C6\u5C31\u662F\u7A7A\u7684\uFF0C\u6240\u4EE5\u4E5F\u5C31\u6CA1\u6709\u5FC5\u8981\u53BB\u7269\u5316\u67E5\u8BE2\u4E2D\u7684\u6D3E\u751F\u8868\u4E86\u3002</p>
</li>
<li>
<p>\u5C06\u6D3E\u751F\u8868\u548C\u5916\u5C42\u7684\u8868\u5408\u5E76\uFF0C\u4E5F\u5C31\u662F\u5C06\u67E5\u8BE2\u91CD\u5199\u4E3A\u6CA1\u6709\u6D3E\u751F\u8868\u7684\u5F62\u5F0F</p>
<p>\u6211\u4EEC\u6765\u770B\u8FD9\u4E2A\u8D3C\u7B80\u5355\u7684\u5305\u542B\u6D3E\u751F\u8868\u7684\u67E5\u8BE2\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM (SELECT * FROM s1 WHERE key1 = <span>'a'</span>) AS derived_s1;
</code></pre><p>\u8FD9\u4E2A\u67E5\u8BE2\u672C\u8D28\u4E0A\u5C31\u662F\u60F3\u67E5\u770B<code>s1</code>\u8868\u4E2D\u6EE1\u8DB3<code>key1 = 'a'</code>\u6761\u4EF6\u7684\u7684\u5168\u90E8\u8BB0\u5F55\uFF0C\u6240\u4EE5\u548C\u4E0B\u8FB9\u8FD9\u4E2A\u8BED\u53E5\u662F\u7B49\u4EF7\u7684\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 WHERE key1 = <span>'a'</span>;
</code></pre><p>\u5BF9\u4E8E\u4E00\u4E9B\u7A0D\u5FAE\u590D\u6742\u7684\u5305\u542B\u6D3E\u751F\u8868\u7684\u8BED\u53E5\uFF0C\u6BD4\u5982\u6211\u4EEC\u4E0A\u8FB9\u63D0\u5230\u7684\u90A3\u4E2A\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM (
        SELECT * FROM s1 WHERE key1 = <span>'a'</span>
    ) AS derived_s1 INNER JOIN s2
    ON derived_s1.key1 = s2.key1
    WHERE s2.key2 = 1;
</code></pre><p>\u6211\u4EEC\u53EF\u4EE5\u5C06\u6D3E\u751F\u8868\u4E0E\u5916\u5C42\u67E5\u8BE2\u7684\u8868\u5408\u5E76\uFF0C\u7136\u540E\u5C06\u6D3E\u751F\u8868\u4E2D\u7684\u641C\u7D22\u6761\u4EF6\u653E\u5230\u5916\u5C42\u67E5\u8BE2\u7684\u641C\u7D22\u6761\u4EF6\u4E2D\uFF0C\u5C31\u50CF\u8FD9\u6837\uFF1A</p>
<pre><code lang="bash" class="">SELECT * FROM s1 INNER JOIN s2 
    ON s1.key1 = s2.key1
    WHERE s1.key1 = <span>'a'</span> AND s2.key2 = 1;
</code></pre><p>\u8FD9\u6837\u901A\u8FC7\u5C06\u5916\u5C42\u67E5\u8BE2\u548C\u6D3E\u751F\u8868\u5408\u5E76\u7684\u65B9\u5F0F\u6210\u529F\u7684\u6D88\u9664\u4E86\u6D3E\u751F\u8868\uFF0C\u4E5F\u5C31\u610F\u5473\u7740\u6211\u4EEC\u6CA1\u5FC5\u8981\u518D\u4ED8\u51FA\u521B\u5EFA\u548C\u8BBF\u95EE\u4E34\u65F6\u8868\u7684\u6210\u672C\u4E86\u3002\u53EF\u662F\u5E76\u4E0D\u662F\u6240\u6709\u5E26\u6709\u6D3E\u751F\u8868\u7684\u67E5\u8BE2\u90FD\u80FD\u88AB\u6210\u529F\u7684\u548C\u5916\u5C42\u67E5\u8BE2\u5408\u5E76\uFF0C\u5F53\u6D3E\u751F\u8868\u4E2D\u6709\u8FD9\u4E9B\u8BED\u53E5\u5C31\u4E0D\u53EF\u4EE5\u548C\u5916\u5C42\u67E5\u8BE2\u5408\u5E76\uFF1A</p>
<ul>
<li>
<p>\u805A\u96C6\u51FD\u6570\uFF0C\u6BD4\u5982MAX()\u3001MIN()\u3001SUM()\u5565\u7684</p>
</li>
<li>
<p>DISTINCT</p>
</li>
<li>
<p>GROUP BY</p>
</li>
<li>
<p>HAVING</p>
</li>
<li>
<p>LIMIT</p>
</li>
<li>
<p>UNION \u6216\u8005 UNION ALL</p>
</li>
<li>
<p>\u6D3E\u751F\u8868\u5BF9\u5E94\u7684\u5B50\u67E5\u8BE2\u7684<code>SELECT</code>\u5B50\u53E5\u4E2D\u542B\u6709\u53E6\u4E00\u4E2A\u5B50\u67E5\u8BE2</p>
</li>
<li>
<p>... \u8FD8\u6709\u4E9B\u4E0D\u5E38\u7528\u7684\u60C5\u51B5\u5C31\u4E0D\u591A\u8BF4\u4E86\u54C8\uFF5E</p>
</li>
</ul>
</li>
</ul>
<p>\u6240\u4EE5<code>MySQL</code>\u5728\u6267\u884C\u5E26\u6709\u6D3E\u751F\u8868\u7684\u65F6\u5019\uFF0C\u4F18\u5148\u5C1D\u8BD5\u628A\u6D3E\u751F\u8868\u548C\u5916\u5C42\u67E5\u8BE2\u5408\u5E76\u6389\uFF0C\u5982\u679C\u4E0D\u884C\u7684\u8BDD\uFF0C\u518D\u628A\u6D3E\u751F\u8868\u7269\u5316\u6389\u6267\u884C\u67E5\u8BE2\u3002</p>
`};export{e as default};
